css:
@-moz-document url-prefix("https://bato.to/myfollows"), url-prefix("https://bato.to/comic/_/comics"), url-prefix("http://bato.to/myfollows"), url-prefix("http://bato.to/comic/_/comics") {
/*
  #primary_nav,
  #community_app_menu,
  */
  
  /* Blood style elements */
  /* uncomment logo if you want to get rid of logo at top of page in blood style */
  /*#logo,*/
  #content > div:nth-child(3) > div > div.clear > table,
  #user_navigation,
  #secondary_navigation,

  /* ipb style elements */
  /* uncomment branding if you want to get rid of dead space at top of page in ipb style*/
  /*#branding,*/
  
  #nav_explore,
  #search,
  #index_stats,

  /* sylo style elements */  
  #headertop,
  #header_bar,
  .moderation_bar

  #index_stats,
  .maintitle,
  .header,
  #footer_utilities,
  #themeToggle,
  #nav_menu_2,
  #nav_menu_3,
  #nav_menu_5,
  #nav_menu_7,
  #nav_app_forums,
  #nav_app_members,
  #nav_app_subscriptions,
  #exclusive_titles,
  .category_block .clear,
  /*stupid forum section*/
  /*.clear.ipb_table:nth-child(2),*/
  /*table.ipb_table:nth-of-type(2),*/
  #anonymous_element_6,               
  /* social media */
  .topic_share,
  /*.ipsPad,*/
  /* edit description */
  .post_controls,
  /* Comments */
  #commentsStart,
  /* forum table */
  .row0:nth-of-type(2) {
    display:none;
  }
        
  /* new topic */
  /*.topic_buttons    */
  #mainContent .clearfix .clear .ipb_table {
    display:none;
  }
                 
  .ipb_table.chapters_list {
    display:table !important;    
    width:100% !important;
    border-collapse: collapse;
  }

  .clearfix,
  .category_block {
    width:auto!important;
    margin-right:auto!important;
  }
}

@-moz-document url-prefix("https://bato.to/reader"), url-prefix("http://bato.to/reader") {
body{
    background-image:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAGBAMAAAAS4vJ7AAAAG1BMVEUAAAAbGxvd7h7d7h7d7h7d7h4bGxsbGxsbGxvf7wGmAAAAGklEQVQImWNIYGhnKGPgYGhPYGDgKGNggDIANMIEMGRbi90AAAAASUVORK5CYII=")!important;    
  }
  
  #mainContent{
    background:none;
  }
  
  #mainContent,
  #content{
    padding:0!important;
  }
  
  #webtoon160ad {
    width: 0px!important;
  }
  

  /* Blood style elements */
  #secondary_navigation,
  .ipbfooter,

  /* sylo style elements */  
  #headertop,
  #header_bar,
  #primary_nav,
  .breadcrumb,

  br,
  #branding,
  #user_bar,
  #footer_utilities,
  #stats_div,
  #full_image > div:nth-child(1),
  #zoom_notice,
  #webtoon160ad,
  .moderation_bar:nth-of-type(1) {
    display:none;
  }
  
  #ipbwrapper{
    width:auto!important;
    max-width:100%!important;
    padding-top:0px;
  }
  
  #comic_wrap > div:nth-child(3),
  #comic_wrap > div:nth-child(3) > div{
    width:0px!important;
    height:0px!important;
    top:0px!important;
  }
   
  #comic_wrap > div:nth-child(1),
  #content,
  #comic_page{
    position:static!important;
    margin-bottom:0!important;
    margin-top:0!important;
    width:100%!important;
    margin:0 0 0!important;
    border:none!important;
  }
}
