css:
@-moz-document domain("www.steamgifts.com") {
    span.itad_everywhere {
        z-index: 99 !important;
        position: absolute!important;
        left: -196px !important;
        top: -7px !important;
    }
    body {
        background-color: rgb(39, 41, 34);
        font: 12px Arial, sans-serif;
        color: #c00;
        min-width: 1000px;
    }
    .homepage_table_column_heading {
        word-wrap: break-word;
        color: #9ac96a;
        font: 700 13px "Open Sans", sans-serif;
    }
    .markdown h1,
    .markdown h2,
    .markdown h3,
    .markdown h4,
    .markdown h5,
    .markdown h6 {
        color: #9ac96a;
        padding-top: 5px;
        margin-bottom: 8px !important;
        line-height: 1em !important;
    }
    .markdown h1 {
        font: 300 28px "Open Sans", sans-serif
    }
    .markdown h2 {
        font: 700 18px "Open Sans", sans-serif
    }
    .markdown h3 {
        font: 700 14px "Open Sans", sans-serif
    }
    .markdown {
        word-wrap: break-word
    }
    .markdown--resize-body {
        font-size: 13px;
        line-height: 1.55em
    }
    .markdown table {
        border-collapse: collapse;
        border: 1px solid #9ac96a;
        table-layout: fixed;
        width: 100%;
    }
    .markdown thead {
        background-color: rgba(122, 185, 122, 0.5);
        font-weight: bold;
        border-bottom: 1px solid #9ac96a;
    }
    .markdown td,
    .markdown th {
        padding: 3px 10px
    }
    .markdown td:not(:last-child),
    .markdown th:not(:last-child) {
        border-right: 1px solid rgba(122, 185, 122, 0.5);
    }
    .markdown tr:not(:last-child) {
        border-bottom: 1px solid #9ac96a;
    }
    .markdown pre {
        white-space: pre-wrap;
        background-color: rgb(39, 41, 34);
        border: 1px solid rgba(122, 185, 122, 0.5);
        padding: 5px 15px;
        border-radius: 0px;
        text-shadow: 0px 0px rgba(255, 255, 255, 0);
        color: #caeea7;
    }
    .markdown code {
        font-family: "Droid Sans Mono", sans-serif;
        font-size: 11px
    }
    .markdown hr {
        border-top: 1px solid #d2d6e0;
        border-bottom: 1px solid rgba(255, 255, 255, 0.3);
        border-left: none;
        border-right: none
    }
    .markdown > *:not(:last-child):not(div),
    .markdown .have > *:not(:last-child):not(div),
    .markdown .want > *:not(:last-child):not(div) {
        margin-bottom: 15px
    }
    .markdown .spoiler:not(:hover) {
        background-color: #d9dee6;
        color: transparent;
        text-shadow: none
    }
    .markdown .spoiler:not(:hover) a {
        color: transparent;
        text-decoration: none
    }
    .markdown ul,
    .markdown ol {
        margin-right: 25px;
        margin-left: 25px
    }
    .markdown ol > li {
        counter-increment: list
    }
    .markdown li {
        padding: 2px 5px
    }
    .markdown ol > li:before {
        content: counter(list) "."
    }
    .markdown ul > li:before {
        content: "â€¢"
    }
    .markdown li p:not(:last-child) {
        margin-bottom: 5px
    }
    .markdown li:before {
        color: #63a0f4;
        margin-left: -60px;
        font-weight: bold;
        font-size: 11px;
        position: absolute;
        width: 50px;
        text-align: right;
    }
    .markdown .search_highlight {
        background-color: #ff0;
        text-shadow: none
    }
    .markdown img {
        max-width: 500px;
        max-height: 500px;
        margin-top: 5px;
        display: inline-block
    }
    .markdown .comment__toggle-attached {
        font-size: 11px;
        font-style: italic;
        text-decoration: underline;
        color: #c86848;
        cursor: pointer
    }
    .markdown a {
        color: #4B72D4;
        text-decoration: underline
    }
    .markdown blockquote {
        border-left: 5px solid #d2d6e0;
        padding: 3px 15px;
        font-style: italic;
        opacity: .8
    }
    .markdown .have,
    .markdown .want {
        border-left: 5px solid;
        padding: 10px 20px
    }
    .markdown .have:not(:last-child),
    .markdown .want:not(:last-child) {
        margin-bottom: 15px
    }
    .markdown .have {
        border-left-color: #e1868c;
        background-color: #efedf0
    }
    .markdown .want {
        border-left-color: #6bbfdb;
        background-color: #e8eff3
    }
    .markdown blockquote blockquote {
        border-left: none;
        padding: 0;
        opacity: 1
    }
    .pageouter-wrap {
        margin-top: 20px;
    }
    .sidebar--wide {
        min-width: 130px;
    }
    .giveaway__row-outer-wrap {
        padding: 9px 10px 20px;
        transition: .3s ease;
    }
    .giveaway__row-outer-wrap:hover {
        background-color: rgba(122, 185, 122, 0.11);
    }
    .giveaway_image_thumbnail {
        order: 1;
    }
    .giveaway__summary {
        order: 2;
        position: relative;
        margin-top: -12px
    }
    .giveaway__row-inner-wrap > :not(:last-child) {
        margin-right: 0px;
    }
    .giveaway__row-inner-wrap > :not(:first-child) {
        margin-right: 17px;
    }
    .giveaway_image_avatar {
        display: none;
    }
    .giveaway_image_thumbnail,
    .giveaway_image_thumbnail_missing {
        border: 3px solid;
        height: 70px;
        width: 170px;
        -webkit-filter: grayscale(0.2);
        filter: grayscale(0.2);
        transition: .3s ease;
    }
    .giveaway__row-outer-wrap:hover .giveaway_image_thumbnail {
        -webkit-filter: grayscale(0);
        filter: grayscale(0);
    }
    .featured__outer-wrap--home,
    .pinned-giveaways__outer-wrap,
    .sidebar__mpu,
    .giveaway__links {
        display: none !important;
    }
    .esgst-giveaway-panel.giveaway__columns {
        width: 100%;
        /*float: left;*/
        margin: 5px 0 0 0;
    }
    .esgst-giveaway-panel {
        position: relative;
    }
    .esgst-elgb-button {
        position: absolute;
        left: -40px;
        top: -1px;
        z-index: 1;
    }
    .esgst-egh-button {
        padding: 7px 14px;
        position: absolute;
        left: -219px;
        top: 30px;
        z-index: 1;
        color: #fff900;
        background-color: #3c424d;
        border-radius: 14px;
    }
    .esgst-ttec {
        position: absolute;
        top: 35px;
        left: -42px;
    }
    .giveaway__row-outer-wrap .sidebar__error,
    .giveaway__row-outer-wrap .btn_action {
        padding: 0px 7px 1px;
    }
    .giveaway__row-outer-wrap .sidebar__error > span,
    .giveaway__row-outer-wrap .btn_action > span {
        display: none;
    }
    .esgst-button-set.esgst-gp-button {
        position: absolute;
        top: 0;
        right: 0;
    }
    .giveaway__summary .giveaway__columns:nth-child(3) {
        width: calc(100% + 194px);
        height: 15px;
        position: absolute;
        top: 72px;
        left: -186px;
        z-index: 1;
    }
    .giveaway__summary .giveaway__columns:nth-child(3) div:nth-child(1) {
        padding-right: 64px;
    }
    .fa {
        line-height: 11px;
        font-size: 16px;
    }
    .fa-bookmark {
        color: red;
    }
    .giveaway__row-outer-wrap:hover .fa-steam {
        color: #ffff00;
    }
    .giveaway__row-outer-wrap:hover .fa-search {
        color: #63a0f4;
    }
    .giveaway__icon {
        opacity: 1;
        padding: 3px 8px;
        background-color: #272922;
        border-radius: 4px;
    }
    .row-spacer {
        background-color: rgba(255, 0, 0, 0.4);
        border-left: 1px solid #d2d6e0;
        border-right: 1px solid #d2d6e0;
        padding: 2px;
        border-top: none;
    }
    .giveaway__columns > *:not(.giveaway__column--group):not(.giveaway__column--whitelist):not(.giveaway__column--invite-only):not(.giveaway__column--community-voted):not(.giveaway__column--region-restricted):not(.giveaway__column--contributor-level) {
        box-shadow: 0px 0px 0 rgba(255, 255, 255, 0) inset;
    }
    .giveaway__columns > * {
        color: #6b7a8c;
        white-space: nowrap;
        border-radius: 4px;
        font-size: 11px;
        background-image: linear-gradient(rgba(255, 255, 255, 0) 0px, rgba(255, 255, 255, 0) 6px, rgba(255, 255, 255, 0) 100%);
        border: none;
        line-height: 26px;
        padding: 0 8px;
    }
    .table__column__secondary-link,
    .pagination__navigation a:not(.is-selected) span,
    .comment__cancel-button span,
    .page__description__cancel span,
    .form__edit-button span,
    .form__logout-button span,
    .comment__username:not(.comment__username--op) a,
    .comment__actions__button,
    .page__description__edit,
    .giveaway__username,
    .giveaway__links span {
        border-bottom: 0px;
        box-shadow: 0 0px 0 rgba(255, 255, 255, 0);
    }
    .giveaway__columns > .esgst-gwc {
        color: #ffffff;
        white-space: nowrap;
        border-radius: 4px;
        font-size: 11px;
        background-image: linear-gradient(rgb(39, 41, 34) 0px, rgb(39, 41, 34) 6px, rgb(39, 41, 34) 100%);
        border: 0px solid #f0f2f5;
        line-height: 26px;
        padding: 0 8px;
        box-shadow: 0px 0px 0 rgba(158, 158, 158, 0.42) inset !important;
        text-shadow: 0px 0px rgba(255, 255, 255, 0);
    }
    .giveaway__heading__name {
        font: 700 16px "Open Sans", sans-serif;
        color: #4b72d4;
    }
    .esgst-gc-genres {
        max-width: 300px;
        overflow: hidden;
        text-overflow: ellipsis;
        vertical-align: middle;
        white-space: nowrap;
    }
    .esgst-gc-rating-positive {
        color: #fff !important;
        background-color: #66c0f452;
    }
    .esgst-gc-rating-mixed {
        color: #fff !important;
        background-color: #b9a07473;
    }
    .esgst-gc-rating-negative {
        color: #fff !important;
        background-color: #a34c2575;
    }
    .page__outer-wrap {
        text-shadow: 0px 0px rgba(39, 41, 34, 0);
        color: #e8eaef;
        background-color: #3c424d;
    }
    .footer__outer-wrap {
        padding: 96px 15px 25px;
    }
    .sidebar__heading {
        border-bottom: 1px solid rgb(39, 41, 34);
        box-shadow: 0 1px 0 rgba(39, 41, 34, 0.22);
        margin-bottom: 3px;
        font: bold 11px/15px Arial, sans-serif;
        text-shadow: 0px 0px rgba(255, 255, 255, 0);
        color: #e8eaef;
    }
    .sidebar__navigation {
        padding-bottom: 15px;
        text-shadow: 0px 0px rgba(255, 255, 255, 0);
    }
    .sidebar__navigation__item__name {
        color: #9ac96a;
    }
    .sidebar__navigation__item.is-selected .sidebar__navigation__item__name,
    .sidebar__navigation__item.is-selected i {
        color: #6d8ddc;
    }
    .sidebar__navigation__item__link:not(:hover) .sidebar__navigation__item__underline {
        border-bottom: 1px dotted #3c424d;
        box-shadow: 0 0px 0 rgba(255, 255, 255, 0);
    }
    .sidebar__navigation__item__link:hover {
        background-image: linear-gradient(#fff 0%, rgba(255, 255, 255, 0.4) 100%);
        background-image: -moz-linear-gradient(#fff 0%, rgba(255, 255, 255, 0.4) 100%);
        background-image: -webkit-linear-gradient(#878f9f 0%, #50596a 100%);
        border: 1px solid #5f738c;
        border-radius: 4px;
    }
    .sidebar__heading,
    .giveaway__row-outer-wrap:not(:last-child) {
        border-bottom: 1px solid rgb(39, 41, 34);
        box-shadow: 0 1px 0 rgba(39, 41, 34, 0.22);
    }
    .widget-container > div:not(:first-child) {
        border-left: 1px solid rgb(39, 41, 34);
        box-shadow: 0 1px 0 rgba(39, 41, 34, 0.22);
    }
    .page__heading > * {
        background-image: linear-gradient(#fff 0%, rgba(255, 255, 255, 0.4) 100%);
        background-image: -moz-linear-gradient(#fff 0%, rgba(255, 255, 255, 0.4) 100%);
        background-image: -webkit-linear-gradient(#878f9f 0%, #50596a 100%);
        padding: 5px 10px;
        border: 1px solid rgb(60, 66, 77);
        border-radius: 4px;
        color: #9ac96a;
        font: 700 14px/22px "Open Sans", sans-serif;
    }
    .page__heading.esgst-fmph {
        background-color: #3c424d;
    }
    .page__heading .esgst-page-heading-buttons {
        padding: 0;
    }
    .page__heading .esgst-heading-button {
        padding: 1px 4px 1px 4px;
        margin: 3px 3px 2px 3px;
    }
    .pagination__navigation a:not(.is-selected) {
        color: #9ac96a;
    }
    .pagination__navigation a.is-selected {
        font-weight: bold;
        color: #63a0f4;
    }
    .esgst-popup {
        background-color: #3d4230;
        border-radius: 4px;
        color: #f6f7f9;
        max-width: calc(90% - 50px);
        padding: 25px;
        position: fixed;
        text-align: center;
        text-shadow: 0px 0px rgba(255, 255, 255, 0);
        transition: 500ms ease;
    }
    .esgst-gf-box {
        background-color: rgb(39, 41, 34);
        border: 1px solid #9ac96a;
        border-radius: 0 !important;
        margin: 0! important;
        padding: 0 15px;
    }
    .esgst-gf-left-panel {
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        overflow-y: scroll;
    }
    .esgst-gf-basic-filters {
        order: 2;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
    }
    .esgst-gf-number-filters {
        width: 350px;
        text-align: left;
    }
    .esgst-gf-number-filters div {
        margin-bottom: 4px;
        background-color: #555555;
        padding: 3px 0px 3px 8px;
        border-radius: 4px;
    }
    .esgst-gf-number-filters input[type="number"] {
        margin: 0 3px;
    }
    .esgst-gf-boolean-filters {
        width: 230px;
        background-color: #555555;
        padding: 3px 8px 3px 8px;
        border-radius: 4px;
    }
    .esgst-gf-string-filters {
        width: 200px;
        background-color: #555555;
        padding: 3px 8px 3px 8px;
        border-radius: 4px;
    }
    .esgst-gf-string-filters .esgst-gf-legend-panel {
        margin-top: 20px;
    }
    .query-builder {
        color: #272922;
    }
    .esgst-gf-box input {
        background-color: #272922;
        border-radius: 0px;
        border-width: 3px;
        border-style: solid;
        border-color: #9ac96a;
        line-height: 1.1em;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        -webkit-box-sizing: border-box;
        color: #5cb85c;
    }
    .esgst-gf-filter-count {
        background-color: #4b72d4;
        border-radius: 5px;
        font-size: 10px;
        padding: 2px;
        vertical-align: middle;
    }
    .is-faded {
        opacity: 0.2;
    }
    .esgst-pgb-button,
    .esgst-gf-button {
        border: 1px solid #9ac96a;
        border-top: none;
        background-color: #e1e6ef;
        background-image: linear-gradient(rgb(60, 66, 77) 0%, rgba(39, 41, 34, 0.78) 100%);
        color: #f0f2f5;
        cursor: pointer;
        margin-bottom: 15px;
        padding: 3px;
        text-align: center;
        border-radius: 0 0 4px 4px;
    }
    .esgst-pgb-button:hover,
    .esgst-gf-button:hover {
        background-image: linear-gradient(rgb(39, 41, 34) 0%, #9ac96a 100%);
    }
    .table__row-outer-wrap .esgst-gc-panel,
    .table__row-outer-wrap .esgst-egh-button {
        display: none;
    }
    .table__row-outer-wrap {
        position: relative;
    }
    .table__row-outer-wrap .table__row-inner-wrap .esgst-giveaway-panel {
        position: inherit;
    }
    .table__row-outer-wrap .esgst-gwc span {
        color: #3d424e;
    }
    .table__row-outer-wrap .esgst-gwc span span {
        padding-top: 2%;
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        pointer-events: none;
    }
    .table__row-outer-wrap .esgst-gwr {
        display: none !important;
    }
    .table__row-outer-wrap .esgst-gwc span span {
        color: #fff;
    }
    .table__column__heading {
        color: #fff;
    }
    .table__row-inner-wrap .table__column--width-fill p {
        display: inline;
    }
    .comment__child {
        box-shadow: 0px 0px 0 #fff inset, 0 0px 0px rgba(255, 255, 255, 0.3) inset;
        background-color: rgba(122, 185, 122, 0.08);
        border: 0px solid rgba(255, 255, 255, 0);
        border-radius: 0px;
        padding: 15px;
    }
    .comment__children:not(.comment__children--no-indent) {
        padding-left: 15px;
        border-left: 1px solid rgba(122, 185, 122, 0.5);
        box-shadow: 0px 0 0 rgba(255, 255, 255, 0.3) inset;
    }
}
