css:
@-moz-document domain("literatureandlatte.com") {
html {
    font-family: Helvetica, Arial, sans-serif !important;
}
textarea {
    font-family: Helvetica, Arial, sans-serif !important;
}
.common-questions__listing h3 {
    font-family: Helvetica, Arial, sans-serif !important;
}
.subnav li a:after {
    font-family: Helvetica, Arial, sans-serif !important
}
.subnav li.subnav__buy-now a {
    font-family: Helvetica, Arial, sans-serif !important
}
.subnav li a:active,
.device-desktop .subnav li a:hover {
    font-family: Helvetica, Arial, sans-serif !important
}
.subnav li.subnav__download a:active,
.device-desktop .subnav li.subnav__download a:hover {
    font-family: Helvetica, Arial, sans-serif !important
}
.subnav li.subnav__buy-now a:active,
.device-desktop .subnav li.subnav__buy-now a:hover {
    font-family: Helvetica, Arial, sans-serif !important
}
.subnav li a {
    font-family: Helvetica, Arial, sans-serif !important
}
.common-questions__listing__section--open h3 {
    font-family: Helvetica, Arial, sans-serif !important
}
.features__icons h3 {
    font-family: Helvetica, Arial, sans-serif !important
}
.forum #viewprofile .details dt,
.forum #viewprofile .profile-details dt {
    font-family: Helvetica, Arial, sans-serif !important
}
.accordion__title {
    font-family: Helvetica, Arial, sans-serif !important;
}
.text-filter {
    font-family: Helvetica, Arial, sans-serif !important;
}
strong {
    font-weight: bold !important;
}
.forum .forumtitle,
.forum .topictitle,
.forum li.header2 dt,
.forum li.header2 dd {
    font-family: Helvetica, Arial, sans-serif !important;
    font-weight: bold;
    font-size: 1.1em;
}
/* Cleans up BBCode extraneous spacing. */
blockquote + br {
    display: none;
}
/* Clean up list spacing and left margin issues */
.content ul,
.content ol,
#preview ul,
#preview ol {
    margin: 0 !important;
    padding-left: 3em !important;
}
/* Ensure hyperlinks are coloured correctly even in preview mode */
#preview a.postlink {
    color: #e9505a;
}
div.content a * {
    color: #e9505a !important;
}
}
