css:
@-moz-document domain("github.com")
{
.Box, .Box-header--blue, .Box-row {
    background-color: #222;
    border-color: #555;
}

.Box-row {
    border-top: 1px solid #555;
}

.Box-row--focus-gray.navigation-focus {
    background-color: #555;
}

.Subhead {
    border-bottom: 1px solid #333;
}

.bg-white {
    background-color: #222;
}

.blob-code {
    color: #666;
}

.border-bottom {
    border-bottom: 1px solid #555 ;
}

.border-gray-dark {
    border: 1px solid #444;
}

.border-gray-light {
    border-color: #555 ;
}

.border-left {
    border-left: 1px solid #555 ;
}

.border-right {
    border-right: 1px solid #555 ;
}

.border-top {
    border-top: 1px #555 solid ;
}

.boxed-group-inner {
    background: #222;
    border: 1px solid #555;
    color: #ccc;
}

.boxed-group>h3, .boxed-group .heading {
    background-color: #111;
    border: 1px solid #555;
}

.btn.disabled {
    background-color: #222;
    background-image: linear-gradient(-180deg, #411 0%, #211 90%);
    border-color: rgba(27,31,35,0.2);
    color: #888;
}

.commit-tease, .file-wrap {
    background-color: #222;
    border: 1px solid #333;
    color: #ccc;
}

.commit-tease-contributors, .file-header {
    background-color: #222;
    border-bottom: 1px solid #555;
    border-top: 1px solid #555;
}

.copyable-terminal {
    background-color: #222;
}

.copyable-terminal pre {
    border-color: #222;
    box-shadow: 0 0 15px #222 inset ;
}

.discussion-timeline-actions {
    background-color: #222;
    border-top: 2px solid #555;
}

.file {
    border: 1px solid #555;
}

.filter-item.selected {
    background-color: #c33;
    color: #ccc;
}

.flash {
    background-color: #411;
    color: #aaa;
}

.intro-shelf {
    background-image: linear-gradient(180deg, rgba(125,0,0,0) 60%, #222),linear-gradient(70deg, #311 32%, #633);
    border-bottom: #555;
    color: #a22;
}

.js-navigation-item {
    background-color: #222;
}

.language-color {
    background-color: #311;
}

.link-gray-dark {
    color: #aaa;
}

.link-gray-dark:hover {
    color: #c33;
}

.logged-in {
    background-color: #222;
    color: #aaa;
}

.mini-repo-list-item {
    border-top: 1px solid #555;
}

.news .alert {
    border-top: 1px solid #555;
}

.overall-summary, .repository-lang-stats-graph {
    border: 1px solid #555;
}

.pagehead {
    border-bottom: 1px solid #555;
}

.profile-rollup-wrapper+.profile-rollup-wrapper {
    border-top: 1px solid #333;
}

.readme .markdown-body, .readme .plain {
    background-color: #333;
    border: 1px solid #555;
}

.repohead.experiment-repo-nav {
    background-color: #333;
}

.reponav-item.selected {
    background-color: #333;
    border-color: #a22 #555 transparent;
    color: #ccc;
}

.site-footer {
    border-top: 1px solid #333;
}

.subnav-item {
    border: 1px solid #555;
}

.subnav-item.selected, .subnav-item.selected:hover {
    background-color: #422;
    border: 1px solid #555;
}

.subnav-item:hover, .subnav-item:focus {
    background-color: #555;
}

.table-list-header {
    background-color: #333;
    border: 1px solid #555;
}

.text-gray-dark {
    color: #aaa;
}

.timeline-comment-wrapper {
    border-bottom: 2px solid #222;
    border-top: 2px solid #222;
}

.topic-tag-link {
    background-color: #333;
}

.underline-nav a {
    color: #aaa;
}

.user-profile-nav {
    border-bottom: solid 1px #555;
}

.user-profile-sticky-bar::after, .vcard-names-container,.py-3, .js-sticky, .js-user-profile-sticky-fields, .is-stuck {
    background-color: #222;
}

a {
    color: #c33;
}

a.btn, .social-count {
    background-color: #eff3f6;
    background-image: linear-gradient(-180deg, #844 0%, #622 90%);
    color: #d64;
}

button.btn {
    background-image: linear-gradient(-180deg, #855 0%, #422 90%);
    color: #aaa;
}

h3.profile-timeline-month-heading {
    background-color: #333;
}

hr {
    border-bottom: 1px solid #333;
    border-top: 1px solid #000;
}

input {
    background-color: #333;
    border: 1px solid #555;
    color: #ccc;
}

pre {
    background-color: #222;
    border: 1px solid #666;
    box-shadow: 0 0 15px #000 insert;
    color: #ccc;
    text-shadow: 0 0 10px #333;
}

rect[data-count="0"] {
    opacity: 0.1;
}

span.pl-c1 {
    color: #a03;
}

span.pl-en {
    color: #378;
}

span.pl-k {
    color: #555;
}

span.pl-md {
    background-color: #411;
    color: #c88;
}

span.pl-mi1 {
    background-color: #131;
    color: #8c8;
}

span.pl-pds {
    color: #808080;
}

span.pl-s {
    color: #f66;
}

span.pl-s1 {
    color: #966;
}

span.pl-smi {
    color: #c26;
}

table.files td {
    border-top: 1px solid #333;
}

table.files tr.navigation-focus td {
    background-color: #622;
    color: #c66;
    text-shadow: 0 0 5px black;
}

}
