css:
@-moz-document domain("connpass.com")
{
#editor_area {
    background-color: #111;
    color: #999;
}

#editor_area h1 {
    background-color: #400;
    border: 1px solid #111;
    box-shadow: 0 0 10px #200 inset;
    padding: 20px
;
}

#editor_area h1 a {
    color: #CCC;
    font-size: 12px;
    font-weight: bold;
    text-decoration: none;
}

#editor_area h2 {
    border-bottom: 1px solid #800;
    color: #900;
    font-size: 16px;
    font-weight: normal;
}

#editor_area h3 {
    border-bottom: 1px dashed #800;
}

#editor_area table td, .group_inner table td {
    background-color: #444;
    border: 1px solid #000;
    color: #ccc;
}

#editor_area table th, .group_inner table th {
    background-color: #333;
    border: 1px solid #000;
    color: #ccc;
}

#editor_area table, .group_inner table {
    border: 1px solid #c00;
}

#footer {
    background-color: #000;
    border: 1px solid #333;
    box-shadow: inset 0 1px 0 rgba(10,10,10,1);
    color: #666;
}

#header {
    background-color: #111;
    border: 1px solid #333;
    box-shadow: 0 0 20px #000;
}

#wrapper {
    background-color: #222;
}

.event_detail_title {
    background-color: #000;
    border-top: 1px solid #555;
    box-shadow: 0 -1px 1px #333;
    color: #ccc;
}

.event_entry_area, .event_inquiry_area, .event_place_area, .footer_link_area {
    border-top: 1px solid #555;
}

.event_list {
    border-bottom: 1px solid #666;
}

.event_title a {
    color: #c00;
    font-weight: normal;
}

.feed_area, .thumb36_list {
    border-top: 1px solid #000;
}

.group_box {
    background-color: #333;
    box-shadow: 0 0 30px #111;
    color: #ccc;
}

.group_inner table .text_center {
    color: #000;
    font-weight: bold;
    text-shadow: 0 1px 0 #fff,
    0 -1px 0 #fff,
    1px 0 0 #fff,
    -1px 0 0 #fff;
}

.group_inner table p.participants {
    background-color: #000;
    color: #c00;
}

.header_nav_contents ul li a {
    color: #666;
}

.header_search input {
    background-color: #333;
    border: 1px solid #666;
}

.information_area, .social_btn_area {
    display: none;
}

.label_status_event {
    background-color: #a00;
}

.label_status_event.open, .open.status {
    background-color: #900;
    color: #000;
    font-weight: bold;
    text-shadow: 0 0 20px #f66,
    0 0 5px #f99;
}

.no_data_area p, .feed_form input, p.comment {
    background-color: #444;
    border: 1px solid #000;
}

.open_event_list + .open_event_list {
    border-top: 1px solid #000;
}

.page_dashboard .content_over, .footer_social_area {
    background: linear-gradient(#333,#000);
    border: 1px solid #333;
    padding-bottom: 20px;
    padding-top: 20px;
}

.round_box_inner {
    background-color: #333;
    color: #ccc;
}

.square_tab li a {
    background: none;
    border: none;
    box-shadow: 0 0 0 #000;
    color: #ccc;
}

.thumb25_list {
    border-bottom: 1px dashed #000;
}

.thumb25_list p.comment, .square_tab li span {
    border: none;
}

a.font_14 strong {
    color: #666;
    text-decoration: none;
}

a.font_14 strong, .group_box a {
    color: #666;
    text-decoration: none;
}

button[type=submit].btn {
    opacity: 0.3;
}

h3 {
    background-color: #000;
    color: #ccc;
}

.main_content_banner, .side_ads_area, div[id^="div-gpt-ad"] {
    display: none;
}

}
