css:
@-moz-document domain("www.google.co.jp")
{
#center_col {
    margin-left: 30px;
}

#extabar {
    background-color: #121212;
}

#fbar,#hdtbSum {
    background-color: #121212;
    border-bottom: 1px solid #333;
    border-top: 1px solid #333;
}

#fbarcnt #fbar ._HR, #fbarcnt #fbar ._HR a, #fbarcnt #fbar ._nW a {
    color: #666;
}

#rg {
    width: 1896px;
}

.ab_button, .hdtb-td-o {
    background-color: #000;
    border-bottom: 1px solid #333;
}

.ads-ad ._WGk {
    color: #333;
}

.ads-ad ._mB {
    background-color: #7b1c3a;
    color: #000;
}

.ads-ad a {
    color: #333;
}

.ads-ad, .card-section, .card-section a[href^="/pr"], .card-section a[href^="/se"] {
    color: #333;
}

.card-section .brs_col a {
    color: #666;
}

.mn-dwn-arw {
    border-color: #000;
}

.mw {
    columns: 600px auto;
    max-width: 100%;
}

.rc .ts tr td a img {
    border-color: #c60;
    border-style: solid;
    border-width: 1px;
    margin-right: 15px;
    padding: 3px;
}

.s .st em {
    color: #a63;
}

.sbibod,.gstl_0 {
    background-color: #ccc;
    box-shadow: 0 0 5px #666 inset;
}

.sect {
    color: #ccc;
}

.sfbgg {
    background-color: #000000;
    border-bottom: 1px solid #333333;
}

.srg div.g:nth-child(even) a {
    color: #d97b29;
}

.st .f {
    background-color: #521327;
    border-radius: 3px;
    color: #d9ad29;
    padding-bottom: 2px;
    padding-left: 5px;
    padding-right: 0;
    padding-top: 2px;
}

.st, .st em {
    color: #666;
}

Select an element {
    font-family: Helvetica;
}

a.fl {
    color: #555;
}

a:link {
    color: #666;
}

body {
    background-color: #000000;
    color: #aaa;
    font-family: Helvetica;
}

cite._Rm {
    color: #7b1c3a;
}

cite._Rm img {
    opacity: 0.4;
}

h3 {
    font-size: 180%;
    font-weight: normal;
}

h3.med {
    color: #333;
    font-size: 80%;
    font-weight: bold;
}

h3.r a {
    color: #c63;
}

span.unknown_loc {
    background-color: #333;
}
}
