css:
@-moz-document domain("app.zeplin.io") {
body {
	background: #222;
}

header {
 	background: #444;
    outline-color: transparent;
}

input {
 	background: transparent;
    padding: .75em;
    border: 1px solid #999;
    border-radius: .5em;
}

#projectInfo {
    background: #333;
}


body,
header,
#projectName,
#projectLink,
#projectInfo,
#projectInfo td:nth-child(2),
#projectTabs input:checked + label,
#screenSearch,
#taglessProjectInfoLine,
.screenInfo .screenName,
#screenCount {
    color: #CCC;
}

#sortOptions input[type="radio"]:checked + span {
    color: #669;
}

.rightSideButton {
 	background: #000
}

.screenInfo .screenName {
 	padding: 0;
}

#dotgrid {
        background-image: linear-gradient(transparent 0px, transparent 2px, #222 2px, #222 20px), linear-gradient(to right, #444 0px, #444 2px, #222 2px, #222 20px) !important
}

}
