/* 
Note : Je suis loin d'être un expert dans le domaine du CSS et du dev' web, soyez indulgent :D
*/
/* i really want this to be global */
@-moz-document domain(yex.tv) {
   body {
    background-color: #0f1419;
    color: #E6E6E6;
}
.light-blue.lighten-1 {
    background-color: #0f1419 !important;
}
.no-border + label {
    color: #E6E6E6;
}
[type="checkbox"].no-border + label {
    /* couleur police catégories */
    margin-left: -32px;
    color: #E6E6E6;
}
/*stats chaînes acceuil */
.allChannelsList .card .card-action {
    background-color: #0f1419;
}
/*barre titre video + boutons */
*,
 ::before,
 ::after {
    box-sizing: inherit;
    background-color: #0f1419;
}
.tv-channel a {
    color: #E6E6E6;
}
.sort-btn {
    color: #f6eeee;
}
/*Couleur plus d'infos page de chaine */
.grey-text.text-darken-4 {
    color: #E6E6E6;
}
.grey-text {
    color: #E6E6E6;
}
.card .card-reveal {
    background-color: #0f1419;
}
/* map */
.map {
    position: absolute;
    z-index: 4;
}
.map {
    background-color: transparent;
}
.map-aside .map-events {
    height: calc(95% - 325px);
    overflow-y: auto;
    background-color: #0f1419;
}
.card {
    position: relative;
    margin: 0.5rem 0 1rem 0;
    background-color: #0f1419;
    transition: box-shadow .25s;
    border-radius: 2px;
}
*,
 ::before,
 ::after {
    box-sizing: inherit;
    background-color: transparent;
}
.map-full-container {
    background-color: transparent;
}
/* Bouton envoyer*/
.material-icons {
    background-color: transparent;
}
/*Suggestion de chaîne*/
.modal {
    background-color: #0f1419;
}
.collapsible-header {
    background-color: #0f1419;
}
/* bas de page footer */
footer.page-footer {
    margin-top: 0;
    margin-top: 50px;
    background-color: #0f1419 !important;
}
/*compteur abonnés youtube */
.yt-subscription-button-subscriber-count-branded-horizontal {
    background-color: #0f1419;
}
.yt-uix-button-subscribe-branded + .yt-subscription-button-subscriber-count-branded-horizontal,
.yt-uix-button-subscribe-branded + .yt-uix-subscription-preferences-button + .yt-subscription-button-subscriber-count-branded-horizontal,
.yt-uix-button-subscribed-branded.external + .yt-subscription-button-subscriber-count-branded-horizontal,
.yt-uix-button-subscribed-branded.external + .yt-uix-subscription-preferences-button + .yt-subscription-button-subscriber-count-branded-horizontal {
    background-color: #0f1419;
}
/*avis chaînes */
.collection .collection-item.avatar {
    background-color: #0f1419;
}
ul.recommandations {
    border: none;
    background-color: #0f1419;
}
.blue {
    background-color: #0f1419;
}
.footer {
    background-color: #0f1419;
}
.card-action {
    display: none;
}
.card-content p {
    display: none;
}
.grey-text {
    color: #E6E6E6 !important; }
}
.grey-text.text-darken-4 {
    color: #E6E6E6 !important;}
