css:
@-moz-document domain(robloxforums.co.uk)
{
  
/*Another product created by BladefuryDev!*/

#logo {
    color: #f5f5f5; /*Main Top Bar logo color, change it to whatever you like.*/
    position: relative;
    padding: 15px 35px;
    top: 15px;
    font-family: 'Lobster', cursive;
    font-size: 27px;
    text-shadow: -3px 3px 2px black;
}

element.style {
    font-size: 20px;
    background: #3a3a3b;
    border: none;
    color: #2C3E50;
}

Style Attribute {
    font-size: 20px;
    background: #3a3a3b;
    border: none;
    color: #f5f5f5;
}

.trow_deleted, .post.deleted_post {
    background: #f76945;
}

.wo_desc {
    color: #999;
    display: inline-block;
    margin: 15px 15px 40px;
    font-size: 15px;
}

.trow_shaded {
    background: #f30;
    border: 1px solid;
    border-color: #f30 #f76945 #f30 #f76945;
}

.wo_title {
    color: #99978f;
    margin-left: 15px;
    display: block;
    margin-top: 20px;
    font-family: Source Sans Pro;
    font-size: 25px;
}

.smalltext {
    font-size: 13px;
    -webkit-text-fill-color: white;
}

.thread_status.dot_newhotfolder:before {
    color: #323233;
    content: "\f06d";
}

.thread_status.dot_lockfolder:before {
    color: #323233;
    opacity: 0.4;
    content: "\f023";
}

.thread_status.newfolder:before {
    color: #323233;
    content: "\f15b";
}

.thread_status.newlockfolder:before {
    color: #323233;
    content: "\f023";
}

.thread_status.hotlockfolder:before {
    color: #323233;
    opacity: 0.4;
    content: "\f023";
}

.thread_status.dot_newfolder:before {
    color: #323233;
    content: "\f15c";
}

.thread_status.dot_folder:before {
    color: #323233;
    opacity: 0.4;
    content: "\f1ae";
}

.thread_status.folder:before {
    color: #323233;
    opacity: 0.4;
    content: "\f15b";
}

.thread_status.lockfolder:before {
    color: #323233;
    opacity: 0.4;
    content: "\f023";
}

.thread_status.newhotfolder:before {
    color: #323233;
    content: "\f06d";
}

.thread_status.hotfolder:before {
    color: #323233;
    opacity: 0.4;
    content: "\f06d";
}

button, input.button {
    background: #3a3a3b;
    color: #FFF;
    padding: 10px 15px;
    cursor: pointer;
    border: none;
    border-radius: 2px;
    outline: 0;
    transition: 600ms;
    font-family: Roboto;
    font-size: 15px;
}

.scrollToTop {
    background: #3a3a3b;
    color: white;
    position: fixed;
    z-index: 4000;
    bottom: 143px;
    right: 20px;
    border-radius: 2px;
    padding: 9px 10px;
    cursor: pointer;
    display: block;
    font-size: 18px;
}

.scrollToDown {
    background: #3a3a3b;
    color: white;
    position: fixed;
    z-index: 4000;
    bottom: 100px;
    right: 20px;
    border-radius: 2px;
    padding: 9px 10px;
    cursor: pointer;
    display: block;
    font-size: 18px;
}

.tcat {
    background: #444;
    color: #FFF;
    padding: 9px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.15);
    font-size: 15px;
}

.tborder {
    background: #fff;
    width: 100%;
    margin: auto auto;
    border-bottom: 0px solid #333;
    box-shadow: 0px 1px 6px 0px black;
}

#header_block {
    background: rgb(58, 58, 59); /*This changes the Header bar color.*/
    width: 100%;
    text-align: left;
    height: 139px;
    border-bottom: 0px solid #0055B3;
    box-shadow: 0px 0px 4px 0px black;
}

#content {
    background: #323233 none repeat scroll 0% 0%; /*This changes the main background's color.*/
    width: auto !important;
    padding: 20px 10px;
    overflow: hidden;
}

.haut {
    background: #323233;
    width: 100%;
    float: right;
    box-shadow: 0px 0px 3px #323233;
    position: fixed;
    height: 70px;
    z-index: 1000;
    text-align: left;
}

a.button:link, a.button:visited, a.button:active {
    background: rgb(58, 58, 59);
    color: #FFF;
    margin: 2px;
    border-radius: 0px;
    padding: 11px 15px;
    transition: all 600ms;
    border: none;
    text-decoration: none;
    font-size: 14px;
}

#home_bit {
    background: #3a3a3b;
    color: #f6f6f6;
    padding: 15px 18px;
    position: relative;
    cursor: pointer;
    font-size: 17px;
    transition: all 150ms;
    border-bottom: 2px solid rgba(0, 0, 0, 0.14);
}

/*Link things!!!*/
a:visited {
    color: #ffffff;
    text-decoration: none;
}

a:link {
    color: #ffffff;
    text-decoration: none;
}
/*End of link things.*/

element.style {
    font-size: 20px;
    background: #3a3a3b; /*Thread Title Bar*/
    border: none;
    color: #2C3E50;
}

.trow1 {
    background: #777 none repeat scroll 0% 0%;
    border: 0px solid;
    padding: 10px;
    border-bottom: 1px solid #333;
}

.pagination .pages {
    font-weight: bold;
    color: #f5f5f5;
}

body {
    background: #323233 none repeat scroll 0% 0%;
    color: #333;
    text-align: center;
    line-height: 1.4;
    margin: 0;
    overflow-y: scroll;
    text-decoration: none;
    font-family: Open Sans;
    font-size: 13px;
}

.pagination a {
    background: #f5f5f500;
    border: 1px solid #0055b300;
}

.trow2 {
    background: #777 none repeat scroll 0% 0%;
    border: 0px solid;
    padding: 10px;
    border-bottom: 1px solid #333;
}

.postbit_buttons > a:link, .postbit_buttons > a:hover, .postbit_buttons > a:visited, .postbit_buttons > a:active {
    color: #323233;
    display: inline-block;
    margin: 2px;
    padding: 7px;
    transition: 200ms;
    font-size: 15px;
}

Style Attribute {
    font-size: 20px;
    background: #3a3a3b; /*Thread Title Bar text color*/
    border: none;
    color: #f6f6f6;
}

#posts_container {
    padding: 0;
    background: #666; /*Frame*/
}

.thead {
    background: #444;
    color: #FFF;
    border-bottom: 1px solid #333; /*Things you'll never see.*/
    padding: 14px 17px;
    font-family: Open Sans;
    font-size: 15px;
}

.post {
    background: #999; /*Post Body Color*/
    overflow: hidden;
    border-radius: 2px;
    border: 2px solid #333;
}

.post .post_author {
    background: #999 none repeat scroll 0% 0%;
    overflow: hidden;
}

.post_controls {
    clear: both;
    padding: 5px 10px;
    overflow: hidden;
    border-top: 1px dashed #323233;
    background: #999999;
}

.navigation {
    background: rgb(58, 58, 59);
    color: #f9f9f9;
    padding: 15px 0px;
    border-bottom: 2px solid #323233;
    font-family: Open Sans;
    font-size: 13px;
}

.tfoot {
    color: rgb(58, 58, 59);
    background: rgb(153, 153, 153);
    padding: 11px;
}

.footer-distributed {
    background: #333;
    width: 100%;
    box-shadow: 1px 0px 3px 0px #000;
    box-sizing: border-box;
    text-align: left;
    font: 16px Open Sans;
    padding: 15px 50px;
    border-top: 0px solid #0055B3;
}

.forum_on {
    color: #323233;
}

.forum_off, .forum_offlock, .forum_offlink {
    color: #a00000;
}

ul.thread_tools li {
    padding-left: 24px;
    padding-bottom: 4px;
    margin-bottom: 3px;
    font-size: 14px;
    color: #999;
    text-decoration: none;
}
}
