css:
@-moz-document domain(deseretnews.com) {   
html,
body,
header,
#main-nav li,
#header-title,
h1,
h2,
h3,
h4,
.toggle__item:not(.toggle__item--selected),
#react_wrapper_footer div,
.caption,
.gallery-credit-single-image,
.article-related__title,
.title,
.top-comments__title,
.screen-name,
.top-comments__list-item-commenter-location,
.header__button {
    background-color: #222 !important;
    color: #ddd !important;
}
#logo,
.beehive {
    color: #222 !important;
    -webkit-filter: invert(80%);
    filter: invert(80%);
}
input {
    color: #fff !important;
}
a:not(#logo):not(.beehive):not(.btn) {
    background: none !important;
    color: #aaa !important;
    text-decoration: none !important;
}
a:hover,
a:visited,
.btn.link:hover {
    color: #fff !important;
}
.btn-success,
.btn,
.btn-default,
input[type="submit"] {
    box-shadow: none !important;
    background: none !important;
    background-color: #333 !important;
    border-color: #aaa !important;
    color: #fff !important;
}
button[disabled="disabled"],
input:disabled {
    background: #4d1100 none repeat scroll 0% 0% !important;
    color: #ff5327 !important;
    border: 1px solid #ff5327 !important;
}
button[disabled="disabled"]:hover,
input:disabled:hover {
    background: #ff5327 none repeat scroll 0% 0% !important;
    color: #4d1100 !important;
    border: 1px solid #4d1100 !important;
}
.btn-success.active,
.btn-success.focus,
.btn-success:active,
.btn-success:focus,
.btn-success:hover,
.btn:hover,
.selected:hover {
    background-color: #333 !important;
    border-color: #fff !important;
}
table,
thead tr th {
    background: transparent none repeat scroll 0% 0%;
    background: #3b3b3b !important;
}
tbody tr {
    border: none !important;
}
}
