css:
@-moz-document domain(egscomics.com) {
body{
	background:#444;
	margin:0;
	padding:0;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:12px;
}
#footer{
	color:#000;
	width:940px;
	text-align:center;
	clear:both;
	padding:10px;
	margin:0 auto;
}
#footer a{
	color:#000;
}
a{
	color:#444;
}
a:hover{
	color:#444;
}
#wrapper{
	width:960px;
	margin:0 auto;
}
#header{
	width:960px;
	clear:both;
}
#logo{
width:230px;display:block;float:left; margin-top:10px;
}
#leftarea{
	width:745px;
	border-radius:10px;
	background:#000;
	padding:15px;
	float:left;
	margin-top:10px;
}
#rightarea{
	margin-top:10px;
	float:right;
	width:160px;
}
#comicbody{
	width:745px;
	text-align:center;
}
#comic{
	padding-top:10px;
}
#news{
	width:435px;
	float:left;
}
#boxad{
	float:right;
	width:300px;
	padding-top:10px;
}
#leaderboard{
	width:728px;
	float:right;
	margin-top:17px;
}
#menu{
	clear:both;
	width:960px;
}
#menu a{
	width:120px;
	height:32px;
	display:block;
	float:left;
}
#menu a:hover{
	background-position:0px -32px;
}
.nav a{
	width:48px;
	height:48px;
	float:left;
	display:block;
	margin:0px 5px;
}
.nav a:hover{
	background-position:0px -48px;
}
.navspace1{
	float:left;
}
.navspace2{
	float:left;
}
.navspace1 div{
	width:48px;
	height:48px;
	float:left;
	margin:0px 5px;
}
.navspace2 div{
	width:48px;
	height:48px;
	float:left;
	margin:0px 5px;
}
.nav{
	width:560px;
	padding:10px 0px;
	margin: 0 auto;
}
}
