css:
@-moz-document url-prefix("https://www.insight.co.kr/news/") {
.news-article-more,[tbl-feed-card],.aside,.fixed-share{display:none!important}
.news-article{width:unset!important}
}

@-moz-document domain("pressian.com") {
.section_v2_3{display:none!important}
.section_v2_12{width:unset!important}
}

@-moz-document domain("ohmynews.com") {
.aside,#article .con_wrap::after, #article #content_wrap::after,.gallery_area,.more_at_box,.relation_news{display:none!important}
.con_wrap_w .content, .con_wrap .content, #content_wrap .content,.article_view{width:unset!important}
}

@-moz-document domain("news.naver.com") {
.content{width:unset!important}
.aside,.lnb_today,.link_news,.article_list{display:none!important}
}

@-moz-document domain("ytn.co.kr") {
#zone2,#subcontents2,.newsview_newsbox{display:none!important}
#zone1{width:unset!important}
}

@-moz-document domain("edaily.co.kr") {
#aside_right,.article_newsetc,#aside_bottom,.news_domino{display:none!important}
.aside_left{width:unset!important}
}

@-moz-document domain("news.nate.com") {
#newsSidebar,.relatedPhoto,.thisTimeNews,#realArtcContents>:nth-last-child(2){display:none!important}
#newsContents,#articleContetns{width:unset!important}
}

@-moz-document domain("news.v.daum.net") {
#mAside,#cEtc,.cp_view{display:none!important}
#mArticle{width:unset!important}
}

@-moz-document domain("joins.com") {
#aside{visibility:hidden!important}
#sub,#divRelateTag,#divRealtime,.hot_click_wrap{display:none!important}
}

@-moz-document domain("hankooki.com") {
#ctnts-w300,.section4,.right_promo,#headlineDH,#headlinePO_2{display:none!important}
#ctnts-w790,#GS_Content,.gsleft{width:unset!important;margin-right:unset!important}
}

@-moz-document domain("hankookilbo.com") {
aside,.other-articles,.new-articles{display:none!important}
.content-body,.article-body{width:unset!important}
}

@-moz-document domain("newsis.com") {
#aside,.mgt27,.today_head,.bxcn2,.livenews{display:none!important}
#content{width:unset!important}
}

@-moz-document domain("joynews24.com") {
section{width:unset!important}
aside,container.view::before,.related,.photo-list{display:none!important}
}

@-moz-document domain("mediatoday.co.kr") {
.container .lo_left_2_1{width:unset!important}
.edit_inner_content,.wp_wingside{display:none!important}
}

@-moz-document domain("journalist.or.kr") {
[src="/iframe/newarticle.html"],.section_3{display:none!important}
.layout_container .container .section_12{width:unset!important}
}

@-moz-document domain("mt.co.kr") {
#aside,.floating_bn_l1,.floating_bn_r1,.article_relbox,#article .spotcon{display:none!important}
#content{width:unset!important}
}

@-moz-document domain("www.mk.co.kr") {
.left_content{width:unset!important}
.right_aside{display:none!important}
#Content{background:unset!important}
}

@-moz-document domain("msn.com") {
aside,.normalsection{display:none!important}
main{width:unset!important}
}

@-moz-document domain("hankyung.com") {
.wrap_article{width:unset!important}
.utility,.wrap_aside,.box_related_list,.isuue_plus_module,.wrap_grid,.ad_module_wrap,.topicNews,[itemprop="articleslide"],.marker{display:none!important}
}

@-moz-document domain("news.mk.co.kr") {
.right_aside,.t_hotnews,.recommend_box,.sns_reply_section ~ *{display:none!important}
.left_content{width:unset!important}
#Content{background:unset!important}
}

@-moz-document domain("sisunnews.co.kr") {
.clearfix,aside{display:none!important}
}

@-moz-document domain("1boon.kakao.com") {
.view_aside{display:none!important}
.cont_view .wrap_section,
.cont_view .wrap_section .cont_section{max-width:1050px!important}
}

@-moz-document domain("hub.zum.com") {
.aside,.article_author,.contents_bottom{display:none!important}
.article_body,#container .contents .article{width:unset!important}
}

@-moz-document domain("news.chosun.com") {
aside,.news_left_aside,.ad_mid_box,.news_today{display:none!important}
article,.news_body{width:unset!important}
}

@-moz-document domain("news.khan.co.kr") {
.art_side,.art_copyright ~ *{display:none!important}
.art_cont{width:unset!important}
.art_cont{background:unset!important}
}

@-moz-document regexp("https?://www.etnews.com/.*[?].*") {
.aside_wrap,.ad_viewtop,.article_footer{display:none!important}
main{width:unset!important}
}

@-moz-document domain("news.donga.com") {
#aside,.article_relation,.view_copyright ~ *,#container ~ *{display:none!important}
#contents,.article_txt{width:unset!important}
}

@-moz-document domain("news.sbs.co.kr") {
.w_article_side,[data-widget_id],.article_relation_area{display:none!important}
.w_article .w_article_cont{min-height:unset!important}
.w_article .w_article_left{padding-right:unset!important}
}

@-moz-document domain("bizn.donga.com") {
.aside,[data-item-id]{display:none!important}
.main_news{width:unset!important}
}

@-moz-document domain("nocutnews.co.kr") {
#divToastnews,#pnlRightArea,#divRecommend,#divTimeNews,.more_n,.reco_n,[alt="뉴스스탠드 구독하기"]{display:none!important}
.content{width:unset!important}
}

@-moz-document domain("hani.co.kr") {
.a-left{width:unset!important}
.a-right,#foot-fix,[id*="dablewidget_"],#header-top-slide,.relation2-area,#ad_box01,#ad_box04{display:none!important}
}

@-moz-document domain("huffingtonpost.kr") {
.ad_spot,.yr-col-body-right{display:none!important}
.entry .entry__content{width:unset!important}
h2{max-width:unset!important}
}

@-moz-document domain("inews24.com") {
container.view::before,.related,container.photo-list,.photo-list{display:none!important}
aside{display:none!important}
section{width:unset!important}
}

@-moz-document domain("pennmike.com") {
aside{display:none!important}
}

@-moz-document domain("koreadaily.com") {
.cp-area-minor,#relationArticles,.todayhotissue-box,#branded-content,#guess-like,#realtion-videos-news{display:none!important}
.cp-area-major{width:unset!important}
}

@-moz-document regexp(".*") {
a{}
}
