css:
@-moz-document url-prefix("http://www.smzdm.com"),
url-prefix("https://www.smzdm.com") {

  /* hidden */
  #index-elevator,
  #index-head,
  #sub-head,
  #feed-side,
  .feed-hot-index,
  .feed-hot-hits,
  .feed-top-hot {
    display: none;
  }

  /* header */
  #global-nav {
    box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12);
  }

  #focus-notice, #chief-notice {
    top: 42px;
    right: 8px;
    display: none;
    border: none;
    padding: 12px;
    position: fixed;
    z-index: 105;
    -webkit-transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
    transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
    background-color: #323232;
    color: rgb(250, 250, 250);
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.26);
    border-radius: 2px;
  }

  #focus-notice a, #chief-notice a {
    color: rgb(250, 250, 250) !important;
  }

  #focus-notice a span {
    padding: 0 3px;
  }
  
  #chief-notice {
  	top: 90px !important;
  }

  /* feed-main */
  #feed-main {
    float: none !important;
    margin: 0 auto;
  }
}
