css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.animeplus.tv"), url("http://www.animeplus.tv/"), domain("animeplus.tv")
{

/* Remove Ads */
#upper_header .right_col, 
#content>[style="border-collapse: collapse"], 
#content>[align="center"], 
.sc_ad,
.ad,
#rcjsload_bbc25d, 
#rcjsload_79dccc, 
#BB_SK_7397343889, 
body>.right_col, 
#body>.right_col, 
body>div:nth-of-type(2), 
#manga_releases, 
.mcimg  
#content>#g_promo,
iframe+table[width="745"],
iframe[width="742"][height="195"],
#downloads>table[width="720"],
div[style="width:336px;height:768px;margin:auto;"],
div[align="center"]>table[width="740"],
.ad>iframe,
#mini-announcement,
.right_col>iframe,
#ad_unit>iframe,
/* .safeuploada-content,  */
iframe>#MarketGidLuxupDiv16180,
.MarketGidComposite16180,
.note {
    visibility: hidden!important;
    display: none!important;
}


/* Layout */
.vmargin {
    margin-left:0!important;
    padding-left:35px!important;
    display: inline-block!important;
}

div>iframe, .vmargin {
    width:818px!important;
    height:500px!important;	
}

#body>div {
    width: auto!important;
}









/* Stream Background */
#streams div:nth-child(2) div:nth-child(1) {
	background: #052542;
}

#streams div:nth-child(3) div:nth-child(1) {
	background: #052542;
}

#streams div:nth-child(4) div:nth-child(1) {
	background: #052542;
}

#streams div:nth-child(5) div:nth-child(1) {
	background: #052542;
}


/* BACKGROUND */
/* FULL */
#page, html {
    background: url("https://i.imgur.com/shwS8sJ.jpg")!important;
	background-size: 1700px 800px!important;
    background-position: 100% 60%!important;
    background-attachment:fixed!important;
}

/* HEADER */
#header{
    background:url("https://i.imgur.com/jR0f2He.gif")transparent;
    background-size: 20%!important;
    background-position: 95% 100% !important;
}

#upper_header, #menu{
	background: url("https://i.imgur.com/E95FKr3.gif")  transparent no-repeat!important;
    background-size: 15% auto!important;
    background-position: 23% 35%!important ;
}

#upper_header div {
    background: url("https://i.imgur.com/jR0f2He.gif") transparent no-repeat;
    background-size: 100%!important;
    background-position: 100% -50% !important;
}

#upper_header div {
    opacity: 1;
  	transition: all 1.5s linear;
    
}

#upper_header div:hover {
    opacity: 0;
}


/* BODY */
#body, #streams, #downloads, #comments, #footer {
    background-color:#052542;
    border: 1px  solid #052542;
}


.fb_iframe_widget span iframe[style] {
    background: white!important;
}









/* BODY TITLES FONT */
#top_block h1, #slink, h3, #drama_updates tbody tr td div a, #drama_updates tbody tr td ul li a, h3 a, #content h1{
    color: #F9FBF6!important;
}

/* BODY FONTS */
#genre_list ul li a, #drama_releases h2, #drama_updates tbody tr td ul li a, .descr, span, #videos ul li a, .series_index tbody tr td a, #updates tbody tr td div a, #slink a #comments h3{
    color: silver!important;
}

/* VISITED */
#body a:visited {
    color: #35bf0b!important;
}

/* HOVER */
#body a:hover{
    color: #c6efff!important;
}

/* HEADER FONTS */
#lower_header div, #menu li a, .box h2, #drama_releases h2, #series_info, #series_info .left_col img, #videos, #comments_heading{
    background: #050A30!important;
    border: 1px  solid #050A30!important;
    color: wheat!important;
}

/* BODY FONTS */
#drama_releases, div.box, #container li:nth-child(2n+1){
    background:#052542!important;
	color: white!important;
    font:12px "YouTube Noto",Roboto,arial,sans-serif;

}      

/* CONTAINER FONTS */
.box > div, #container a, .comment{
    background-color:#052542!important;
	color: silver!important;
	border: 1px  solid #052542;
}


/* CONTAINER STYLE */
#container #rr li, #container #rs li {
    background-image:none!important;
    list-style: inside;
    list-style-type: circle;
}

/* PAGINATION FONT */
.button-content {
	color: black!important;
}

}
