css:
@-moz-document url-prefix("https://www.reddit.com/r/apple")
{

    body, 
    .content .commentarea h1
    {
        font-family: -apple-system, "SF Pro Text", "Helvetica Neue", sans-serif;
    }

    h2,
    .titlebox h1,
    .thing .title,
    .entry .buttons li a,
    #search input[type="text"],
    .tabmenu li,
    .morelink,
    .helplink,
    .side .md h1, .user,
    .user a,
    .user .userkarma,
    .pref-lang,
    .pref-lang a,
    .logout,
    .logout a
    {
        font-family: -apple-system, "SF Pro Text", "Helvetica Neue", sans-serif;
    }

    @media (-webkit-min-device-pixel-ratio:2),(min-resolution:192dpi)
    {
        body,
        .content h1 
        {
            font-family: -apple-system, "SF Pro Text", "Helvetica Neue", sans-serif;
        }
    }

    .thing.stickied a.title,
    .linkflair-removed .title>.linkflairlabel,
    .linkflair-removed>.linkflairlabel,
    .linkflair-context .title>.linkflairlabel,
    .linkflair-context>.linkflairlabel
    {
        font-family: -apple-system, "SF Pro Text", "Helvetica Neue", "sans-serif";
    }

    #search input[type=text]
    {
        font-family: -apple-system, "SF Pro Text", "Helvetica Neue", sans-serif;
    }

}
