css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbs.thegoyimknow.to"), regexp("^https?:\/\/([a-zA-Z\d-]+\.){0,}dstormer6em3i4km\.onion.*$") {
* {
    background-color: #222 !important;
    color: #ddd !important;
    border-color: #333 !important;
    /*border-style: solid !important;*/
}
* a {
    color: #00ADEE !important;
}
a:hover {
    color: #006e96 !important;
    text-decoration: underline !important;
}
*:hover {
    background-image: none !important;
}
* a:visited {
    color:  #a80ef3 !important;
}
input {
    box-shadow: 0 0 7px 1px #222 inset !important;
    background-color: #333 !important;
}
input:focus {
    box-shadow: 0 0 2px 1px #222 inset !important;
    background-color: #333 !important;
}
.gfgsd {
    background-color: #000 !important;
}
.notifications>ul>li:not(.read)>a>div>p, .notifications>ul>li:not(.read), .unread, .notifications>ul>li:not(.read)>a>div>p>span, .notifications>ul>li:not(.read)>a>div>i, .notifications>ul>li:not(.read)>a>div {
    background-color: #1a4556 !important;
    color: #fff !important;
    font-weight: bold;
}
    
    item.notification.large-notification.unread {
            transition: all ease-in-out 1s !important;;
    }
/*.notifications>ul>li:not(.read)>a>div>p>span { color: #aaa !important; }*/
.has-like>.fa-heart, .read-state>.fa-circle { color: darkviolet !important;}

.d-header .icons .unread-notifications {
    background-color: darkviolet !important;
    color: white !important;
    font-weight: bold;
}

.topic-list .main-link a.title { padding: 0 !important; }
    
.d-header .icons .unread-private-messages, .d-header .icons .ring {
    background-color: darkgreen !important;
    color: white !important;
    font-weight: bold !important;
}

    #multilinks-selection-container, #multilinks-links-container { background-color: rgb(0,0,100,0) !important;}
    #multilinks-selection-container > div { background-color: rgba(0,0,137,0.5) !important;}
    #multilinks-links-container > div { background-color: rgba(255,255,0,.5) !important;}
    #user-card, .card-content, #user-card * { background-color: rgba(0,0,0,0) !important; }
    #user-card:hover, #user-card *:hover { background-color: rgba(34,34,34,1) !important; }
    .spinner {
        border: 4px solid #0a83b1 !important;
        border-right-color: rgba(0,0,0,0) !important;
        background-color: rgba(0,0,0,0) !important;
    }
    .timeline-handle, .badge.badge-notification.clicks { background-color: darkviolet !important; }
    .timeline-scrollarea { border: solid 1px;}
    
    .highlighted, .highlighted * { 
        transition: all  0.5s;
        animation: background-fade-highlight 2.5s ease-out !important;
        background-color: black !important; 
    }
    header.d-header.clearfix > div {
    border-bottom-width: 1px;
    border-color: #7a7a7a;
    border-left-width: 0;
    border-right-width: 0;
    border-style: solid;
    border-top-width: 0;
}

input {
    background-color: #333 ;
    box-shadow: 0 0 7px 1px #222 inset ;
}

input:focus {
    background-color: #333 ;
    box-shadow: 0 0 2px 1px #222 inset ;
}

::-webkit-scrollbar-thumb {
    background: #444;
    -webkit-border-radius: 1ex;
}

::-webkit-scrollbar {
    width: 1em;
    background: #222;
}

::-webkit-scrollbar-thumb:hover {
    background: #666;
}

::-webkit-scrollbar-thumb:active {
    background: #888;
}

ul.select-box-kit-collection.ember-view > li > span {
    z-index: 1000 !important;
}

item.notification.large-notification.unread {
    transition: all ease-in-out 1s ;
}
    @keyframes background-fade-highlight {
	0% {
		background-color: black !important;
	}
	100% {
		background-color: transparent  !important;
	}
    }
    
}
