css:
@-moz-document url(http://www.reddit.com/) {
    .commentarea .thing .arrow {
        margin: 5px 0;
    }
    #siteTable .arrow.up,
    .thing .arrow.up,
    .res-nightmode .arrow.up:hover {
        background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) 0px -12px !important;
    }
    #siteTable .arrow.upmod,
    .thing .arrow.upmod {
        background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) -16px -12px !important;
    }
    #siteTable .arrow.down,
    .thing .arrow.down,
    .res-nightmode .arrow.down:hover {
        background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) 0px -28px !important;
    }
    #siteTable .arrow.downmod,
    .thing .arrow.downmod {
        background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) -16px -28px !important;
    }
    .midcol .score.likes {
        color: #4aabe7;
    }
    .midcol .score.dislikes {
        color: #8870ff;
    }
    #header-img.default-header,
    #header-img {
        z-index: 99;
        width: 0;
        padding-left: 112px;
        height: 31px;
        margin: 5px 0 0px 5px;
        vertical-align: top;
        font-size: 0;
        background-image: url(https://voat.co/images/voat-logo.png);
    }
    #sr-header-area {
        background-color: #f4f4f4;
        height: 24px;
        line-height: 24px;
        font-size: 12px;
        overflow: hidden;
        position: relative;
        padding-left: 5px;
        white-space: nowrap;
        font: 11px Arial, sans-serif;
        text-transform: none;
        border-bottom: 1px solid #dcdcdc;
    }
    #header {
        position: relative;
        background-color: #fff;
        z-index: 99;
        border-bottom: 1px solid #dcdcdc;
        border-top: 1px solid #dcdcdc;
    }
    #header-bottom-left {
        margin: 0 auto;
        max-width: 1200px;
        position: relative;
        font-size: larger;
    }
    #header-bottom-right {
        right: 200px;
    }
    .flat-list li a {
        color: #818181;
    }
    #sr-header-area .selected a {
        color: #818181;
    }
    #sr-header-area .width-clip {
        line-height: 24px;
    }
    .selected,
    .choice.primary {
        font-weight: normal;
    }
    .dropdown.srdrop .selected {
        color: #4aabe7;
    }
    body.with-listing-chooser {
        background: #f4f4f4;
    }
    body.with-listing-chooser.listing-chooser-collapsed > .content,
    body.with-listing-chooser.listing-chooser-collapsed .footer-parent {
        margin: 0 auto;
        position: relative;
        max-width: 1200px;
    }
    body.with-listing-chooser.listing-chooser-collapsed .listing-chooser {
        padding-right: 0px;
    }
    body.with-listing-chooser.listing-chooser-collapsed > .content,
    body.with-listing-chooser.listing-chooser-collapsed .footer-parent {
        border: 1px solid #d1d1d1;
        top: 10px;
        z-index: -2;
        background: #fff;
    }
    body > .content .link .rank,
    .rank-spacer {
        width: 0ex;
        color: #fff;
    }
    .side {
        margin: 10px 5px 0 5px;
        border: 1px solid #d1d1d1;
        padding: 0px 5px;
    }
}
