css:
@-moz-document domain("italiancoders.it") {
* {
    border-color: #444 !important;
    text-shadow: none !important;
    box-shadow: none !important;
}
.typology-fake-bg {
    background-color: #191919 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}
::selection {
    background: #4183C4 !important;
    color: #fff !important;
}
::-moz-selection {
    background: #4183C4 !important;
    color: #fff !important;
}
.post-letter {
    color: rgba(255, 255, 255, .07)
}
.typology-section {
    background: #232323 !important;
    color: #ddd !important;
}
.typology-section h1,
.typology-section h2,
.typology-section h3,
.typology-section h4,
.typology-section h5,
.typology-section h6 {
    color: #ddd !important;
}
.typology-section p,
.typology-section li {
    font-family: 'Roboto', sans-serif;
    font-weight: lighter;
}
.typology-section .section-content p a,
.typology-section .section-content ul a,
.typology-section .section-content ol a {
    color: #4183C4 !important;
    border-bottom: 0 !important;
}
.entry-title > a {
    -webkit-transition: all .15s ease-in-out;
    -moz-transition: all .15s ease-in-out;
    -ms-transition: all .15s ease-in-out;
    -o-transition: all .15s ease-in-out;
    transition: all .15s ease-in-out;
    text-decoration: none;
    color: #ddd;
}
.entry-title > a:hover {
    color: #4183C4;
}
.entry-tags {
    margin: 0;
    margin-bottom: 10px;
    clear: both;
    color: #ddd !important;
    padding: 0;
    border: 0;
    font-size: 100%;
    font: inherit;
    vertical-align: baseline;
}
.entry-tags > a {
    background: #333 !important;
    color: #ccc !important;
    text-decoration: none !important;
    position: relative;
    display: inline-block;
    padding: .4em .5em;
    margin: 2px 2px 2px 0;
    line-height: 1;
    white-space: nowrap;
    text-align: center;
    border-width: 1px;
    border-style: solid;
    border-radius: 0;
    transition: all .15s ease-in-out;
    cursor: pointer;
    font: inherit;
    vertical-align: baseline;
}
.entry-tags > a:hover {
    background-color: #4183C4 !important;
    color: #eee !important;
    border-width: 1px;
    border-style: solid;
    border-radius: 0;
}
.typology-single-sticky,
.typology-footer {
    background-color: #080808 !important;
}
.typology-sticky-c > span {
    color: #ddd;
}
.sticky-author-title > a,
.typology-sticky-l > a,
.typology-sticky-r > a {
    color: #ddd;
}
.sticky-author-title > a:hover,
.typology-sticky-l > a:hover,
.typology-sticky-r > a:hover {
    color: #4183C4 !important;
}
input,
textarea {
    background-color: #111!important;
    color: #ddd;
}
::-webkit-input-placeholder {
    color: #666 !important;
    opacity: 1 !important;
}
::-moz-placeholder {
    color: #666 !important;
    opacity: 1 !important;
}
::placeholder {
    color: #666 !important;
    opacity: 1 !important;
}
blockquote:after,
blockquote:before,
q:after,
q:before {
    box-shadow: 0 0 0 10px #ddd;
}
blockquote:before,
q:before {
    background: #4183C4;
}
blockquote span {
    color: #eee!important;
}
/** theme from **/

.EnlighterJS,
.EnlighterJSWrapper {
    font-family: Monaco, Courier, monospace;
}
.EnlighterJSTabPane .controls li {
    background-color: #303030;
    font-size: 12px;
    color: #f2f2f2;
    border-radius: 0px;
}
.EnlighterJSTabPane .controls li:hover {
    color: #8F9657;
    background-color: #303030;
}
.EnlighterJSTabPane .controls .selected,
.EnlighterJSTabPane .controls .selected:hover {
    background-color: #000000;
}
/* Inline Styles */

.EnlighterJS span {
    color: #f8f8f8;
}
span.enlighterEnlighterJS {
    background-color: #141414;
}
span.EnlighterJS {
    border-radius: 3px;
    padding: 3px 5px;
}
span.EnlighterJS span {
    color: #0978B9;
    font-weight: bold;
}
/* Line numbers */

ol.EnlighterJS {
    background-color: #36382F;
    list-style-type: decimal;
    border: solid 1px #222222;
    border-radius: 3px;
}
ul.EnlighterJS {
    background-color: #f2f2f2;
    border: solid 1px #222222;
}
ol.EnlighterJS li,
ul.EnlighterJS li {
    border: none;
    border-left: 1px solid #939393;
    line-height: 16px;
    background-color: #141414;
}
/* Line hover */

ol.EnlighterJS.hoverEnabled li:hover,
ul.EnlighterJS.hoverEnabled li:hover {
    background-color: #000000;
    border: none;
    border-left: 1px solid #939393;
}
/* Special line highlight */

ol.EnlighterJS li.specialline,
ul.EnlighterJS li.specialline {
    background-color: #313131;
}
/* base styles */

ol.EnlighterJS,
ol.EnlighterJS li,
ul.EnlighterJS,
ul.EnlighterJS li {
    font-family: Monaco, Courier, monospace;
    font-size: 10px;
    color: #939393;
}
/* default symbol style */

.EnlighterJS span {
    font-family: Monaco, Courier, monospace;
    font-size: 12px;
    line-height: 16px;
    color: #DCDCDC;
}
/* Symbol styles */
/* Start Delimiter e.g. <?php */

.EnlighterJS .de1 {
    color: #DCDCDC;
}
/* Stop Delimiter e.g. ?> */

.EnlighterJS .de2 {
    color: #DCDCDC;
}
/* Language Kewords */

.EnlighterJS .kw1 {
    color: #569CD6;
}
/* C# keywords: return, public, private etc. + int, bool, string etc. */

.EnlighterJS .kw2 {
    color: #92CAF4;
}
/* C# Nothing currently */

.EnlighterJS .kw3 {
    color: #4EC9B0;
}
/* C# Classes */

.EnlighterJS .kw4 {
    color: #B8D7A3;
}
/* C# Interfaces */
/* Comments */

.EnlighterJS .co1 {
    color: #57A64A;
}
.EnlighterJS .co2 {
    color: #57A64A;
}
/* Strings */

.EnlighterJS .st0 {
    color: #C8C8C8;
}
/* C# chars (single quote string) | HTML/XML attribute key  */

.EnlighterJS .st1 {
    color: #D69D85;
}
/* string value */

.EnlighterJS .st2 {
    color: #8F9657;
}
/* Numbers */

.EnlighterJS .nu0 {
    color: #B5CEA8;
}
/* Function calls: function() */

.EnlighterJS .me0 {
    color: #DCDCDC;
}
/* C# Noting currently */
/* Method calls: .method() */

.EnlighterJS .me1 {
    color: #DCDCDC;
}
/* Method invocation */
/* Brackets */

.EnlighterJS .br0 {
    color: #DCDCDC;
}
/* Bracket */
/* Symbols/operators */

.EnlighterJS .sy0 {
    color: #B4B4B4;
}
/* Symbol/operators */
/* Escape Sequences */

.EnlighterJS .es0 {
    color: #DCDCDC;
}
/* Escape symbol */
/* Regular Expression outside Strings */

.EnlighterJS .re0 {
    color: #9A5334;
}
/* Regex */
/* Raw Code Pane */

.EnlighterJSWrapper pre {
    font-family: Monaco, Courier, monospace;
    background-color: #141414;
    font-size: 12px;
    color: #f8f8f8;
    line-height: 20px;
}
/* Tab Pane */

.EnlighterJSWrapper .EnlighterJSToolbar > a {
    border-radius: 0px;
    opacity: 0.8;
}
.EnlighterJSWrapper .EnlighterJSToolbar > a:hover {
    opacity: 1.0;
}
}
