css:
@-moz-document domain("open.spotify.com") {
@media (max-width: 960px) {
	body {
		min-width: 0;
		min-height: 0;
	}
	.Root {
		overflow: hidden;
	}
	.Root__top-bar {
		height: auto;
		min-height: 0;
		max-height: 60px;
	}
	[dir=ltr] .Root__top-bar > header {
		max-width: calc(100vw - 20px);
	}
	[dir=ltr] .Root__top-bar > header > div > button {
		margin-right: 0 !important;
	}
	[dir=ltr] .Root__top-bar > header > div > div > div[role="search"] > input {
		width: 200px !important;
	}
	[dir=ltr] .Root__top-bar > header > div:last-child {
		flex: 0 0 calc(90vw - 40px);
		text-align: right;
		/*pointer-events: none;*/
		z-index: -1;
	}
	[dir=ltr] .Root__top-bar > header > div:last-child > button[data-testid="user-widget-link"] {
		margin-right: 0 !important;
		margin-left: auto !important;
	}
	/* left-nav-bar*/
	.Root__nav-bar {
		display: block;
	}
	.Root__nav-bar {
		background-color: rgba(0, 0, 0, .8);
		/* #1db954 rgba(29, 185, 84, .8) */
		position: relative;
		transition-duration: .3s;
		transition-timing-function: ease-in-out;
	}
	[dir=ltr] .Root__nav-bar {
		left: -221px;
	}
	[dir=rtl] .Root__nav-bar {
		right: -221px;
	}
	[dir=ltr] .Root__nav-bar:hover {
		left: 0;
	}
	[dir=rtl] .Root__nav-bar:hover > div,
	[dir=rtl] .Root__nav-bar > div:hover {
		right: 0;
	}
	.navBar-GDPR,
	.navBar-signupPrompt {
		display: flex;
	}
	.btn {
		letter-spacing: 0;
		min-width: 0;
	}

	.Root__top-container {
		grid-template-columns: 20px 1fr;
	}
	/*
	[dir=ltr] .Root__main-view,
	[dir=rtl] .Root__main-view {
		padding-right: 0;
		padding-left: 0;
		padding-bottom: 0;
		max-width:calc(100vw - 20px);
	}*/
	/* main-view-container */
	.main-view-container .os-viewport {
		overflow-x: hidden !important;
	}

	.main-view-container__scroll-node > div {
		max-width: calc(100% - 10px);
	}
	.main-view-container__scroll-node-child section {
		grid-column: 1/3 !important;
	}
	.main-view-container__scroll-node-child section div[role="row"] > div[data-testid="tracklist-row"] {
		padding: 0;
		grid-template-columns: 16px minmax(180px, auto) 70px !important;
	}
	.main-view-container__scroll-node-child section div[role="row"] > div[data-testid="tracklist-row"] > div:last-child > div,
	.main-view-container__scroll-node-child section div[role="row"] > div[data-testid="tracklist-row"] > div:last-child > button {
		margin-right: 0;
	}
	.main-view-container__scroll-node-child section div[role="row"] > div[data-testid="tracklist-row"] > div:last-child {
		grid-column: auto !important;
	}

	.main-view-container__scroll-node-child > section > div:first-child {
		padding: 0px;
		flex-direction: column;
	}
	.main-view-container__scroll-node-child > section > div:first-child {
		padding: 0px;
		flex-direction: column;
	}
	.main-view-container__scroll-node-child > section > div:first-child h1 {
		font-size: 36px !important;
		line-height: 36px !important;
	}

	/* search */
	[dir=ltr] .SearchInputBox {
		left: 0;
	}
	[dir=rtl] .SearchInputBox {
		right: 0;
	}
	.SearchInputBox {
		padding-bottom: 8px;
	}
	.SearchInputBox__input {
		font-size: 20px;
		line-height: 30px;
	}
	.Search__content {
		padding-top: 40px;
	}
	.contentSpacing {
		padding: 0;
	}
	.TrackListHeader__body {
		width: auto;
		margin: 0 0 0 20px;
	}
	.TrackListHeader__children {
		display: block;
	}
	.TrackListHeader__children .btn.btn-green {
		padding: 11px 22px;
	}
	.tracklist-col.position-outer {
		width: 2em;
	}
	.tracklist-row .more {
		min-width: 35px;
	}
	[dir="ltr"] .tracklist-col-duration {
		padding-right: 0;
		font-size: 0.85em;
		width: 3.5em;
	}
	.track-name-wrapper .second-line {
		font-size: 0.8em;
	}
	.TrackListRow__explicit-label {
		overflow: hidden;
		font-size: 8px;
		max-width: 2em;
	}

	.album {
		padding-top: 1em;
	}
	.entity-info {
		-ms-flex-direction: column;
		flex-direction: column;
	}
	.entity-info .media-object {
		margin: 0 auto 0 0;
	}
	.entity-info .media-bd {
		width: 100%;
	}

	/*ad*/
	.Root__main-view > .main-view-container ~ div {
		height: 0;
	}
	.Root__main-view--has-ads {
		padding-bottom: 100px;
	}
	.Root__ads-container {
		display: none;
	}

	/*now-playing-bar*/
	.Root__now-playing-bar,
	.Root__now-playing-bar > footer {
		max-width: 100vw;
		min-width: 100vw;
	}
	.Root__now-playing-bar > footer {
		min-width: 0;
	}
	.Root__now-playing-bar > footer > div {
		flex-wrap: wrap;
	}
	.Root__now-playing-bar > footer > div > div:first-child {
		min-width: 150px;
	}
	.Root__now-playing-bar > footer > div > div:first-child > div > div:nth-child(2) {
		margin: 0 5px 0 5px;
	}
	.Root__now-playing-bar > footer > div > div:nth-child(2) > div.player-controls > .player-controls__buttons {
		width: auto;
		margin-bottom: 3px;
		gap: 0px;
	}
	.Root__now-playing-bar > footer > div > div:nth-child(2) > div.player-controls > .player-controls__buttons > div > button {
		min-width: 20px;
		width: 20px;
	}
	.Root__now-playing-bar > footer > div > div:last-child {
		width: 100%;
		justify-content: space-between;
	}
	.Root__now-playing-bar > footer > div > div:last-child > .ExtraControls{
		justify-content: space-between;
	}
	.Root__now-playing-bar .control-button {
		height: 32px;
		min-width: 20px;
		width: 20px;
	}

	
	.now-playing-bar-container > div[data-testid="signup-bar"] {
		display: none;
	}
	.now-playing-bar {
		-ms-flex-align: start;
		align-items: flex-start;
		padding-top: 10px;
	}
	.now-playing-bar__left {
		width: auto;
	}
	.now-playing {
		flex-wrap: wrap;
	}
	.track-info.ellipsis-one-line {
		flex-basis: calc(100% - 86px);
	}

	.now-playing-bar__right {
		position: absolute;
		right: 20px;
		bottom: 0;
	}
	.volume-bar {
		width: 100px;
	}
	.inputBox {
		padding: 16px 0;
	}
	.inputBox-input {
		font-size: 24px;
		line-height: 28px;
	}
	._2UzxvZHwCBmRIowVNIeNvU {
		-webkit-box-orient: vertical;
		-ms-flex-direction: column;
		flex-direction: column;
	}
	.ef543f98d8d05b0e0138dc206bbac57d-scss {
		width: 330px;
		margin-left: -200px;
	}
}
@media (max-width: 380px) {
	.TrackListHeader {
		-ms-flex-direction: column;
		flex-direction: column;
	}
}
}
