css:
@-moz-document url-prefix("http://bbs.3dmgame.com"),
url-prefix("http://bbs.3dmgame.com") {
  body {
    background: #f1f1f1;
  }
  input:focus::-webkit-input-placeholder {
    color: transparent;
  }
  input:focus:-moz-placeholder {
    color: transparent;
  }
  /* Firefox 18- */
  input:focus::-moz-placeholder {
    color: transparent;
  }
  /* Firefox 19+ */
  input:focus:-ms-input-placeholder {
    color: transparent;
  }
  /* oldIE ;) */
  .wp,
  #h_nv,
  .ct2_a .mn {
    width: auto;
  }

  table.hd_table {
    width: 90%;
    border-bottom: none;
    margin-top: 50px;
  }
  .hd_table table {
    height: 20px;
    width: 100%;
  }
  .hd_table table img,
  .hd_table table tbody tr:last-child,
  .hd_table td[width="15"],
  #scbar a.xi2,
  #h_nv {
    display: none;
  }
  .hd_table td {
    background-image: none;
  }
  #hd .wp,
  .wp.a_h {
    background: transparent;
  }
  #hd {
    margin: 0 5%;
  }
  #hd .pipe,
  #hd .h_menu a {
    color: #333;
  }
  #h_nv {
    margin: 0;
    padding: 0;
  }
  #h_nv ul {
    bottom: auto;
    right: auto;
  }
  #h_nv li {
    background: none;
    margin-left: 0;
  }
  #h_nv a {
    border: none;
    padding: 0;
    padding-right: 16px;
  }
  #hd .h_menu {
    position: absolute;
    right: 0;
    left: 0;
    top: 0;
    padding: 10px 5%;
    background: white !important;
    box-shadow: 0 3px 9px 0 rgba(0, 0, 0, 0.10), 0 0 0 1px rgba(0, 0, 0, 0.04);
  }
  #scbar {
    background: transparent;
    border-top: none;
    border-bottom: none;
    position: absolute;
    left: 5%;
    top: 0;
  }
  #scbar_hot {
    padding-left: 0;
  }
  .scbar_txt_td,
  .scbar_type_td {
    background: none;
    width: auto;
  }
  #scbar input {
    background: no-repeat center bottom, center calc(100% - 1px);
    background-size: 0 100%, 100% 100%;
    border: none !important;
    transition: background 0s ease-out;
    background-image: linear-gradient(to top, #009688 2px, rgba(0, 150, 136, 0) 2px), linear-gradient(to top, rgba(0, 0, 0, 0.26) 1px, transparent 1px);
    padding: 5px 0;
    font-size: inherit !important;
  }
  #scbar input:focus {
    background-size: 100% 100%, 100% 100%;
    transition-duration: 0.3s;
  }
  #scbar .z {
    padding-right: 20px;
  }

  .scbar_icon_td {
    background: none;
    width: 0;
  }

  .scbar_btn_td {
    background: none;
    width: auto;
  }
  #scbar_btn {
    opacity: .6;
    background-position: 0 0;
    width: 23px;
    margin-left: 5px;
    background-image: url(data:image/png;base64,);
  }

  #pt {
    padding-left: 0;
  }

  .m_pgt {
    padding: 15px 10px;
    margin: 0 5%;
    background: white !important;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.04);
    border-radius: 2px;
  }

  #pt,
  .mn {
    padding: 0 5%;
  }
  #chart {
    padding: 0 10px;
    margin: 0 5%;
    position: relative;
    top: -40px;
  }

  #ct {
    padding: 0;
  }
  .wp,
  #ct,
  .fl {
    background: transparent;
  }
  .fl .bm {
    margin-bottom: 10px;
    border: none;
    background: white !important;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.04);
    border-radius: 2px;
  }
  .fl .bm:hover {
    box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.04);
  }
  .fl .bm_h,
  #online .bm_h,
  .lk .bm_h {
    border: none;
  }
  .fl .bm_h {
    padding: 10px;
  }
  .fl .bm_h h2 {
    font-size: 16px;
    line-height: 32px;
  }

  /* main-block */
  .bm.bml.pbn,
  .tl.bm.bmw {
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.04);
    border-radius: 2px;
    border: none;
  }

  .bm.lk {
    background: transparent;
    ;
  }

  .ct2_a .mn {
    width: -webkit-fill-available;
  }

  #postlist.pl.bm,
  #pgt.pgs.mbm.cl,
  #f_pst.pl.bm.bmw,
  .pgs.mtm.mbm.cl,
  #editorbox.bm.bw0.cl,
  .pgbtn {
    margin: 0 5%;
  }

  #postlist,
  #f_pst,
  #editorbox {
    background: white !important;
    border-radius: 2px;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.04);
  }

  #editorbox.bm.bw0.cl {
    margin-bottom: 10px;
    padding: 10px;
  }

  #editorbox.bm.bw0.cl .tb {
    border-bottom: none;
  }

  #editorbox.bm.bw0.cl .tb a {
    border: none;
    font-size: 16px;
    padding: 0;
  }

  #editorbox.bm.bw0.cl .mbm {
    margin-bottom: 0 !important;
  }

  #editorheader~body {
    background: white !important;
  }

  #autopbn, .pgbtn a {
    background: #fff;
  }

  #autopbn:hover, .pgbtn a:hover {
    box-shadow: 0 -1px 0 #e0e0e0, 0 0 2px rgba(0, 0, 0, .12), 0 2px 4px rgba(0, 0, 0, .24);
    text-decoration: none;
  }

  #wp .ct2_a {
    padding: 0;
  }
  
  .pgbtn a {
    margin-top: 10px;
  }
}
