css:
@-moz-document domain("itglue.com") {
/* Always display Copy icon */
.copy-button-styled {
	opacity: 1 !important;
}

/* Content Area & Right-Column Realtive Width */
@media screen and (min-width: 992px) and (max-width: 1600px) {
    .content-body .col-sm-4 {
		width: 320px;
    }
    .content-body .col-sm-8 {
		width: calc(100% - 320px);
    }
}
@media screen and (min-width: 1600px) {
    .content-body .col-sm-4 {
		width: 23%;
    }
    .content-body .col-sm-8 {
		width: 77%;
    }
}
.recent-items ul li {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

/* Organizations Row Height */
.organization-row {
	line-height: 23px;
}
#all_organizations .org.row {
	margin-bottom: 2px;
}
#all_organizations .letter-label {
	margin-top: 0px;
}

/* Recent Organizations */
#recent_organizations {
	margin-top: 0px !important;
    overflow: hidden;
	height: 129px;
}
.organization.tile .caption {
    height: 33px;
}

/* Navigation Bar */
.itglue-sidebar .nav {
    line-height: 1;
}
.itglue-sidebar .badge {
    right: 18px;
}
.edit-sidebar {
	float: left !important;
}

/* Recent Activity */
.activity-sidebar .activity-item {
		max-width: 100%;
}
@media screen and (min-width: 992px) and (max-width: 1600px) {
    .content-body .col-md-3 {
		width: 350px;
    }
    .content-body .col-md-9 {
		width: calc(100% - 350px);
    }
}
.activity-sidebar .activity-item .panel dd {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
dl.dl-horizontal dt {
	float: left;
}
dl dd {
	margin-bottom: 12px;
}

/* Organization Home */
.row .col {
	line-height: 1.3;
}
section {
	margin-bottom: 30px;
}
#recent_items_updated {
	margin-top: 24px;
}

/* Global Assets */
ul.menu-items li {
	margin: 0 15px 15px 0;
    border: 1px solid #ddd;
}
ul.menu-items li a {
	width: 88px;
}
ul.menu-items li a i {
    font-size: 21px;
    margin-top: 5px;
    margin-bottom: 4px;
}
ul.menu-items li a span {
    text-overflow: ellipsis;
    line-height: 1.35;
}

/* Alert Banners */
.alert {
	padding: 10px;
}
}
