css:
@-moz-document domain("www.anaitgames.com") {
/**** GENERAL ****/

body {
    background-color: #151515;
    color: #d0d0d0;
}
/* Color rojo de los enlaces */

a {
    color: #d23030;
}
/* Color de las cajas de texto */

textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    color: #dbdbdb;
    background-color: #151515;
    border: 0px;
}
/* Quito espacio al final */

#bgskin {
    padding-bottom: 50px;
}
/**** BARRA DE NAVEGACIÓN ****/
/* Color del fondo de la barra de navegación */

.navbar-inner {
    background-color: #2b2b2b;
    background-image: -moz-linear-gradient(top, #2b2b2b, #2b2b2b);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#2b2b2b), to(#2b2b2b));
    background-image: -webkit-linear-gradient(top, #2b2b2b, #2b2b2b);
    background-image: -o-linear-gradient(top, #2b2b2b, #2b2b2b);
    background-image: linear-gradient(to bottom, #2b2b2b, #2b2b2b);
}
/* Color del texto de la barra de navegación */

.navbar .nav>li>a {
    color: #FFF;
}
/* Color de las secciones al pasar el ratón */

div#header div.navbar div.navbar-inner ul li.open a.dropdown-toggle,
div#header div.navbar div.navbar-inner ul.top-menu li a.dropdown-toggle:hover,
div#header div.navbar div.navbar-inner ul.top-menu li a.sin-menu:hover,
div#header div.navbar div.navbar-inner ul.top-menu li.dropdown:hover a.dropdown-toggle {
    background-color: #d23030;
}
/**** ACTIVIDAD ****/
/* Color del borde del botón ACTIVIDAD */

div#header div.navbar div.navbar-inner ul.actions li.actividad a.dropdown-toggle {
    background-color: #d23030;
    border: 0px;
}
/* Color de la línea roja de Actividad */

div#header div.navbar div.navbar-inner ul li ul.dropdown-menu {
    border-top: 5px solid #d23030;
}
/* Color del fondo de la lista de Actividad */

.dropdown-menu {
    background-color: #2b2b2b;
}
/* Color texto: Ver más */

.dropdown-menu a {
    color: #FFF;
}
/* Color texto: nickname hace X minutos */

.navbar {
    color: #7a7a7a;
}
/* Color recuadro donde está ubicado el botón ACTIVIDAD */

div#header div.navbar div.navbar-inner ul.actions li.actividad.open,
div#header div.navbar div.navbar-inner ul.actions li.notificaciones.open,
div#header div.navbar div.navbar-inner ul.actions li.usuario.open {
    background-color: #d23030;
}
/* Color texto: Ha comentado la noticia */

ul.dropdown-menu li.item p,
ul.notificaciones li.item p {
    color: #a3a3a3;
}
/* Color texto: título del hilo, noticia, publicación... */

ul.dropdown-menu li.item p span,
ul.notificaciones li.item p span {
    color: #FFF;
}
/* Color rojo de los enlaces de la lista de Actividad */

ul.dropdown-menu li.item a,
ul.notificaciones li.item a {
    color: #d23030;
}
/**** CUERPO PRINCIPAL WEB Y FORO ****/
/* Color principal del fondo del contenido */

div#primary {
    background-color: #2b2b2b;
}
/**** FORO ****/
/* Color del título de los foros */

div.interior div#primary div#post div.header h2 {
    color: #FFF;
}
/* Color del bloque con cantidad de páginas */

div.hilo div#primary div#post div.header span.pagina {
    background-color: #202020;
}
/* Disminuye el número de la cantidad de páginas */

div.hilo div#primary div#post div.header span.pagina strong {
    font-size: 40px;
}
/* Color del rectángulo índice */

span.where-i-am {
    background-color: #d23030;
}
/* Quita un poco de espacio entre el título del foro y el contenido */

div.interior div#primary div#post div.header {
    padding-top: 15px;
    padding-bottom: 10px;
}
/* Quita un poco de espacio entre NUEVO TEMA y temas */

div.foro div#primary div#post div.body div.lista-hilos {
    margin-top: 10px;
}
/* Color del texto de la lista foros */

div.foro div#primary div#post div.body table tr td {
    color: #CCC;
}
/* Le quito el color "zebra" a la lista de hilos */

.table-striped tbody tr:nth-child(odd) td,
.table-striped tbody tr:nth-child(odd) th {
    background-color: transparent;
}

div.foro div#primary div#post div.body table.principal tr td:first-child {
    border-bottom: 1px solid #585858;
}
/* Línea entre cada hilo */

div.foro div#primary div#post div.body table tr td.big {
    padding-top: 15px;
    padding-bottom: 15px;
    border-bottom: 1px solid #585858;
}
/* Línea de la lista de LECTURAS MENSAJES de cada hilo */

div.foro div#primary div#post div.body table tr td.number {
    padding-top: 15px;
    padding-bottom: 15px;
    border-bottom: 1px solid #585858;
}
/* Línea entre últimos mensajes */

div.foro div#primary div#post div.body table tr td.avatar {
    padding-top: 15px;
    padding-bottom: 15px;
    border-bottom: 1px solid #585858;
}
/* Quita la línea entre TEMAS LECTURAS MENSAJES ÚLTIMO MENSAJE y los hilos */

.table th,
.table td {
    border-top: 0px;
}
/* Quita espacio dentro de los hilos */

.ç {
    margin-bottom: 0px;
}
/* Color de los números de las páginas de un hilo */

div.pagination a {
    color: #a1a1a1;
}

div.foro div#primary div.pagination a {
    color: #d0d0d0;
}
/* Color del boton activados y en hover de los números dentro de un hilo */

.pagination a:hover,
.pagination .active a,
.pagination .active span {
    background-color: #d23030;
    color: #FFF;
}
/* Cambio el color de los botones de paginación y quito borde */

.pagination a,
.pagination span {
    background-color: #202020;
    border: 0px;
}
/* Icono de bocadillo entre avatar y comentario */

div.interior div#primary ul#comentarios li b.bocadillo {
    background: transparent url(https://i.imgur.com/KkTXvKy.png) 0 0 no-repeat;
}

a#boton_enviar_compartir,
ul.dropdown-menu li.login button.btn,
ul.dropdown-menu li.registro a.btn {
    color: #FFF;
}

a#boton_enviar_compartir,
ul.dropdown-menu li.login button.btn,
ul.dropdown-menu li.registro a.btn:hover {
    background-color: #202020;
}
/*Botón de acciones para el comentario, como EDITAR, dar likes... */

.btn {
    background-color: #202020;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#151515), to(#151515));
    background-image: -webkit-linear-gradient(top, #151515, #151515);
    background-image: -o-linear-gradient(top, #151515, #151515);
    background-image: linear-gradient(to bottom, #151515, #151515);
    background-image: -moz-linear-gradient(top, #151515, #151515);
    border: 0px;
    text-shadow: 0 0px 0px rgba(0, 0, 0, 0);
    color: #CCC;
}

.btn:hover {
    background-color: #cc0000;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#151515), to(#151515));
    background-image: -webkit-linear-gradient(top, #cc0000, #cc0000);
    background-image: -o-linear-gradient(top, #cc0000, #cc0000);
    background-image: linear-gradient(#cc0000, #cc0000);
    background-image: -moz-linear-gradient(top, #cc0000, #cc0000);
    border: 0px;
    text-shadow: 0 0px 0px rgba(0, 0, 0, 0);
    color: #CCC;
}

.btn.active,
.btn:active {
    background-color: #590606;
}

.btn.active:hover,
.btn:active:hover {
    background-color: #cc0000;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#151515), to(#151515));
    background-image: -webkit-linear-gradient(top, #cc0000, #cc0000);
    background-image: -o-linear-gradient(top, #cc0000, #cc0000);
    background-image: linear-gradient(#cc0000, #cc0000);
    background-image: -moz-linear-gradient(top, #cc0000, #cc0000);
}
/* Color del texto: "Por x autor," bajo el titular del tema */

div.interior div#primary div#post div.header p span.author {
    color: #FFF;
}
/* Color de fecha y hora al lado del autor del hilo */

div.interior div#primary div#post div.header p {
    color: #6d6d6d;
}
/* Color de la numeración de los hilos */

div.foro div#primary div#post div.body table tr td div.paginacion_hilo a {
    border: 1px solid #202020;
    background-color: #202020;
    color: #FFF;
}
/* Color del fondo de los comentarios */

div.interior div#primary ul#comentarios li div.comment-block {
    background-color: #202020;
    border: 0px;
}
/* Color de la fecha del comentario */

div.interior div#primary ul#comentarios li div.comment-block a.date {
    color: #6d6d6d;
}
/* Color del fondo del bloque de Spoiler */

div.hilo div#primary .spoiler {
    border: 0px solid #ccc;
    background-color: #151515;
}
/* Color del fondo de dentro del Spoiler */

div.hilo div#primary .spoilerins {
    background: #151515;
}
/* Botón NUEVO TEMA y ENVIAR */

.btn-primary {
    color: #fff;
    background-color: #cc0000;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#d80000), to(#780202));
    background-image: -webkit-linear-gradient(top, #d80000, #780202);
    background-image: -o-linear-gradient(top, #d80000, #780202);
    background-image: linear-gradient(to bottom, #d80000, #780202);
    background-image: -moz-linear-gradient(top, #d80000, #780202);
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
}
/* Botón NUEVO TEMA y ENVIAR al pasar por encima */

.btn-primary:hover {
    background-color: #cc0000;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#d80000), to(#780202));
    background-image: -webkit-linear-gradient(top, #d80000, #780202);
    background-image: -o-linear-gradient(top, #d80000, #780202);
    background-image: linear-gradient(to bottom, #c00, #c00);
    background-image: -moz-linear-gradient(top, #d80000, #780202);
    background-repeat: repeat-x;
    border-color: #780202 #780202 #800000;
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
}
/* Color de la lista de comandos del comentario a enviar */

div.interior div#primary div.comment-form div.format-buttons {
    background: -webkit-linear-gradient(#202020, #121212) repeat scroll 0 0 transparent;
    background: linear-gradient(#202020, #121212) repeat scroll 0 0 transparent;
    border: 1px solid #676767;
}
/* Color del texto de los comandos Negrita, Cursiva... */

div.interior div#primary div.comment-form div.format-buttons a {
    color: #FFF;
    border-right: 1px solid #676767;
}
/* Color del fondo del texto citado */

div.interior div#primary ul#comentarios li div.comment-text blockquote,
div.interior div#primary ul#comentarios li div.comment-text div.quote {
    background: none repeat scroll 0 0 #151515;
    border: 1px solid #2b2b2b;
    color: #CCC;
}
/* Color texto: Usuarios conectados */

div.foro div#primary h3 {
    color: #FFF;
}
/* Color del fondo de En los últimos 5 minutos */

.label-info,
.badge-info {
    background-color: #d23030;
}
/* Fondo que rodea el comentario a enviar */

div.interior div#primary div.comment-form {
    background-color: #202020;
}
/* Texto "desconecta" debajo del bloque del comentario a enviar */

div.interior div#primary div.comment-form p a {
    color: #d23030;
}
/**** WEB ****/
/* Botones highlight */

ul#highlights-buttons li a {
    background: -webkit-linear-gradient(top, #2b2b2b, #1e1e1e);
    background: linear-gradient(top, #474747, #1e1e1e);
    color: #dbdbdb;
    border-top: 4px solid #151515;
    border-right: 1px solid #333333;
    border-bottom: 1px solid #333333;
}
/**** OJO QUE INTERESA ****/
/* Color texto: OJO QUE INTERESA */

div.lo-mas-popular div.header h3 {
    color: #FFF;
}
/* Color fondo del bloque con destacadoslos tres destacados */

div.lo-mas-popular div.caja {
    background-color: #202020;
}
/* Color del fondo de los destacados y sombra */

div.lo-mas-popular div.caja div {
    background-color: #2b2b2b;
    box-shadow: 0 1px 5px rgba(0, 0, 0, .2);
}
/**** ESAT ****/
/* Cambia el color del fondo y sube la posición del div */

div#primary div#modulos {
    background-color: #202020;
    margin-top: 81px;
}
/**** COLUMNA DEL CONTENIDO PRINCIPAL ****/
/* Recuadros: TODAS - LO IMPRESCINDIBLE*/

#primary div#newslist ul.tipo-comentarios li a.seleccionado,
div#primary div#newslist ul.tipo-notis li a.seleccionado {
    color: #FFF;
    border-top: 2px solid #d4021c;
    border-right: 1px solid #585858;
    border-left: 1px solid #585858;
    background-color: #2b2b2b;
}
/* Texto: LO IMPRESCINDIBLE */

#primary div#newslist ul.tipo-comentarios li a,
div#primary div#newslist ul.tipo-notis li a {
    color: #d0d0d0;
}
/* Línea blanca que separa pestañas TODAS y LO IMPRESCINDIBLE y las consolas */

div#primary div#newslist ul.tipo-comentarios,
div#primary div#newslist ul.tipo-notis {
    border-bottom: 1px solid #585858;
}
/* Texto de consolas debajo de las pestañas TODAS y LO IMPRESCINDIBLE */

div#primary ul.plataformas li a {
    color: #d0d0d0;
}
/* Arreglo para el color de la línea derecha */

div#primary div#newslist ul.plataformas {
    border-right: 1px solid #585858;
}
/* Color de la línea derecha */

div#posts {
    border-right: 1px solid #585858;
}
/* Línea que separa noticias, artículos... */

div.post {
    border-bottom: 1px solid #585858;
}
/* Texto: hace X horas */

div.post div.texto span {
    color: #6f6f6f;
}
/* Quita borde del recuadro de amigos que han comentado */

div.post div.amigos_han_comentado {
    border: 0px
}
/* Color del bloque de amigos que han comentado */

div.post div.amigos_han_comentado p {
    border-top: 0px;
    background-color: #202020;
}
/* Color títulos de artículos, noticias... */

h2 a {
    color: #FFF;
}
/**** TOP DE JUEGOS ****/
/* Bloque: TOP DE JUEGOS */

div.bloque {
    background-color: #2b2b2b;
}
/* Color del texto seleccionado, debajo del texto TOP DE JUEGOS */

div.top-juegos div.cont-filtros ul li a.selected {
    color: #FFF;
}
/* Color de títulos del top */

div.top-juegos ul.lista-juegos li h2 a {
    color: #FFF;
}
/* Color del texto descriptivo de cada juego */

div.top-juegos ul.lista-juegos li div.extendido div.portada ul.notis li a {
    color: #FFF;
}
/***** VÍDEOS *****/
/* Color borde del bloque VÍDEOS */

div.bloque div.caja {
    border-right: 1px solid #585858;
    border-left: 1px solid #585858;
    border-bottom: 1px solid #585858;
}
/* Color "zebra" de la lista de VÍDEOS */

div.videos div.caja ul.lista-videos li.alt {
    background-color: #202020;
}

div.otros-videos h3 {
    color: #CCC;
}
/**** TWEETS ****/
/* Color texto: Tweets */

.timeline-Header-title {
    color: #dbdbdb;
}
/**** LO MÁS COMENTADO ***/
/* Color texto descriptivo de lo más comentado */

div.lo-mas-comentado div ul.popular p a {
    color: #FFF;
}

div#primary div#newslist ul.tipo-comentarios li a,
div#primary div#newslist ul.tipo-notis li a {
    color: #d0d0d0;
}
/* Color texto: comentarios */

div.lo-mas-comentado div ul.foro p,
div.lo-mas-comentado div ul.popular p,
div.lo-mas-comentado div ul.ultimos p {
    color: #6f6f6f;
}
/* Recuadros: POPULAR - ÚLTIMOS - FORO */

div#primary div#newslist ul.tipo-comentarios li a.seleccionado,
div#primary div#newslist ul.tipo-notis li a.seleccionado {
    color: #FFF;
    border-top: 2px solid #d4021c;
    border-right: 1px solid #585858;
    border-left: 1px solid #585858;
    background-color: #2b2b2b;
}
/**** USUARIO DESTACADO ****/
/* Color texto: frase del usuario */

div.usuario-destacado div.caja p {
    color: #dbdbdb;
}
/* Color del fondo del usuario destacado */

div.usuario-destacado div.mini-perfil {
    background-color: #202020;
}
/* Color texto: nombre del usuario */

div.usuario-destacado div.mini-perfil h3 {
    color: #FFF;
}
/* Botón: Cargar más noticias */

.btn-inverse {
    background-color: #181818;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#252525), to(#161616));
    background-image: -webkit-linear-gradient(top, #252525, #161616);
    background-image: -o-linear-gradient(top, #252525, #161616);
    background-image: linear-gradient(to bottom, #252525, #161616);
    background-image: -moz-linear-gradient(top, #252525, #161616);
    background-repeat: repeat-x;
    border-color: #161616 #161616 #000;
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
}
/* Cursor por encima de Cargar más noticias */

.btn-inverse:hover {
    background-color: #181818;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#252525), to(#161616));
    background-image: -webkit-linear-gradient(top, #252525, #161616);
    background-image: -o-linear-gradient(top, #252525, #161616);
    background-image: linear-gradient(to bottom, #252525, #161616);
    background-image: -moz-linear-gradient(top, #252525, #161616);
    background-repeat: repeat-x;
    border-color: #161616 #161616 #000;
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
}
/**** ZONA DE USUARIOS ****/
/* Color fondo de ZONA DE USUARIOS*/

div.zona-usuarios div.caja div.instrucciones {
    background-color: #202020;
}
/* Color texto: PUBLICA Y COMPARTE TUS ARTÍCULOS */

div.zona-usuarios div.caja div.instrucciones h4 {
    color: #FFF;
}
/* Color texto: Publica tus propios artículos desde tu perfil... */

div.zona-usuarios div.caja div.instrucciones p {
    color: #FFF;
}
/* Color fondo de los tres bloques de usuarios */

div.zona-usuarios div.caja ul.zona-usuarios-list li {
    background-color: #202020;
}
/**** PRÓXIMOS LANZAMIENTOS ****/
/* Color borde de los juegos próximos */

div.proximos-lanzamientos div.juego {
    border: 1px solid #585858;
}
/* Color texto: título de los próximos lanzamientos */

div.proximos-lanzamientos div.juego h4 a {
    color: #dbdbdb;
}
/**** NOTICIA, ARTÍCULO... ****/
/* Bloque Número de comentarios debajo de la noticia */

div.interior div#primary div.comments-top {
    color: #FFF;
    background-color: #202020;
    border-top: 1px solid #151515;
    border-bottom: 1px solid #151515;
}
/* Color texto de cita dentro de noticias */

div.body blockquote {
    color: #969696;
}
/* Cambio de color botones Todos - Siguiendo - Seguidores */

.nav-pills>.active>a,
.nav-pills>.active>a:hover {
    color: #fff;
    background-color: #d23030;
}
/* Cambio de color botones Todos - Siguiendo - Seguidores inactivos */

div.interior div#primary div.comments-filtros ul li a:hover {
    color: #fff;
    background-color: #121212;
}
/* Color de fondo Todos - Siguiendo... */

.well {
    background-color: #202020;
}
/* Color borde bloques de Todos, siguiendo... */

div.interior div#primary div.comments-filtros div.well {
    border-color: #151515;
}
/**** SOCIAL ****/
/* Color de texto */
/* Quita fondo blanco del avatar en el perfil */

div.perfil div#primary div.big-image img.avatar {
    background-color: transparent;
}
/* Color del fondo de avatar, nick, logros... */

div.perfil div#primary div.descripcion {
    background-color: #202020;
    border-width: 0px;
}
/* Color de la localización del usuario */

div.perfil div#primary div.descripcion div.left span.localizacion {
    color: #FFF;
}
/* Color del borde de la caja de texto */

div.perfil div#primary div.comunidad div.twitt_principal {
    background-color: #585858;
}
/* Icono bocadillo entre avatar y comentario */

ul#lista-actividad li.publicacion-usuario b.bocadillo {
    background: url(https://i.imgur.com/KkTXvKy.png) 0px 0px no-repeat transparent;
}
/* Color texto: hace X horas al lado del nick del usuario */

ul#lista-actividad li.publicacion-usuario div.actividad-block a.date {
    color: #585858;
}
/* Recuadro alrededor del bloque donde introducir el tweet principal */

div.perfil div#primary div.muro div.twitt_principal {
    background-color: #202020;
}
/* PESTAÑA MURO LOGROS JUEGOS... */

.nav-tabs>.active>a,
.nav-tabs>.active>a:hover {
    background-color: #2b2b2b;
    border: 1px solid #585858;
    border-bottom-color: transparent;
}
/* Color pestañas inactivas al pasar el ratón por encima */

.nav-tabs>li>a:hover {
    background-color: #202020;
    border: 0px;
}
/* Color texto pestaña MURO LOGROS JUEGOS...*/

div.perfil div#primary div.muro ul.nav-tabs li.active a {
    color: #FFF;
}
/* Color línea de debajo de la pestaña MURO LOGROS...*/

.nav-tabs {
    border-bottom: 1px solid #585858;
}
/* Icono MURO */

div.perfil div#primary div.muro ul.nav-tabs li i.icon-home24 {
    background-image: url(https://i.imgur.com/T4uWyVj.png);
