css:
@-moz-document domain("bloodmallet.github.io") {
body {
    background-color: #22232b;
    color: #fff;
}
div.central_aligner_outer {
    background-color: #191a1f;
}
.div-cell {
    border: 1px solid #22242b;
    padding: 3px;
    display: table-cell;
}
.class-table {
    background-color: #191a1f;
}
.highcharts-background {
    fill: #22242b;
    color: white;
}
#fight_style_button, #pruner, #chart_linker {
    color: white;
    border: 1px solid #191a1f;
    text-align: center;
    font-weight: 200;
    background-color: #191a1f;
    padding-top: 7px;
    padding-bottom: 6px;
    padding-left: 12px;
    padding-right: 12px;
    cursor: pointer;
}
#select_language {
    padding-top: 6px;
    padding-bottom: 6px;
    padding-left: 7px;
    padding-right: 7px;
    color: white;
    border: 1px solid #191a1f;
    text-align: center;
    font-weight: 200;
    background-color: #191a1f;
    cursor: pointer;
}
.highcharts-root text {
    color: white;
}
a {
    color: #7b7b7b;
    text-decoration: none;
    transition: background-color .25s ease-in;
}
a:hover {
    background-color: #191a1f;
}
a:focus, a:hover {
    color: #ffffff;
    font-weight: bold;
}
.highcharts-tick, .highcharts-grid path {
    stroke: #22242b;
}
tspan {
    font-size: 11px;
    font-weight: bold;
    color: #ffffff;
    fill: #ffffff;
    background-color: #191a1f;
}
.highcharts-legend-box {
    fill: #22252b;
    stroke: #22252b;
    background-color: #191a1f;
}
path {
    fill: rgb(25, 26, 31);
    stroke: #191a1f;
}
}
