css:
@-moz-document url-prefix("https://www.finn.no")
{

/*Edit 8des 2017*/
body > div.container.bg-ice.pbs{background: #222!important;}
body > div.container.bg-ice.pbs > div {background: #222!important;}
body > div.container.bg-ice.pbm{background: #222!important;}
#finnlet-footer > footer{background: #222!important;}
#finnlet-footer > footer > div{background: #222!important;}
html{background: #222!important;}
/*Edit 8des 2017*/


/*Body, header*/
  body.no-focus-ring
  {
    background: #111 !important;
  }

  .topbar-finnlet-2ktKP.fixed,  a.topbar-item-2t_6H
  {
    background: #222;
    border-bottom-color: #333;
  }

  nav.topbar-nav-21pTS * span
  {
    color: #ddd !important;
    font-weight: bold;
  }

  h2 span
  {
    color: #ddd !important;
  }

/*Lenker i header*/
  a.topbar-item-2t_6H:hover
  {
    background: #444 !important;
  }

  .button-group input:checked+label, a.button.primary, a.icon.primary, button.primary, input.primary, label.button.primary
  {
    border-color: #888;
  }

/*Main div*/
  .bg-ice
  {
    background: #222 !important;
  }

/*Diverse input*/
  div.bd, .inner.r-pal, button, button.unit.primary.pam, a.button.plain-button, select, input, .button.primary.fullwidth, div.extended-profile, textarea
  {
    background: #333 !important;
    color: #ccc !important;
  }

  .market-selector .market-icon use, .secondary-blue, a.secondary-blue
  {
    color: #fff;
  }

  .market-selector.market-icon use:hover
  {
    color: #000 !important;
 /*Må fikses*/;
  }

  .pas:hover > use.licorice, a.fleft.pas svg.blockify:hover > use
  {
    color: #fff !important;
  }

  .licorice, a.licorice
  {
    color: #fff !important;
  }

  .bg-mint
  {
    background-color: #222 !important;
    border-color: #555 !important;
  }

  .inner * a, use.licorice /*Hjerte*/
  {
    color: #bbb !important;
    font-weight: bold;
  }

/*Antall rom f.eks. - valgt -virker ikke enda*/
  .button-group input:checked+label, a.button.primary, a.icon.primary, button.primary, input.primary, label.button.primary
  {
    background: #555 !important;
    color: #222 !important;
  }

/*Antall rom f.eks. - ikke valgt*/
  .button-group label, a.button.utility, a.icon.utility, button.utility, input.utility, label.button.utility, div.inner
  {
    background: #333 !important;
    color: #555 !important;
  }

  a.button.plain-button span
  {
    color: #ccc !important;
  }

  button:hover, a.button.plain-button:hover, .button.fullwidth:hover, button.unit.primary.pam:hover
  {
    background: #555 !important;
    color: #eee !important;
  }

  a.button.plain-button:hover > span
  {
    color: #eee !important;
  }

  polyline, path
  {
    stroke: #bbb;
  }

  .pvl.phm * path
  {
    fill: #bbb;
  }

  span.truncate.gallery-image-caption-text
  {
    color: #eee !important;
    background: rgba(0, 0, 0, 0.63);
  }

  span.mrs.objectstatus
  {
    background: #111;
    color: #ccc;
  }

/*Checkbox*/
  .fancyinput [type=radio]:checked+label:before, .fancyinput [type=radio]:not(:checked)+label:before, .fancyinput [type=checkbox]:checked+label:before, .fancyinput [type=checkbox]:not(:checked)+label:before
  {
    background: #222;
    border-color: #555;
  }

  li, span, label
  {
    color: #777 !important;
  }

  .inner.r-pal
  {
    border-bottom: 2px solid #444;
  }

}
