css:
@-moz-document regexp("http:\\/\\/(www\\.)?snowheads.com\\/ski-forum\\/(viewtopic.php(\\?.*)?)") {
/* CSS for viewtopic.php */
body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > div:nth-child(1) {
    /* The top login box */
    margin: inherit !important;
}

body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > div:nth-child(1) {
 
    margin: inherit !important;
}

#table3 .message_cell {
    max-width: 90vw;
}

.forumline td {
    display: block;
    width: 100%;
}
.forumline .row1,
.forumline .row2 {
    height: 100%;
}
.forumline .poster_info {
    display: flex !important;
    padding-bottom: 10px;
    padding-top: 5px;
    margin-left: 10px;
}
.forumline .poster_info .plusclick {
    margin-right: 5px;
}
.forumline .poster_info span {
    display: flex;
}
.forumline .poster_info font {
    display: flex;
}
.forumline .poster_info .report_conditions:not(.post_date_portrait) {
    display: none;
}
.forumline .poster_info .report_conditionsRed {
    /* e.g. freerider, user, super-snowhead */
    
    display: none;
}
.forumline .poster_info .report_conditions.post_date_portrait {
    display: flex;
    
	/* remove bold HH:MM */
    font-weight: normal !important;
    padding-right: 5px;
}
.forumline .poster_info .report_conditions.post_date_portrait br {
    display: none;
}

.forumline .post_row #table3 tbody tr td:not(.quote) {
    border-left: none !important;
    position: inherit !important;
    display: block;
    width: 100% !important;
    max-width: 100% !important;
}

.forumline td.row1,
.forumline td.row2 {
    background-color: #F5F5FF;
}
.forumline tbody tr:nth-child(2n+2) {
    height: 5px;
}
#table2 .forumline .row1,
#table2 .forumline .row2 {
    /* Aligns the poster_info and the post */
    
    border: none !important;
}
.post_info .plusclick {
    display: flex;
}
.post_date_portrait {
    background-color: inherit;
}
.postbody {
    font-size: 0.8em;
}
.post_info {
    position: inherit !important;
    display: flex !important;
    flex-direction: row-reverse;
    vertical-align: inherit !important;
    margin-right: 65px !important;
}
.post_info div {
    float: inherit !important;
    position: inherit !important;
    margin-top: 9px;
}
.forumline #post_row.post_row table#table3 tbody tr td a img {
    width: 21px !important;
    padding: inherit !important;
}
.forumline tbody tr td.row1 form {
    background: none !important;
    width: auto !important;
}

table {
    table-layout: fixed;
}
.quote {
    overflow: hidden;
}
}

@-moz-document regexp("http:\\/\\/(www\\.)?snowheads.com\\/ski-forum\\/(index.php(\\?.*)?)?") {
/* CSS for ski-forum/index.php */
body > div:nth-last-of-type(3) table table tr:first-child {
	display: none;
}

body > div:nth-last-of-type(3) table table tr td:nth-child(n+3) {
	display: none;
}

body > div:nth-last-of-type(3) table table tr td {
    border-radius: 0 !important;
}

body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > div:nth-child(1) {
    /* The top login box */
    margin: inherit !important;
}

.ycattitle {
    display: flex;
    justify-content: center;
}

table .forum_block {
    border: 0;
}

table .forum_block:hover {
    background-color: inherit;
    color: inherit;
    border: 0;
    padding: 3px 3px 3px 10px;
}
}

@-moz-document regexp("http:\\/\\/(www\\.)?snowheads.com\\/ski-forum\\/((viewforum.php|snow_reports.php)(\\?.*)?)?") {
/* CSS for viewforum.php and snow_reports.php */
body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > div:nth-child(1) {
    /* The top login box */
    margin: inherit !important;
}

body > div:nth-last-of-type(3) .forumline {
    display: flex;
    flex-direction: column;
    padding: 0 5px;
}

body > div:nth-last-of-type(3) .forumline tr {
    display: flex;
    flex-wrap: wrap;
    padding-top: 5px;
}

body > div:nth-last-of-type(3) .forumline .gensmall {
    display: none;
}

body > div:nth-last-of-type(3) .forumline tr .row1,
body > div:nth-last-of-type(3) .forumline tr .row2,
body > div:nth-last-of-type(3) .forumline tr .row3Right {
    background-color: #F5F5FF;
    display: flex;
    flex-grow: 1;
}

body > div:nth-last-of-type(3) .forumline tr .row2 {
    display: flex;
    justify-content: flex-end;
    order: 4;
    flex-grow: 1;
}

body > div:nth-last-of-type(3) .forumline tr:first-child th:nth-child(n + 2) {
    display: none;
}

body > div:nth-last-of-type(3) .forumline th.thCornerL {
    border-width: 0;
}

body > div:nth-last-of-type(3) .forumline .row2 .postdetails {
    /* View count should not be visible */
    color: #F5F5FF;
    font-size: 0;
}

body > div:nth-last-of-type(3) .forumline .row2 .postdetails b {
    /* But post count should be visible! */
    color: #7777AA;
    display: flex;
    font-size: 9px;
}

body > div:nth-last-of-type(3) .forumline .row3Right .postdetails {
    color: #F5F5FF;
    font-size: 0;
}
body > div:nth-last-of-type(3) .forumline .row3Right .postdetails div:first-child {
	/* Post Creator */
    display: none !important;
}
body > div:nth-last-of-type(3) .forumline .row3Right .postdetails div {
    color: #7777AA;
    font-size: 9px;
}

body > div:nth-last-of-type(3) .forumline .row3Right .postdetails div:nth-child(2)::after {
	/* Last poster */
    font-size: 9px;
    color: #7777AA;
    content: ",";
}   

body > div:nth-last-of-type(3) .forumline .row2 .postdetails b::after {
    display: flex;
    margin-left: 5px;
    width: 15px;
    height: 15px;
    content: "";
	background-image: url("data:image/svg+xml;charset=utf8,%3C?xml version='1.0' encoding='UTF-8'?%3E%3C!DOCTYPE svg PUBLIC '-//W3C//DTD SVG 1.1//EN' 'http://www.w3.org/Graphics/SVG/1.1/DTD/svg11.dtd'%3E%3Csvg xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' version='1.1' width='15' height='15' viewBox='0 0 24 24'%3E%3Cpath d='M20,2H4A2,2 0 0,0 2,4V22L6,18H20A2,2 0 0,0 22,16V4A2,2 0 0,0 20,2M6,9H18V11H6M14,14H6V12H14M18,8H6V6H18' /%3E%3C/svg%3E");

}

body > div:nth-last-of-type(3) .nav {
    display: flex;
    flex-direction: column;
}

body > div:nth-last-of-type(3) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(2) > span:nth-child(1) {
    /* Page 1, 2, 3 at the bottom */
    flex-direction: row;
}

body > div:nth-last-of-type(3) .forumline tr:nth-child(n + 2) td:first-child {
    display: none;
}
body > div:nth-last-of-type(3) table:nth-child(3) tr:nth-child(2) td:first-child {
    display: none;
}
}

@-moz-document regexp("http:\\/\\/(www\\.)?snowheads.com\\/(mx\\/index.php)?((\\?.*)?)?") {
/* CSS for index.php and mx/index.php */
body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > div:nth-child(1) {
    /* The top login box */
    margin: inherit !important;
}

body > div:nth-child(4) > div:nth-child(1) {
    padding: 0 !important;
}

body > div:nth-child(4) table tbody tr td {
    display: flex;
    flex-direction: column;
    width: inherit !important;
}

.forumline {
    width: 100%;
    padding-top: 10px;
    background-color: inherit;
}

body > div:nth-child(4) > div:nth-child(2) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(2) {
    /* Ski Club 2.0 */
    border: none;
}

body > div:nth-child(4) > div:nth-child(2) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(2) td {
    /* Ski Club 2.0 */
    border: none;
}

table.forumline:nth-child(3) {
    /* Who is Online */
    order: 3;
}

table.forumline:nth-child(6) {
    /* 2nd Hand */
    order: 2;
}
table.forumline:nth-child(6) > tbody:nth-child(1) > tr:nth-child(n+11),
body > div:nth-child(4) > div:nth-child(2) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(n+16),
body > div:nth-child(4) > div:nth-child(2) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(n+16){
    /* only show 10 2nd hand links, hide the rest */
    /* only show 15 Recent posts, hide the rest */
    display: none;
}
.forumline .row1,
.forumline .row2 {
    background-color: #F5F5FF;
    margin-top: 5px;
}

.forumline .genmed {
    border: none !important;
}

.forumline .btnhov,
.forumline .btnunhov {
    background-color: #F5F5FF;
    color: inherit;
    margin-top: 5px;
}

.genmed img {
    margin-right: 5px;
}

.thHead .cattitle {
    color: #EEDD00;
    font-weight: bold;
}
body > div:nth-child(4) > div:nth-child(2) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) {
    /* Recent posts */
    order: 1;
}

body > div:nth-child(4) > div:nth-child(2) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) {
    /* About... */
    margin-top: 10px;
}
}
