css:
@-moz-document url-prefix("https://vk.com/") {
:root {
    --layout-width: /*[[custom-width]]*/px !important;
}

#page_body {
    width: calc(var(--layout-width) - 165px) !important;
}

body.new_header_design {
    --left-menu-row-height: 45px !important;
}

.side_bar_inner li a {
    transition: background 0.2s;
}

li#l_combo {
    display: none;
}
#ads_left, ._ads_promoted_post {
    display: none !important;
}
.im-chat-input .im-chat-input--textarea {
    width: calc(var(--layout-width) - 600px);
}
#feed_story_-195465376id2697ff6e66f32dde82f966e6c7459628d970577a {
    display: none;
}
.videocat_row {
    width: 100%;
}
[dir=ltr] .video_items_list, .videocat_grid_wrap {
    display: grid;
    grid-template-columns: repeat(auto-fill, 244px);
    grid-column-gap: 40px;
    width: 100%;
}
}

@-moz-document url-prefix("https://vk.com/app7362610_150535238") {
#app_7362610_container {
    margin: 0 !important;
}
}
