@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.blockland.us") {
/* Normal, standard links. */
body {
    background-image: url("https://imgur.com/N12klLo.gif") !important;
    background-attachment: fixed !important;
}
body, th {
	color: #000 !important;
    font-size: small;
}
td.tborder {
	color: #000 !important;
}
a:link, a:visited {
    color: #566780 !important;
}
a:hover {
    color: #9fbfd3 !important;
    text-decoration: underline !important;
}
.tborder {
	background-color: white !important;
}
 
/* Navigation links - for the link tree. */
.nav, .nav:link, .nav:visited
{
    color: #fff !important;
}
a.nav:hover
{
    color: #bcdff4 !important;
    text-decoration: underline;
}
 
/* By default (td, body..) use Verdana in black. */
 
html body table tbody tr td center div[style="margin-top:10px; background-color:#AFC6DB; border:solid 10px #AFC6DB;"] {
    display: none;
}
/* Alternating backgrounds... */
 
.windowbg {
    background-color: rgba(213, 228, 237, 1) !important;
    color: #000 !important;
}
td.windowbg2{
	background: rgba(255,255,255,1) !important;
    color: #000 !important;
}
/*.bordercolor {
	background-color: rgba(0,0,0,0) !important;
    border-color: black !important;
}*/
/* Titles - such as table headers. */
 
.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg a:link,
.titlebg a:visited,
.titlebg2,
tr.titlebg2 th,
tr.titlebg2 td,
.titlebg2 a:link,
.titlebg2 a:visited {
    color: #ffffff !important;
    background-color: #9fbfd3 !important;
}
.tborder {
    border: 1px solid #000;
}
.hrcolor {
    height: 1px;
    border: 0;
    color: #566780;
    background-color: #566780;
}
/* The category headers, page indexes, and such things. */
 
.catbg3 {
    font-weight: bold;
    background-color: #4c73ab;
    color: #FFF;
}
.catbg {
    background-color: #4c73ab !important;
    background-image: url('https://i.imgur.com/RUapMUd.gif') !important;
}
.catbg a:visited,
.catbg a:link {
    color: #FFF !important;
}
.catbg a:hover {
    color: #e3f2f1 !important;
}
body>table.tborder>tbody>tr,
body>table:not(.tborder)>tbody>tr,
body>table:not(.tborder)>tbody>tr>td {
    background-color: transparent !important;
}
.logoLink {
    width: 0 !important;
    height: 50px !important;
    padding-right: 400px !important;
    background-image: url("https://i.imgur.com/TOw7iNY.png") !important;
}
.quote {
    background-color: #eaf4f9 !important;
}
input,
textarea {
    background-color: #eaf4f9 !important;
}
select {
    background-color: #eaf4f9 !important;
}
input[type="submit"] {
    background-color: #d5e4ed !important;
}
