css:
@-moz-document url-prefix("http://onepiece-tube.com/") {
/* Main Content Cleanup */
div.module:nth-child(1) > div:nth-child(1) > div:nth-child(1),
div.module:nth-child(2) > div:nth-child(1) > div:nth-child(1),
div.main > span.article_separator,
div.main > p[align="right"],
div.main > table[height="20"],
div.main > img[src="/templates/Grafiken/new_voting.png"],
div.box > table[width="737"]:last-child td:first-child,
#contenttb > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1),
#contenttb > span.article_separator,
#mainlevel-nav li img,
#logo > img,
table.amz,
tr > td > table[height="20"],
tr > td > table[height="50"],
tr > td > table[height="60"],
td[valign="top"] span.article_separator,
#kj28qzmc29w {
    display: none; }
#contenttb {
    padding: 15px 15px 0 15px; }
td > p[align="right"] {
    margin: 0 0 13px 0; }

/* Navbar */
div#topmenu {
    text-align: center;
    background-image: none;
    background-color: #914849;
    padding: 0; }

/* Header */
#maincontainer {
    background: none; }
#topcontainer,
#imglogo,
#logo {
    height: 50px; }
#imglogo {
    background-image: url("https://i.imgur.com/SJqBCFn.png");
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center top; }

/* Other */
#body {
    background: #583839; }
div.box > table[width="737"]:last-child td:last-child p {
    text-align: center; }
}
