css:
@-moz-document domain(www.realmeye.com){
	
	a:focus, a:hover {
		color: #5cc5f9!important;
	}
	
	.alternatives-table td, .alternatives-table th, .stats-table td {
		color: #aaa!important;
	}
	
	aside.quote .title {
		border-left: 5px solid #777!important;
		background-color: #557!important;
		color: #eee!important;
	}
	
	.badge-card {
		background-color: #333!important;
		box-shadow: 1px 1px 3px #aaa7!important;
	}
	
	.badge-card .badge-contents .badge-icon {
		background-color: #444!important;
	}
	
	.badge-card .badge-contents .badge-info {
		color: #bbb!important;
	}
	
	.badge-info-item h3 a {
		color: #0bf!important;
	}
	
	blockquote {
		border-color: #555!important;
		background-color: #28283f!important;
		color: #fff!important;
	}
	
	body {
		color: #ddd!important;
	}
	
	body input[type="text"], body input[type="password"], body input[type="datetime"], body input[type="datetime-local"], body input[type="date"], body input[type="month"], body input[type="time"], body input[type="week"], body input[type="number"], body input[type="email"], body input[type="url"], body input[type="search"], body input[type="tel"], body input[type="color"] {
		color: #888!important;
	}
	
	body input[type="text"], body input[type="password"], body input[type="datetime"], body input[type="datetime-local"], body input[type="date"], body input[type="month"], body input[type="time"], body input[type="week"], body input[type="number"], body input[type="email"], body input[type="url"], body input[type="search"], body input[type="tel"], body input[type="color"] {
		background-color: #000!important;
		border: 1px solid #777!important;
		box-shadow: inset 0 1px 1px #bbb8!important;
	}
	
	body input[type="text"], body input[type="password"], body input[type="datetime"], body input[type="datetime-local"], body input[type="date"], body input[type="month"], body input[type="time"], body input[type="week"], body input[type="number"], body input[type="email"], body input[type="url"], body input[type="search"], body input[type="tel"], body input[type="color"] {
		color: #aaa!important;
	}
	
	body textarea {
		background-color: #333!important;
	}
	
	.brand {
		text-shadow: 0 0 0.2em #fff!important;
	}
	
	.bonus-table th {
		background-color:#222!important;
	}
	
	.bonus-table tr {
		color:#ddd!important;
	}
	
	.btn {
		border: 1px solid transparent!important;
	}
	
	.d-editor-input {
		color: #ddd!important;
	}
	
	.d-editor-preview {
		color: #bbb!important;
	}
	
	.btn-default {
		color: #ddd!important;
		background-image: linear-gradient(to bottom,#888 0,#222 100%)!important;
		text-shadow: 0 1px 0 #000!important;
		border-color: #333!important;
		background-color: #333!important;
	}
	
	.badge-wrapper.bullet span.badge-category {
		color: #ccc !important;
	}
	
	.cookie-consent-alert {
		background: #444!important;
		border-top: 1px solid #777!important;
	}
	
	.category-list tbody .category h3 a[href] {
		color: #ddd!important;
	}
	
	.category-list tbody tr {
		border-bottom: 1px solid #444!important;
	}
	
	.category-list tbody tr:first-of-type {
		border-top: 3px solid #444!important;
	}
	
	.d-editor {
		border: 1px solid #444!important;
	}
	
	.d-editor-container {
		background-color: #222!important;
	}
	
	.d-header {
		background-color: #333!important;
	}
	
	.d-header .title {
		border-radius: 20px!important;
		box-shadow: 0 0 6px white!important;
		margin-right: 8px!important;
	}
	
	.diff-smaller {
		color:#7070ff!important;
	}
	
	div.ac-wrap {
		background-color: #000!important;
		border: 1px solid #888!important;
	}
	
	.dropdown-menu {
		background-color: #555!important; 
	}
	
	.dropdown-menu > li > a {
		color: #ddd!important;
	}
	
	.dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {
		background-image: linear-gradient(to bottom,#888 0,#666 100%)!important;
		background-color: #444!important;
	}
	
	.ember-view .btn {
		color: #eee!important;
		background-color: #333!important;
		border: 1px solid #222!important;
	}
	
	.ember-view .btn:hover {
		color: #111!important;
		background-color: #ccc!important;
		border: 1px solid #888!important;
	}
	
	.extra-info-wrapper .topic-link {
		color: #999!important;
	}
	
	.form-control {
		background-color: #222!important;
		border: 1px solid #444!important;
	}
	
	.form-control, output {
		color: #ccc!important;
	}
	
	.form-control::-moz-placeholder {
		color: #ddd!important;
		background-color: #222!important;
	}
	
	.form-horizontal .control-label {
		color: #ededed!important;
	}
	
	.guild-search input, .row.player-search input {
		color: #ddd!important;
		background-color: #222!important;
	}
	
	hr {
		border-top: 1px solid #e8e8e8!important;
	}
	
	html {
		color: #0e0!important;
		background-color: #080808!important;
	}
	
	img[title="Undead Lair"], img[title="Haunted Cemetery"], img[title="Snake Pit"], img[title="Mad Lab"], img[title="Parasite Chambers"],  img[title="Halloween Haunted Cemetery"]{
		border: 1px solid #333!important;
	}
	
	.img-thumbnail, body {
		background-color: #000!important;
	}

	.item-buying, .item-selling {
		background-color: #333!important;
		border: 1px solid #222!important;
	}
	
	.current-offers .item-wrapper .item {
		width: 49px!important;
		height: 44px!important;
		background-color: #00000088!important;
		margin: -3px -5px -1px!important;
	}
	
	.item-buying {
		color: #7070ff!important;
	}
	
	.item-selling {
		color: #a44!important;
	}
	
	.item-wrapper {
		 height: 42px!important;
	}
	
	.current-offers .item-wrapper {
		border: 1px solid #444!important;
		box-shadow: 2px 2px 2px #333!important;
	}
	
	@keyframes background-fade-highlight {
		0% {
			background-color:#003e5b!important;
		}
		100% {
			background-color:transparent!important;
		}
	}
	
	.list-controls .category-dropdown-menu {
		background-color: #333!important;
	}
	
	.list-controls .category-dropdown-menu .home {
		color: #ddd!important;
	}
	
	.menu-panel {
		background-color: #222!important;
		box-shadow: 0 2px 2px #e8e8e888!important;
	}
	
	.message-body, .preview {
		background-color: #222!important;
		border: 1px solid #444!important;
	}
	
	.modal-content {
		background-color: #111!important;
	}
	
	.modal-header {
		border-bottom: 1px solid #444!important;
	}
	
	.modal-footer {
		border-top: 1px solid #444!important;
	}
	
	.motto {
		color: #bbb!important;
	}
	
	.names span a {
		color: #c8c8c8!important;
	}
	
	.nav > li > a:focus, .nav > li > a:hover {
		text-decoration: none!important;
		background-color: #eee!important;
	}
	
	.nav-pills > li > a {
		color: inherit!important;
	}
	
	.nav-pills > li > a:hover {
		color: #4094db!important;
		background-color: #333!important;
	}
	
	.nav-pills > li.active > a, .nav-pills > li > a.active {
		color: #fff!important;
	}
	
	.navbar-default {
		background-image: linear-gradient(to bottom,#4a4a4a 0,#252525 100%)!important;
		border-color: #333!important;
	}
	
	.navbar-default .navbar-nav > li > a, .navbar-default .navbar-text {
		color: #ccc!important;
	}
	
	.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .open > a {
		background-image: linear-gradient(to bottom,#333 0,#444 100%)!important;
	}
	
	.navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > li > a:hover {
		color: #46aaff!important;
		background-color: transparent!important;
	}
	
	.panel {
		background-color: #333!important;
	}
	
	.panel-default {
		border-color: #555!important;
	}
	
	.panel-default > .panel-heading {
		color: #ddd!important;
		background-image: linear-gradient(to bottom,#555 0,#444 100%)!important;
		border-color: #333!important;
	}
	
	.panel-footer {
		background-color: #222!important;
		border-top: none!important;
	}
	
	.popover {
		background-color: #222!important;
		border:1px solid #555!important;
	}
	
	.popover.bottom>.arrow {
		 border-bottom-color:#f555!important;
	}
	
	.popover.bottom>.arrow:after {
		 border-bottom-color:#f555!important;
	}
	
	.popover.left>.arrow {
		 border-left-color:#f555!important;
	}
	
	.popover.left>.arrow:after {
		 border-left-color:#f555!important;
	}
	
	.popover.right>.arrow {
		 border-right-color:#555!important;
	}
	
	.popover.right>.arrow:after {
		 border-right-color:#555!important;
	}
	
	.popover.top>.arrow {
		 border-top-color:#555!important;
	}
	
	.popover.top>.arrow:after {
		border-top-color:#555!important;
	}
	
	.popover-title {
		background-color: #444!important;
		border-bottom:1px solid #555!important;
	}
	
	.buy {
		color: #7070ff!important;
	}
	
	#reply-control {
		background-color: #111!important;
		border-top: 2px solid #222!important;
	}
	
	#reply-control .d-editor-button-bar {
		background-color: #222!important;
		border-bottom: 2px solid #444!important;
	}
	
	#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview {
		background-color: #111!important;
	}
	
	.select2-container {
		border: 1px solid #888!important;
	}
	
	.select2-container a.select2-choice {
		background: #444!important;
		border-color: #eee!important;
		color: #ddd!important;
	}
	
	.select2-drop {
		color: #ddd!important;
		background: #444!important;
	}
	
	td.above-avg {
		color: green!important;
	}
	
	td.below-avg {
		color: red!important;
	}
	
	td.to-max {
		color: #88c!important;
	}
	
	.tt-dropdown-menu {
		background-color: #444!important;
		color: #bbb!important
	}
	
	.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
		border-top: 1px solid #666!important;
	}
	
	.table > thead > tr > th {
		background-color: #555 !important;
		border-bottom: 2px solid #666!important;
	}
	
	.table td,.table th {
		background-color:#222!important;
	}
	
	table.character-dyes td {
		 background-color:#222!important;
	}
	
	.table-striped > tbody > tr:nth-of-type(2n+1) {
		background-color: #333!important;
	}
	
	.topic-list a.title {
		color: inherit!important;
	}
	
	.topic-list > tbody > tr:nth-child(2n):not(.highlighted) {
		background-color: #333!important;
	}
	
	.topic-list > tbody > tr {
		border-bottom: 1px solid #ccc!important;
	}
	
	.topic-list td {
		color: #ddd!important;
	}
	
	.topic-map {
		background-color: #222!important;
		border-color: #333!important;
	}
	
	.topic-map .avatars, .topic-map .links, .topic-map .information {
		color: #aaa!important;
	}
	
	.topic-map .buttons .btn {
		background-color: #282828!important;
	}
	
	.topic-map .buttons .btn:hover {
		color: #eee!important;
		background-color: #444!important;
	}
	
	.topic-map h3 {
			color: #bbb!important;
	}
	
	.topic-map .map .number, .topic-map .map i {
		color: #aaa!important;
	}
	
	.topic-map section {
		border-top: 1px solid #333!important;
	}
	
	#topic-title h1 a {
		color: #eee!important;
	}
	
	#user-card {
		color: #ccc!important;
	}
	
	#user-card .card-content {
		background: rgba(25,25,25,0.9)!important;
	}
	
	#user-card h1 a {
		color: #eee!important;
	}
	
	#user-card h3 {
		color: #888!important;
	}
	
	#user-card .bio a, #user-card .location-and-website .website-name a {
		color: #09f!important;
	}
	
	#user-card .bio a, #user-card .location-and-website .website-name a:hover {
		color: #9df!important;
	}
	
	#user-card .metadata h3 .desc, #user-card .metadata h3 a {
		color: #aaa!important;
	}
	
	#user-card.show-badges .badge-section .user-badge, #user-card.show-badges .badge-section .more-user-badges {
		background: #555!important;
		border: 1px solid #282828!important;
		color: #d8d8d8!important;
	}
	
	.user-main .about.collapsed-info .details {
		background: rgba(50,50,50,0.85)!important;
	}
	
	.user-main .nav-stacked li > a.active {
		color: #444!important;
	}
	
	.user-main .user-content {
		background-color: #282828!important;
	}
	
	.user-main .user-stream .item {
		background-color: #333!important;
		border-color: #666!important;
	}
	
	.user-main .user-stream .notification p span {
		color: #ddd!important;
	}
	
	.user-menu .notifications li span {
		color: #ddd!important;
	}
	
	.user-menu .notifications li {
		background-color: #884!important;
	}
	
	.user-menu .notifications .read {
		background-color: #222!important;
	}
	
	.user-preferences .static {
		color: #aaa!important;
	}
	
	.well {
		background-image: linear-gradient(to bottom,#505050 0,#424242 100%)!important;
		border-color: #444!important;
	}
	

}
