css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://pear.php.net") {

body {font-family: Arial !important}
h1, h2 {color: black !important; background: white !important; border: none !important}
h1 {font-size: 1.8em !important; margin: .5em 0 .5em !important}
input {font-family: Arial !important; font-size: 1em !important}

a {text-decoration: underline !important}
a:link {color:blue !important; background: white !important;}
a:visited {color:purple !important; background: white !important;}
a:active {color:red !important; background: white !important;}

#user {position: absolute !important; right: 0; top: 0; background: white !important; padding: 1em !important; font-size: 1em !important}

#search {position: static !important; float: none !important; text-align: center !important; font-size: 1em !important; background: #449922 !important; padding:1em 2em !important}
#search input[type=text] {width: 25em !important}

#header {position: absolute !important; top: 0 !important; left: 0 !important; background: white !important}
#header img {margin: 0 !important; background: #449922; padding: 0 2em !important}

#menubar {margin: .5em 0 !important}

ul#menu a {color:white !important; background: black !important; text-decoration:none !important}
ul#menu li.current a {color:black !important; background: white !important;}
ul#menu a:hover {color:black !important; background: white !important;}

ul#submenu {border: none !important}

#footer {margin-top: 400px !important}

/** package bug statistics */
.bug_header {color: black !important; background: white !important}
.bug_head {color: black !important; background: #FFFFCC !important}
}
