css:
@-moz-document regexp("(https?:\/\/)([a-zA-Z\d-._]+)(\.js\.org)\/?(?!\.js\.org).*") {
#book-search-input {
  background: rgb(32, 32, 32);
}
#book-search-input input:placeholder {
  color: #d3d3d3;
}
#book-search-input input,
#book-search-input input:focus {
  color: whitesmoke;
}
body,
mark,
a,
h1,
h2,
h3,
h4,
h5,
h6,
p,
li,
span,
.book-body,
.button,
.book-header,
.book-header h1,
.book-summary ul.summary li a,
.book-summary ul.summary li span .navigation,
code,
.markdown-section code {
  color: whitesmoke;
}
.book-header .btn:link,
.book-header .btn:active,
.book-header .btn:visited {
  color: #bfbfbf;
}
.book-header .btn:hover {
  color: white;
}
body,
.container {
  background-color: #222 !important;
  background-image: url(data:image/png;base64,) !important;
  background-clip: border-box !important;
  background-origin: padding-box !important;
  background-attachment: scroll !important;
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important;
}
div,
p,
h1,
h2,
h3,
h4,
h5,
h6,
p,
li,
header,
footer,
main,
.header,
.footer,
#header,
#footer,
.page-inner,
.book-body,
.book-summary,
div[class*="card"],
.project-table {
  background-color: rgb(32, 32, 32);
}
.markdown-section code,
.markdown-section pre {
  background: rgb(28, 28, 28);
}
.book-summary ul.summary>li>a,
li.chapter>.articles>li>a {
  color: whitesmoke;
}
/* nav */

a.navigation.navigation-prev,
a.navigation.navigation-next {
  background: rgb(32, 32, 32);
}
a.navigation i[class*="fa-angle"] {
  color: #686868;
}
a.navigation i[class*="fa-angle"]:hover {
  color: whitesmoke;
}
/* nav */
}
