css:
@-moz-document domain("signin.aws.amazon.com") {
#container > h1.background {
    height: 25px;
}

form#saml_form > p {
    font-weight: bold;
    font-size: 18px !important;
}

.saml-account-name {
    font-size: 14px;
    font-weight: bold;
}

.saml-role-description {
    font-size: 14px;
    font-weight: normal;
}

.saml-role {
    display: inline-block;
    width: 20em;
    margin-left: 1em;
    margin-right: 0;
    margin-top: 1em;
    margin-bottom: 0;
}

.saml-account {
    padding-left: 0;
    margin-bottom: 25px;
}

#input_signin_button {
    padding-top: 0;
}
}

@-moz-document domain("console.aws.amazon.com") {
#awsgnav .service-link .service-label {
    font-size: 80%;
}

#awsgnav .nav-menu .nav-elt-label, .nav-elt,
#awsgnav #nav-resourceGroupsMenu .nav-elt-label,
#awsgnav #nav-servicesMenu .nav-elt-label {
    font-size: 80%;
    padding: 0 8px 0 0 !important;
}

#awsgnav #nav-servicesMenu,
#awsgnav #nav-resourceGroupsMenu {
    padding: 12px 12px 12px 0;
}

span.service-icon {
    display: none;
}

#awsgnav #nav-shortcutBar .service-link {
    margin-left: 0;
}

#awsgnav #nav-shortcutBar a {
    padding-left: 0;
    padding-right: 0;
}
}
