body {
    font-size: 90%;
    color: #212529;
}
dt {
    line-height: 1.8em;
}
div.navheader,
div.navfooter,
div.NAVHEADER,
div.NAVFOOTER {
    background-color: #f8f9fa;
}
table th,
table td {
    padding: 0.5em 1em !important;
}
div.navheader table,
div.navfooter table,
div.NAVHEADER table,
div.NAVFOOTER table {
    margin-left: unset;
}
.titlepage h2.title,
.titlepage h1.title {
    font-size: 2em !important;
}
hr {
    border-style: none;
    height: 1px;
    background-color: #cfcfcf;
}
h1 {
    font-size: 2em;
}
h2 {
    font-size: 1.8em !important;
}
h3 {
    font-size: 1.6em;
}
h4 {
    font-size: 1.4em;
}
h5 {
    font-size: 1.2em;
}
h6 {
    font-size: 1.1em;
}
.caution,
.warning,
.CAUTION,
.WARNING {
    color: #856404;
    background-color: #fff8e2 !important;
    border-color: #ffeeba !important;
}
.note,
.tip,
.NOTE, 
.TIP {
    color: #0c5460;
    background-color: #e5f4f7 !important;
    border-color: #bee5eb !important;
}
.caution,
.warning,
.note,
.tip {
    max-width: 800px;
    width: unset;
}
blockquote.CAUTION,
blockquote.WARNING,
blockquote.NOTE,
blockquote.TIP {
    width: unset;
}
.caution h3,
.warning h3,
.note h3,
.tip h3 {
    margin-top: 0.5em;
    margin-bottom: 1em;
}
body {
    font-family: 'Roboto', sans-serif;
}
pre,
code,
tt,
kbd {
    font-family: 'Fira Mono', monospace;
    font-size: 1em !important;
    color: #bd4147 !important;
    background-color: #f8f9fa !important;
    border-color: #e2e0e0 !important;
    border-radius: .25em;
    padding: 0.2em 0.4em;
}

pre * {
    background-color: unset !important;
    padding: unset;
}

pre {
    line-height: 1.5em;
}

pre,
.note,
.tip,
.caution,
.warning,
.NOTE,
.TIP,
.CAUTION,
.WARNING,
.table table,
.informaltable table {
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
    border-radius: 4px !important;
}
div.table table,
div.informaltable table {
    background-color: #e5f4f7 !important;
}
@media only screen and (min-width: 900px) {
    #docContainer {
        width: 75%;
    }
}
  
