css:
@-moz-document url-prefix(https://www.youtube.com) {

	#royt {
		font-size: /*[[comSize]]*/;
	}
	a.royt_author {
		font-size: /*[[userSize]]*/;
	}

	/* This color is meant for the YouTube dark theme. If you use the light theme, comment out the color line.  */
	
  div.info > a.title,
	a.royt_author {
		color: /*[[linkColor]]*/ !important;
	}
  
}
