css:
@-moz-document url-prefix("http://www.baidu.com/")
{
 

  body
  {
    background: url('http://nikolow.com/dragon2.jpg') top center  fixed !important;
    background-repeat: no-repeat !important;
    background-size: cover !important;
  }

  #wrapper > div#head > div.head_wrapper > div.s_form > div.s_form_wrapper.soutu-env-nomac.soutu-env-index > div#lg > img
  {
    display: none !important;
  }

  div#lg
  {
    background: url(http://nikolow.com/users/baidulogo1.png) no-repeat!important;
    background-size: 269px 95px!important;
    height: 95px!important;
    width: 269px!important;
  }

  div#lg:not([title~=Google])
  {
    margin-top: 50px;
    margin-left: 170px!important;
  }

  div#lg>:not([nowrap=''])
  {
    display: none!important;
  }

  img#lg
  {
    background: url(http://nikolow.com/users/baidulogo1.png) no-repeat!important;
    background-size: 269px 95px!important;
    height: 0!important;
    width: 269px!important;
  }

  img#lg:not([title~=Google])
  {
    margin-top: 50px;
    margin-left: 170px!important;
  }

  img#lg>:not([nowrap=''])
  {
    display: none!important;
  }

  #wrapper > div#head > div.head_wrapper > div.s_form > div.s_form_wrapper.soutu-env-nomac.soutu-env-index
  {
    top: -100px !important;
  }

  #page a, #page strong
  {
    background: none !important;
    color: #fff !important;
  }

  #rs
  {
    background: rgba(0,0,0,0.8) !important;
    padding-left: 20px !important;
    color: #fff !important;
  }

  *|*:link
  {
    color: #efefef !important;
    ;
  }

  p, div, span, body
  {
    color: #bbb !important;
  }

  #s_tab
  {
    background: rgba(0,0,0,0.5) !important;
    border-bottom: 1px solid #555 !important;
  }

  #head
  {
    padding-top: 10px!important;
    background: transparent !important;
    border-bottom: 0px!important;
  }

  #head.s_down
  {
    background: rgba(0,0,0,0.8) !important;
  }

  #form.fm > span.bg.s_ipt_wr.quickdelete-wrap
  {
    background: #fff !important;
  }

  #wrapper.wrapper_l > div#s_tab.s_tab > b
  {
    color: #e10601 !important;
  }

  #result_logo
  {
    width: 101px !important;
    height: 35px !important;
    background: url('http://pic1.qiyipic.com/common/lego/20171211/cbda070de190428bb07cbe35ffb1f718.jpg') !important;
    background-repeat: no-repeat;
  }

  #result_logo > img
  {
    display: none !important;
  }

  .c-border
  {
    background: rgba(0,0,0,0.8) !important;
    border: 1px solid #555 !important;
  }

  #form.fm > span.bg.s_ipt_wr.quickdelete-wrap
  {
    background: rgba(255,255,255,0.7) !important;
  }

  #form.fm > span.bg.s_btn_wr > input#su.bg.s_btn
  {
    background: rgba(255,6,1,0.7) !important;
    border-bottom: 1px solid rgba(255,6,1,0.7)!important;
  }

  #wrapper.wrapper_l > div#wrapper_wrapper > div#container.container_l > div#content_right.cr-offset
  {
    background: rgba(0,0,0,0.8) !important;
    border: 1px solid #555 !important;
    padding-top: 15px !important;
  }

  #foot, #help
  {
    background: rgba(0,0,0,0.8) !important;
  }
}
