css:
/* Applies only to homepage */
@-moz-document url("https://www.amazon.com/") {

    /*Remove unnecessary content*/
    #pageContent, #rhf .rhf-fresh-header, #rhf-shoveler, .rvi-fresh-footer, #nav-upnav {
        display: none;
    }

    .a-spacing-base.a-spacing-top-base.a-divider-normal {
        border-top: none;
    }

    .nav-locale-us.nav-lang-en.nav-ssl.nav-rec.nav-opt-sprite {
        margin: 23% !important;
    }

    #navFooter.navLeftFooter {
        margin-top: 5%;
    }

    #navbar {
        min-width: 125%;
        left: -15%;
    }
}

/* Applies to all of Amazon */
@-moz-document domain("amazon.com") {}
