css:
@-moz-document domain("foroazkenarock.com") {
img {
    visibility: hidden;
}
img.i_icon_quote {
    visibility: visible;
}
.postprofile {
    display: none;
}
.row1 {
    background-color: rgba(0, 0, 0, 0);
}
.row2 {
    background-color: rgba(0, 0, 0, 0);
}
#wrap {
    margin: 0;
    padding: 0;
    width: 100%;
}
#page-header {
    display: none;
}
.postbody {
    width: 100%;
    margin-top: 14px;
}
.postbody img {
    max-width: 100px;
}
li.row {
    border-bottom: none;
    border-top: none;
}
.forumbg {
    background-color: rgba(0, 0, 0, 0);
    background-size: 0;
}
blockquote blockquote {
    background-color: rgba(0, 0, 0, 0);
    margin: 0px;
}
blockquote {
    background: rgba(0, 0, 0, 0);
    box-shadow: 0 0 0 0;
    margin: 0;
}
.signature_div {
    display: none;
}
blockquote blockquote blockquote {
    background-color: rgba(0, 0, 0, 0);
}
blockquote blockquote blockquote blockquote {
    background-color: rgba(0, 0, 0, 0);
}
blockquote div {
    margin-left: 10px;
}
.postbody .topic-title {
    display: none;
}
blockquote cite {
    font-weight: 100;
}
.postbody .content {
    font-size: 10px;
}
div.sceditor-toolbar {
    display: none;
}
a.topictitle {
    font-size: 9px;
    font-weight: 100;
    text-decoration: none;
}
.panel {
    background-color: rgba(0, 0, 0, 0);
}
.ytp-thumbnail-overlay-image {
    display: none;
}
.topic-actions {
    font-size: 1.1em;
    height: 0;
    margin-bottom: 0;
    margin-top: 0;
}
a.addthis_button {
    display: none;
}
.pagination span a,
.pagination span a:active,
.pagination span a:link,
.pagination span a:visited {
    background-color: transparent;
    border: 0;
}
.topic-actions .pagination {
    display: none;
}
.content {
    font-size: 1em;
    line-height: inherit;
    min-height: 0;
    margin-left: 26px;
}
img.i_icon_quote {
    visibility: visible;
    height: 14px;
}
p.right {
    display: none;
}
.postbody ul.profile-icons {
    margin-right: 0;
    padding: 0;
}
.clearfix {
    display: none;
}
.content.clearfix {
    display: inline-block;
}
body {
    background-color: #FCFCFC;
}
#wrap {
    background-color: #FCFCFC;
}
img.sprite-icon_topic_newest {
    visibility: visible;
}
.smiley-element img {
    visibility: inherit;
}
.smiley-element {
    visibility: inherit;
    display: inline-block;
}
img {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}
img.i_post {
    visibility: visible;
    height: 18px;
    margin: 9px;
}
iframe {
    max-height: 245px;
}
}
