css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yoyochinese.com") {
  
  body {
    background-color: #222;
    color: #aaa;
  }
  
  /***** Landing Page *****/
  
  .landing1_banner {
    background-color: #282828;
  }
  
  .landing1_logo {
    background-color: #333;
  }
  
  .landing1_nav ul li a {
    color: #aaa;
    border-right-color: #666;
  }
  
  .landing1_box4, .landing1_zong2 {
    background-color: #282828;
  }
  
  .landing1_zong {
    background-color: #333;
  }
  
  .landing1_box6 {
    background-color: #282828;
  }
  
  .landing1_box, .landing1_box5 {
    background-color: #114b4e; /* from #2bc3cc */
  }
  
  .landing1_box .zw ul li {
    color: #aaa;
  }
  
  .landing1_box .zw ul li img {
    opacity: 0.5;
  }
  
  .landing1_box5 .zw h2, .landing1_box5 .zw ul li {
    color: #aaa;
  }
  
  .landing1_box6 .ganx {
    background: #333;
  }
  
  .landing1_box6 .ganx ul li {
    background-color: #333;
  }
  
  .landing1_box6 .ganx ul li:nth-child(2) {
    background-color: #363636;
  }
  
  /***** Home Page *****/
  
  /* Top Bar */
  .navbar-default {
    background-color: #282828;
  }
  
  .navbar-brand > img {
    filter: invert(88%); /* #282828 */
  }
  
  .navbar-default .navbar-nav > li > a {
    color: #aaa;
  }
  
  .navbar .navbar-nav > li > a:hover {
    background-color: #333;
    color: #aaa;
  }
  
  #menu-course.active, #menu-course.open, #menu-course.active > #Courses, #Courses:hover {
    background-color: #333;
  }
  
  #menu-course .courses-list {
    background-color: #333;
  }
  
  #menu-course .courses-list .courses-item:not(:last-child) {
    border-bottom-color: #666;
  }
  
  #menu-course .courses-item a {
    color: #aaa;
  }
  
  /* Profile dropdown */
  .navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:focus, .navbar-default .navbar-nav > .open > a:hover {
    background-color: #333;
    color: #aaa;
  }
  
  .dropdown-menu {
    background-color: #333;
  }
  
  .dropdown-menu li a {
    color: #aaa;
  }
  
  .dropdown-menu li i {
    border-bottom-color: #666;
  }
  
  .dropdown-menu li img {
    filter: invert(84%); /* #333 */
  }
  
  /* Study Stats left box */
  .box_left {
    background-color: #333;
  }
  
  .priceName {
    border-bottom-color: #666;
  }
  
  .right_center p img {
    filter: invert(84%); /* #333 */
  }
  
  /* Central box */
  .content .carousel-inner {
    border-color: #333;
  }
  
  .text_center {
    color: #aaa;
  }
  
  ._ccimg img {
    opacity: 0.5;
  }
  
  div[style="background:#2bc3cc"] {
    background-color: #114b4e !important;
  }
  
  div[style="background:#73c07a"] {
    background-color: #306a35 !important;
  }
  
  div[style="background:#fab255"] {
    background-color: #114b4e !important;
  }
  
  div[style="background:#4a89d8"] {
    background-color: #184071 !important;
  }
  
  /* Bottom arrows and buttons */
  .willRotate > .leftimg > img, .willRotate > .rightimg > img {
    filter: invert(86.5%);
  }
  
  .content .carousel-indicators li {
    background-color: #363636;
  }
  
  /* 'Recently viewed' */
  #myscroll ul li .x {
    border-color: #333;
  }
  
  #myscroll ul li h3 a {
    color: #aaa;
  }
  
  /** Footer **/
  .footer_index {
    background-color: #282828;
  }
  
  /* 'Find us on' */
  .footer_index_02 {
    background-color: #282828;
    border-color: #666;
  }
  
  /***** Lesson Page e.g. https://yoyochinese.com/lesson/beginner-conversational-unit-1-lesson-2-What-Is-Pinyin *****/
  
  /* Video bkgd */
  
  .banner {
    background-color: #333;
  }
  
  .banner_box a {
    color: #f00;
  }
  
  .lesson-left-arrow img, .lesson-right-arrow img {
    opacity: 0.2;
  }
  
  /* Title */
  .container-fluid .row .breadcrumb {
    background-color: #222;
  }
  
  .container-fluid .row .breadcrumb > .active {
    color: #aaa;
  }
  
  /** 'Learn on the Go' box **/
  .box .boxright {
    border-color: #666;
  }
  
  /** 'Start Practice' button **/
  #startTag {
    background-color: #674600; /* from #ffaf00 */
    box-shadow: 3px 3px 4px #444;
    color: #aaa;
  }
  
  #startTag:hover {
    background-color: #7b3c03; /* from #f9851f; */
  }
  
  /** Pop-up chat dialog **/
  
  #intercom-container div {
    background-color: #333;
  }
  
  #intercom-container .intercom-launcher-discovery-text {
    color: #333;
  }
  
  #intercom-container .intercom-messenger-background {
    background-color: #333;
  }
  
  #intercom-container .intercom-conversation-summary {
    border-top-color: #666;
  }
  
  #intercom-container .intercom-conversation-summary:hover {
    background-color: #444;
    border-top-color: #666;
  }
  
  #intercom-container .intercom-conversation-summary:last-child {
    border-bottom-color: #666;
  }
  
  #intercom-container .intercom-conversation-summary-body-author, #intercom-container .intercom-conversation-summary-timestamp {
    color: #666;
  }
  
  #intercom-container .intercom-conversation-summary-content-container {
    color: #666; /* Added */
  }
  
  #intercom-container .intercom-conversations-footer {
    background: linear-gradient(0deg,#222,hsla(0,0%,100%,0));
  }
  
  #intercom-container .intercom-post .intercom-block-image img {
    filter: invert(80%);
  }
  
  /* Separator */
  .hline {
    background-color: #333;
  }
  
  /** 'Discussion' **/
  
  .Resources_31 .bian3 {
    border-color: #666;
    color: #aaa;
  }
  
  .Resources_32 h3 {
    border-bottom-color: #666;
  }
  
  /* 'Most recent'/'Most upvoted' selector */
  .Resources_32 h3 .form-control {
    border-color: #666;
  }
  
  /* Comment row */
  .Resources_32 .bian {
    border-bottom-color: #333;
  }
  
  .Resources_3 {
    color: #999;
  }
  
  /***** Quiz Page *****/
  
  /** Card **/
  .Desktop {
    background-color: #333;
    box-shadow: 0 0 12px #333;
  }
  
  .Desktop_x a img {
    filter: invert(85%);
  }
  
  /* Progress bar */
  .pb-wrapper {
    background-color: #222;
  }
  
  /* Options - Before Check */
  .btn-light, .btn-light:focus {
    background-color: #222 !important;
    border-color: #333;
  }
  
  .btn-light {
    color: #aaa !important;
  }
  
  /* Options - After check */
  .btn-light.disabled, .btn-light[disabled], fieldset[disabled] .btn-light, .btn-light.disabled:hover, .btn-light[disabled]:hover, fieldset[disabled] .btn-light:hover, .btn-light.disabled:focus, .btn-light[disabled]:focus, fieldset[disabled] .btn-light:focus, .btn-light.disabled:active, .btn-light[disabled]:active, fieldset[disabled] .btn-light:active, .btn-light.disabled.active, .btn-light.active[disabled], fieldset[disabled] .btn-light.active {
    background-color: #666 !important;
    border-color: #333;
  }
  
  /* 'Next' button */
  .Quiz_hong_2 .yi {
    background-color: #333;
  }
  
  /** 'Are You Sure?' Quit overlay **/
  
  .flashcard-info-heardimg img {
    filter: invert(84%);
  }
  
  .modal-content .modal-header {
    background-color: #282828;
  }
  
  /* 'Quit', 'Keep studying' buttons */
  .modal-body .container-fluid button {
    background-color: #333;
  }
  
  
  /***** Flashcards *****/
  
  #intercom-container .intercom-post-composer {
    background-color: #333;
  }
  
  /* Reply Pop-up */
  #intercom-container .intercom-composer textarea, #intercom-container .intercom-composer textarea:focus {
    background-color: #333;
    color: #999;
  }
  
  /* Selection list */
  .selection_list {
    background-color: #222;
  }
  
  .selection_item:not(:first-child) select.disabled {
    background-color: #333;
  }
  
  .selection_item h4.title {
    color: #999;
  }
  
  .selection_item select {
    border-color: #333;
  }
  
  /***** Resources *****/
  
  /* Resources tab */
  .Resources_top {
    background-color: #114b4e; /* from #2bc3cc */
  }
  
  .Resources_container .active {
    opacity: 0.7;
  }
  
  .Resources_container img {
    opacity: 0.9;
  }
  
  /**** Pinyin chart Page or yoyochinese.com/chinese-learning-tools/Mandarin-Chinese-pronunciation-lesson/pinyin-chart-table ****/
  
  /*** Table ***/
  
  .Resources_table .bj, .Resources_table .zi4, .Resources_table .bj6 {
    background-color: #333;
  }
  
  .Resources_table .zi3, .Resources_table .zi2 {
    color: #00a8bd;
  }
  
  .Resources_table .bj3_img {
  }
  
  .Resources_table td {
    border-right-color: #333;
    border-bottom-color: #333;
  }
  
  .Resources_table {
    border-left-color: #333;
    border-top-color: #333;
  }
  
  /* Hover cell */
  .Resources_table .bj4 {
    background-color: #282828;
  }
  
  .Resources_table .bj4 .bj5 {
    color: #00a8bd; /* from #007b8a; */
  }
  
  /** 'Video/Auto Demonstration' overlay **/
  .modal-dialog .modal-content {
    background-color: #282828;
  }
  
  .modal-dialog .modal-content .modal-header h4 {
    color: #999;
  }
  
  .modal-dialog .modal-content .modal-header .close img {
    filter: invert(85%);
  }
  
  /**** Reference Videos ****/
  
  /*** Chinese Grammar ***/
  .Resources_zong .form-control {
    background-color: #282828;
    border-color: #333;
  }
  
  .Resources_zong .form-control option {
    background-color: #282828;
  }
  
  .Resources_zong .nopadding {
    border-top-color: #666;
  }
  
  .Resources_zong .Resources_022 {
    border-bottom-color: #333;
  }
  
  
  /**** Download Center Page or yoyochinese.com/download-center ****/
  
  /* Section heading */
  .Resources_zong {
    color: #aaa;
  }
  
  .Resources_012 h3 {
    background-color: #333;
  }
  
  /* Section row */
  .Resources_012 ul li {
    border-top-color: #666;
  }
  
  /***** Profile Page *****/
  
  .navleft {
    background-color: #333;
  }
  
  .navleft ul li {
    border-bottom-color: #666;
  }
  
  /* 'Edit basic info' */
  .profile_01 h5 button {
    background-color: #222;
  }
  
  /* Separator */
  .profile_01 {
    border-bottom-color: #333;
  }

}

@-moz-document url-prefix("https://apis.google.com/u/0/_/widget/render/person"), url-prefix("https://apis.google.com/_/widget/render/person") {
  
  /* Home Page > Footer > 'Find us on' */
  .nae {
    background-color: #333;
  }
  
}
