css:
@-moz-document domain("habitica.com") {
.chat-row .row .col-2+.col-10 .card-body {
    padding: 0.2em 0.2em;
}
.chat-row .row .col-2+.col-10 .card-body .time {
    position: absolute;
    top: 0.5em;
    left: -11em;
    width: 10em;
    font-size: 0.8em;
    /*background:red;*/
    
    text-align: right;
    opacity: 0.5;
}
.chat-row .row .col-2+.col-10 .card-body .leader,
.chat-row .row .col-2+.col-10 .card-body hr {
    display: none;
}
.chat-row .row .col-2+.col-10 .card-body .action:not(.liked) {
    position: absolute;
    top: 0.3em;
    left: 100%;
    width: 5em;
    opacity: 0.5;
    margin-left: 0.5em;
}
.chat-row .row .col-2+.col-10 .card-body .action.liked {
    position: absolute;
    top: 0.3em;
    left: 100%;
    width: 5em;
    margin-left: 6em;
}
.chat-row .row .col-2+.col-10 .card-body code {
    background: transparent;
    color: #666;
    font-size: 0.9em;
    padding: 0;
}
/* Classes added by Greasemonkey*/

.desc {
    font-size: 0.8em;
    color: #666;
    font-style: italic;
}
.wiki {
    font-weight: bold;
    padding: 0.1em 0.7em;
}
.card.today {
    opacity: 1;
    padding: 0.3em 0.6em;
}
.card.yesterday {
    opacity: 0.6;
}
.card.days-ago {
    opacity: 0.4;
}
.card.old {
    opacity: 0.2;
}


.card.cast {
    border: 0.15em solid #efc6f3;
    padding: 0.5em 1em;
    background-color: #feffd2;
}
.card.cast a.wiki {
    color: #ab10b9 !important;
    background: #fdffc7;
}
.card.found {
    border: 0.1em solid #c6f3c7;
}
.card.found .count {
    color: #46bf17;
}
.card.found .count-0 {
    color: pink;
    font-weight: normal;
}
.card.found a.wiki {
    color: #46bf17 !important;
    background: #edffed;
}
.card.boss .wiki {
    color: inherit;
}
.card.boss .attack {
    color: #32b368;
}
.card.boss .attack .hp {
    background: #edfff4;
    padding: 0.1em 0.3em;
}
.card.boss .damage {
    font-size: 0.9em;
    color: #e06d6d;
    padding: 0.1em 0.3em;
}
.card.boss .damage .hp {
    background: #fff1f4;
    padding: 0.1em 0.3em;
}
.card.start {
    border: 0.4em double #c4dbec;
    padding: 0.4em 1em;
    background-color: #76beff;
    margin-bottom: 5em;
}
.card.start h4 {
    font-size: 1.6em;
    padding: 0;
    margin: 0;
    color: #fff;
}
.card.start a {
    color: #fff;
    text-decoration: underline;
}
.card.victory {
    border: 0.4em double #c6f3c7;
    padding: 0.5em;
    background-color: #ecffde;
}
.card.victory h4 {
    font-size: 1.6em;
}

}
