@-moz-document domain("miningpoolhub.com")
{
	body, .selected_row, .visualize
	{
		color: #aaa !important;
		background-color: #2C2C2C !important;
	}

	aside#sidebar, .module, #main h4,
	.module_content > div,
	.module_content > div > div
	{
		color: #AAA !important;
		background-color: #1C1C1C !important;
	}

	#homeTitle
	{
		color: #AAA !important;
		background-color: #3C3C3C !important;
	}

	#sidebar li a, #subHomeTitle
	{
		color:  #8098AD !important;
	}

	input[type="text"]:focus, input[type="email"]:focus, input[type="password"]:focus,
	input[type="checkbox"], input[type="text"], input[type="email"], input[type="password"],
	input[type="submit"].alt_btn, select
	{
		color: #aaa !important;
		background-color: #3C3C3C !important;  
	}

	.visualize-labels-x li span.label,
	.visualize-labels-y li span.label,
	.module_content
	{
		color: #AAA !important;
	}
	#sidebar h3, #main h3
	{
		color: #86BFF0 !important;
	}

	ul.tabs li.active
	{
		background: #999 !important;
	}
	svg > text > tspan
	{
		fill: #BBB !important;
	}
}
