css:
@-moz-document url-prefix("http://www.feedly.com/home"), url-prefix("https://www.feedly.com/home"), url-prefix("http://feedly.com/index.html"), url-prefix("https://feedly.com/index.html"), domain("cloud.feedly.com"), domain("feedly.com") {
/* ==================== Fonts ==================== */
* {
font-family: tahoma!important;
text-decoration: none!important;
letter-spacing: 0!important;
}

/* ==================== Body ==================== */
body {
background: #011820 url("http://hddesktopwallpapers.in/wp-content/uploads/2015/07/abstract-wallpapers-hd-dna.jpg") no-repeat fixed 0% 50%!important;
background-size: cover!important;
}

.home.stretched .light #feedlyFrame, .light #feedlyPageFX.container.centered.title-only, .home.stretched .light #feedlyPart0 {
background: #EFF0F2!important;
}

.home .light #feedlyFrame, .light #feedlyPageHolderFX, .light #mainBarFX, .light #feedlyPageFX.container.centered, .home .light #feedlyPart0 {
background: #8E959A!important;
}

.home .dark #feedlyFrame, .dark #feedlyPageHolderFX, .dark #mainBarFX, .dark #feedlyPageFX.container.centered, .home .dark #feedlyPart0 {
background: #2E3337!important;
}

#mainBar, #feedlyPage {
width: 100%!important;
}

#feedlyPart0 {
margin-right: 33px!important;
}

#feedlyPart {
padding-right: 0!important;
width: 100%!important;
}

#feedlyPageHeader, #mainArea[style="margin-right:-34px"], #timeline {
margin-right: 0!important;
}

.libraryPage {
margin-right: -33px!important;
}

.main.site-content, .site-content, body.home.blog, body.api {
background: #FFFFFF!important;
}

/* ==================== Login and Pro Page ==================== */
[src="/images/login-feedly-logo@2x.png"], [src="//feedly.com/images/login-feedly-logo@2x.png"], [src="/images/login-devices@2x.png"] {
display: none!important;
}

#feedlyBanner {
color: #FFFFFF!important;
text-shadow: 0 0 1px #FFFFFF,0 0 1px #FFFFFF,0 0 1px #FFFFFF!important;
font-size: 18px!important;
font-weight: normal!important;
letter-spacing: 2px!important;
margin-top: 120px!important;
}

.container:not(.title-only) h2 div.sub, body .container .topmessage {
color: #FFFFFF!important;
}

[method="post"] [type="submit"], .container .footer .lnk {
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
font-weight: bold!important;
}

.container .footer .lnk {
color: #2BB24C!important;
}

.leftBarWelcome h1 {
opacity: 1!important;
}

.leftBarWelcome .primary, .leftBarWelcome .secondary {
background-color: transparent!important;
background-image: -webkit-linear-gradient(left,rgba(36,52,68,1),rgba(36,52,68,0),rgba(36,52,68,0))!important;
background-image: -moz-linear-gradient(left,rgba(36,52,68,1),rgba(36,52,68,0),rgba(36,52,68,0))!important;
background-image: -ms-linear-gradient(left,rgba(36,52,68,1),rgba(36,52,68,0),rgba(36,52,68,0))!important;
background-image: -o-linear-gradient(left,rgba(36,52,68,1),rgba(36,52,68,0),rgba(36,52,68,0))!important;
background-image: linear-gradient(to right,rgba(36,52,68,1),rgba(36,52,68,0),rgba(36,52,68,0))!important;
color: #FFFFFF!important;
text-shadow: 0 0 3px #000000,3px 3px 3px #000000,0 0 3px #000000!important;
letter-spacing: 3px!important;
text-align: left!important;
border: 1px solid #FFFFFF!important;
box-shadow: 0 0 3px 1px #000000!important;
}

#feedlyPageHolderFX.fx.page-landing #feedlyPageFX {
background: #E2E2E1!important;
}

.home:not(.stretched) .dark #feedlyPageHolderFX.fx.page-landing #feedlyPageFX {
background: #171B1F!important;
}

.home #feedlyPageHolderFX.fx.page-landing .landing .header {
background-color: #FF6600!important;
border-bottom: 2px solid #FFFFFF!important;
box-shadow: 0 0 2px 1px #555555!important;
margin: 0 -24px 80px -24px!important;
padding-top: 30px!important;
padding-bottom: 30px!important;
}

.home:not(.stretched) .dark #feedlyPageHolderFX.fx.page-landing .landing .header {
background-color: #CC3300!important;
}

.home.stretched #feedlyPageHolderFX.fx.page-landing .landing .header {
background-color: #2BB24C!important;
border-bottom: none!important;
box-shadow: none!important;
margin: 0 -24px 192px -24px!important;
padding-top: 64px!important;
padding-bottom: 64px!important;
}

.home.stretched .list-people.background-gray {
margin-top: 0!important;
}

.small.pro.primary {
background-image: -webkit-linear-gradient(left,#EFF0F2,#FF6600,#EFF0F2)!important;
background-image: -moz-linear-gradient(left,#EFF0F2,#FF6600,#EFF0F2)!important;
background-image: -ms-linear-gradient(left,#EFF0F2,#FF6600,#EFF0F2)!important;
background-image: -o-linear-gradient(left,#EFF0F2,#FF6600,#EFF0F2)!important;
background-image: linear-gradient(to right,#EFF0F2,#FF6600,#EFF0F2)!important;
color: #FFFFFF!important;
letter-spacing: 2px!important;
box-shadow: 0 0 3px 0 #333333!important;
padding: 4px 17px 2px 17px!important;
}

.dark .small.pro.primary {
background: #2B3034!important;
box-shadow: 0 0 2px 1px rgba(0,0,0,0.3)!important;
}

.onboarding.container630.centered {
background-image: -webkit-linear-gradient(left,#40494F,#55626F)!important;
background-image: -moz-linear-gradient(left,#40494F,#55626F)!important;
background-image: -ms-linear-gradient(left,#40494F,#55626F)!important;
background-image: -o-linear-gradient(left,#40494F,#55626F)!important;
background-image: linear-gradient(to right,#40494F,#55626F)!important;
}

.onboarding.container630.centered h1 {
color: #FFFFFF!important;
text-shadow: 1px 1px 3px #000000!important;
}

.onboarding.container630.centered .sub {
color: #FFFFFF!important;
}

section.container630.centered h1, section.container630.centered .sub,
section.container630.centered .bordered-box .item-header, section.container630.centered .bordered-box .item-description:not(a), .sliderContainer .fx.account-login .item-header {
color: #000000!important;
text-shadow: none!important;
}

.onboarding.container630.centered table {
background: rgba(64,73,79,0.5)!important;
color: #FFFFFF!important;
}

.onboarding.container630.centered button.primary.button-icon-far-right {
background-color: #249540!important;
color: #FFFFFF!important;
text-shadow: none!important;
font-size: 12px!important;
border: 2px solid #FFFFFF!important;
border-radius: 4px!important;
box-shadow: 0 0 3px 0 #333333!important;
}

.onboarding.container630.centered button.primary.button-icon-far-right[disabled=""] {
background-color: #3A4248!important;
}

.onboarding.container630.centered .field label, .onboarding.container630.centered table .label-cell .item-header:not(.selected) {
color: #050505!important;
}

.onboarding.container630.centered .skip.tertiary, .onboarding.container630.centered .go-back.tertiary, .onboarding.container630.centered .required::after, .onboarding.container630.centered .hint, .onboarding.container630.centered table .feed-count {
color: #B3B7BA!important;
}

.proB h1, .home .col-xs-12.col-md-6 h1 {
color: #FFFFFF!important;
text-shadow: 1px 1px 3px #000000!important;
letter-spacing: 1px!important;
}

.home.stretched .col-xs-12.col-md-6 h1 {
color: #2BB24C!important;
text-shadow: 0 0 3px #000000,3px 3px 3px #000000,0 0 3px #000000!important;
}

.home .col-xs-12.col-md-6 div.sub {
color: #FFFFFF!important;
font-size: 21px!important;
font-weight: normal!important;
}

.home.stretched .col-xs-12.col-md-6 div.sub {
text-shadow: 0 0 3px #000000,3px 3px 3px #000000,0 0 3px #000000!important;
}

.dark .proB .edition {
background-color: #40494F!important;
border: 1px solid #202020!important;
}

.dark .proB .edition .editionInfo, .dark .proB .edition .editionFeatures b {
color: #DDDDDD!important;
}

.dark .proB .edition .editionDescription, .dark .proB .edition .editionFeatures {
color: #BBBBBB!important;
}

.proB .priceTag, .proB .dollarCents, .proB .dollarSign {
color: #FFFFFF!important;
text-shadow: 0 0 3px #FF6600,0 0 3px #FF6600,0 0 3px #FF6600!important;
}

.dark .proB .priceTag, .dark .proB .dollarCents, .dark .proB .dollarSign {
color: #40494F!important;
text-shadow: 0 0 3px #000000,3px 3px 3px #000000,0 0 3px #000000!important;
}

.dark .priceDurationExplanation {
color: #999999!important;
}

.proB .priceTag.free {
color: #000000!important;
text-shadow: none!important;
}

.home .landing .col-xs-12.col-md-6 .primary, .home .col-xs-8.col-md-8.right-col .primary, .home .landing footer .primary {
background-color: #FF6600!important;
color: #FFFFFF!important;
font-size: 15px!important;
letter-spacing: 1px!important;
border: 2px solid #FFFFFF!important;
box-shadow: 0 0 3px 0 #333333!important;
}

.home:not(.stretched) .dark .landing .col-xs-12.col-md-6 .primary, .home:not(.stretched) .dark .landing .col-xs-12.col-md-6 .secondary.loud,
.home:not(.stretched) .dark .col-xs-8.col-md-8.right-col .primary, .home:not(.stretched) .dark .landing footer .primary {
background-color: #CC3300!important;
border: 1px solid #FFFFFF!important;
box-shadow: 0 3px #000000!important;
}

.home.stretched .landing .col-xs-12.col-md-6 .primary {
background-color: #2BB24C!important;
color: #EFEFEF!important;
font-size: 14px!important;
border: 1px solid #FFFFFF!important;
}

.home.stretched .col-xs-8.col-md-8.right-col .primary {
background-color: #FFFFFF!important;
color: #000000!important;
font-size: 12px!important;
letter-spacing: 0!important;
border: 1px solid #D9D9D9!important;
box-shadow: none!important;
}

.home.stretched .landing footer .primary {
background-color: #249540!important;
font-size: 13.5px!important;
letter-spacing: 0!important;
}

.home .priceBox .fx-button.secondary, .home .landing .col-xs-12.col-md-6 .secondary.loud {
background-color: #FF6600!important;
color: #000000!important;
font-size: 12px!important;
letter-spacing: 1px!important;
border: 2px solid #FFFFFF!important;
box-shadow: 0 0 3px 0 #333333!important;
}

.home.stretched .landing .col-xs-12.col-md-6 .secondary.loud {
background-color: #249540!important;
}

.home .col-xs-8.col-md-8.right-col .secondary.loud, .home .landing footer .secondary.loud {
background-color: transparent!important;
color: #000000!important;
text-shadow: none!important;
font-size: 12px!important;
letter-spacing: 1px!important;
}

.home .col-xs-8.col-md-8.right-col .primary, .home .col-xs-8.col-md-8.right-col .secondary.loud {
line-height: 18px!important;
}

.home .col-xs-8.col-md-8.right-col .secondary.loud {
border: 1px solid rgba(200,200,200,0.50)!important;
}

.landing-v0 .kicker {
text-shadow: none!important;
}

#feedlyPageHolderFX.fx.page-landing h2, .landing-old .backgrounded h1 {
color: #F7F5F3!important;
text-shadow: 0 0 3px #2BB24C,0 0 3px #2BB24C,0 0 3px #2BB24C!important;
font-size: 36px!important;
letter-spacing: 1px!important;
}

.landing-old .section h1 {
color: #EBEBEB!important;
text-shadow: 0 0 3px #2BB24C,0 0 3px #2BB24C,0 0 3px #2BB24C!important;
letter-spacing: 1px!important;
}

#feedlyPageHolderFX.fx.page-landing h2 div.sub {
color: #000000!important;
text-shadow: none!important;
}

.landing-old .passion.dark, #feedlyPageFX.container.centered .landing .background-accent {
background: #FF6600!important;
border-top: 1px solid #FF6600!important;
border-bottom: 1px solid #FF6600!important;
}

.landing-old .passion.dark h1, #feedlyPageFX.container.centered .background-accent h2 {
color: #FFFFFF!important;
text-shadow: 1px 1px 3px #000000!important;
letter-spacing: 1px!important;
}

.dark .landing-old .passion.dark {
background: #CC3300!important;
border-top: 1px solid #CC3300!important;
border-bottom: 1px solid #CC3300!important;
}

.dark .landing-old .section.top .fx-button.primary.pro, .dark .fx-button.primary.pro, .dark .fx-button.secondary.pro,
.dark .page-account .actions-and-details-container .button.secondary, .dark #headerBarFX [href="https://feedly.com/i/pro/account"] {
background: #CC3300!important;
border: 1px solid #FFFFFF!important;
box-shadow: 0 3px #000000!important;
}

.home.stretched #feedlyPageHolderFX.fx.page-landing footer {
background-color: #D9D9D9!important;
margin-left: -24px!important;
margin-right: -24px!important;
}

/* ==================== Add Content Page ==================== */
.tabs .item {
color: #000000!important;
}

.tabs .item:hover {
border-bottom: 2px solid #555555!important;
}

.tabs .item .icon {
opacity: 1!important;
}

.tabs .active.item {
color: #FFFFFF!important;
font-weight: bold!important;
border-bottom: 3px solid #FFFFFF!important;
}

.tabs .active.item .icon {
-webkit-filter: brightness(2000%)!important;
filter: brightness(2000%)!important;
}

.herculeKicker, .discover .description {
color: #313131!important;
font-size: 16px!important;
}

.dark .herculeKicker, .dark .discover .description {
color: #DDDDDD!important;
}

.discover-search .fx-input.button-right.icon-left, .discover-search .fx-input.button-right.icon-left input {
background-color: #EFF0F2!important;
border-color: #8E959A!important;
border-radius: 4px!important;
box-shadow: 1px 1px 2px 0 #222222 inset!important;
}

.dark .discover-search .fx-input.button-right.icon-left, .dark .discover-search .fx-input.button-right.icon-left input {
background-color: #3B3D3F!important;
color: #FFFFFF!important;
border-color: #2E3337!important;
box-shadow: 1px 1px 2px 0 #000000 inset!important;
}

.dark .discover-search .fx-input.button-right.icon-left input:focus {
border-color: #2BB24C!important;
}

.domain-select .popover {
padding-right: 10px!important;
}

.discover-search.with-language.with-domain h3, .discover-search.with-language.with-domain .hint, .discover-search.with-language.with-domain label:not(.label-radio) {
color: #9E9E9E!important;
}

.fx-select .button-dropdown .container-flex {
background-color: rgba(255,255,255,0.2)!important;
border: 1px solid #BBBBBB!important;
}

.dark .fx-select .button-dropdown .container-flex {
border: 1px solid #333333!important;
}

h3, .container h4, .stream h4 .label, .sliderContainer .list-entries h4, .discover .search-example, .form.row .field label, .fx-select .button-dropdown .container-flex span {
color: #2bb24c!important;
text-shadow: none!important;
}

.discover .sub {
color: #CCCCCC!important;
}

.discover .chip, .feed-large.item.feed .topic {
color: #000000!important;
border: 1px solid #FFFFFF!important;
box-shadow: none!important;
}

.feed-large.item.feed .topic {
border: 1px solid #BBBBBB!important;
}

.discover .chip:hover, .feed-large.item.feed .topic:hover {
background-color: #000000!important;
color: #FFFFFF!important;
box-shadow: 0 1px 15px 0 rgba(0,0,0,0.15), 0 0 100px 0 #2B3034 inset!important;
}

.discover .topic-card {
background-color: transparent!important;
color: #000000!important;
border: 1px solid #FFFFFF!important;
border-radius: 4px!important;
box-shadow: none!important;
}

.discover .topic-card:hover {
background-color: #000000!important;
color: #FFFFFF!important;
border: 1px solid transparent!important;
box-shadow: 0 2px 8px 0 rgba(0,0,0,0.2), 0 0 2px 0 rgba(0,0,0,0.38), 0 0 100px 0 #2B3034 inset!important;
}

.light .discover .topic-card .card-footer div {
color: #333333!important;
}

.dark .discover .topic-card .card-footer div {
color: #090909!important;
}

.discover .topic-card:hover .card-footer div, .feed-large.item.feed .item-meta {
color: #DDDDDD!important;
}

.feed-large.item.feed {
background: transparent!important;
border: none!important;
box-shadow: none!important;
}

.feed-large.item.feed .item-header, .feed-large.item.feed .entry .title, .relatedEntryHolder .related-title {
color: #000000!important;
}

.light .feed-large.item.feed .item-description {
color: #333333!important;
}

.feed-large.item.feed .small.follow, button.regular.follow, .u100Header-banner .small.follow, .domain-select .primary:not([disabled=""]) {
background-color: #005CB8!important;
color: #FFFFFF!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
border: 1px solid #FFFFFF!important;
border-radius: 3px!important;
box-shadow: 0 0 2px 1px #555555!important;
}

button.regular.follow {
text-shadow: none!important;
}

.u100Header-banner {
background-color: #4D585F!important;
color: #000000!important;
border-bottom: 1px solid #8E959A!important;
}

.u100Header-banner .small.follow {
font-weight: bold!important;
line-height: 16px!important;
border: 1px solid #000000!important;
}

.dark .feed-large.item.feed .small.follow, .sliderContainer .feed-large.item.feed .small.follow, .dark button.regular.follow, .sliderContainer button.regular.follow, .dark .domain-select .primary:not([disabled=""]) {
box-shadow: 0 0 2px 1px #000000!important;
}

#feedlyPageFX.container.centered .header.row .regular.following-feed, #feedlyPageFX.container.centered .small.following-feed {
color: #555555!important;
border: 1px solid #757575!important;
}

.light #feedlyPageFX.container.centered.title-only .header.row .regular.following-feed, #feedlyPageFX.container.centered.title-only .small.following-feed {
color: #757575!important;
border: 1px solid #C7C7C7!important;
}

.dark #feedlyPageFX.container.centered .header.row .regular.following-feed, .dark #headerBarFX .header.row .regular.following-feed, .dark #feedlyPageFX.container.centered .small.following-feed, .sliderContainer .header.row .regular.following-feed, .sliderContainer .small.following-feed {
color: #111111!important;
border: 1px solid #1E2428!important;
}

.discover .action[data-uri="support/apps"], .discover .link.primary.action {
background: #FFFFFF!important;
color: #000000!important;
line-height: 34px!important;
border: 2px solid #FFFFFF!important;
border-radius: 3px!important;
box-shadow: 0 0 3px 0 #333333!important;
padding: 5px 10px!important;
}

.dark .discover .action[data-uri="support/apps"], .dark .discover .link.primary.action {
box-shadow: 0 3px #000000!important;
}

.discover .action[data-uri="support/apps"]:hover, .discover .link.primary.action:hover {
background: #000000!important;
color: #FFFFFF!important;
}

/* ==================== Add Content Features ==================== */
#feedlyDialogFramePart .textValue {
background-color: #FFFFFF!important;
color: #005CB8!important;
font-weight: bold!important;
border: 1px solid rgba(0,0,0,0.15)!important;
margin: 0!important;
}

.secondaryActionButton.capitalize {
padding: 7px 0 10px 0!important;
}

.actionButton.capitalize, #formActions .fx-button.primary {
background-color: #005CB8!important;
color: #FFFFFF!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
font-size: 12px!important;
font-weight: bold!important;
line-height: 0!important;
text-align: center!important;
border: 1px solid #FFFFFF!important;
border-radius: 3px!important;
box-shadow: 0 0 2px 1px #000000!important;
padding: 14px 10px!important;
width: 89px!important;
height: 22px!important;
}

.secondaryActionButton.capitalize [style="text-transform:none;"] {
display: none!important;
}

.secondaryActionButton.capitalize .secondaryActionLink.capitalize {
color: #757575!important;
font-size: 14px!important;
border: 1px solid #D9D9D9!important;
border-radius: 3px!important;
padding: 5px 24px!important;
}

#followSubscriptionComponent .primary, #close.fx-button.primary.small, .form.add-url .floated.left.primary:not([disabled=""]) {
background-color: #005CB8!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
border: 1px solid #FFFFFF!important;
box-shadow: 0 0 2px 1px #000000!important;
}

#close.fx-button.primary.small {
font-size: 10px!important;
width: 60px!important;
}

#feedlyDialogFramePart .textValue:focus, [placeholder="New collection"]:focus, #feedlyDialogFramePart textarea:focus, .popover input:focus, .popover .small.accent.secondary, .popover .menu-footer, .enterprise-tag-info-form input[placeholder="Title"]:focus {
border: 1px solid #005CB8!important;
}

.popover .item-add-remove.selected {
color: #005CB8!important;
font-weight: bold!important;
}

.popover .small.primary.button-icon-left {
background-color: #005CB8!important;
text-shadow: none!important;
font-size: 11px!important;
border: 1px solid transparent!important;
border-radius: 3px!important;
box-shadow: none!important;
}

.popover .small.danger.secondary.button-icon-left {
background-color: #FFFFFF!important;
color: #F44336!important;
border: 1px solid #F44336!important;
}

.popover .small.accent.secondary, .popover .menu-footer .accent.tertiary {
background-color: #FFFFFF!important;
color: #005CB8!important;
}

.popover .menu-footer .accent.tertiary.button-icon-left {
background-color: #FFFFFF!important;
}

.popover div.menu.form button.primary {
background-color: #005CB8!important;
color: #FFFFFF!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
border: 1px solid #FFFFFF!important;
box-shadow: 0 0 2px 1px #555555!important;
}

.popover div.menu.form button.primary[disabled=""] {
background-color: #D9D9D9!important;
color: #FFFFFF!important;
text-shadow: none!important;
border: 1px solid #D9D9D9!important;
box-shadow: none!important;
}

.popover div.menu.form button.secondary {
background-color: #FFFFFF!important;
border: 1px solid #D9D9D9!important;
}

/* ==================== Floating Panel and Title Bar ==================== */
.dark .fx:not(.page-landing) .profile-bubble-menu .user.item-with-visuals {
background-color: #333333!important;
}

.dark .fx:not(.page-landing) .profile-bubble-menu .menu, .dark .group-button.button.secondary .menu, .dark .menu.menu-custom, .dark #addContentPlaceholderFX .menu, .dark .discover-search .popover {
