css:
@-moz-document domain("pikabu.ru") {
.footer__inner {
    background-color: #f0f0f0 !important;
}
.header_dark .header__main {
    background-color: #8ac858 !important;
}
.story__rating-up, .story__rating-down {
    cursor: pointer;
    width: 18px !important;
    height: 10px !important;
    position: relative;
    overflow: visible !important;
    -webkit-tap-highlight-color: transparent;
    display:block !important;
}
div[story__rating-up] {
    display: block !important;
    width: 100% !important;
    cursor: pointer;
    position: relative;
    overflow: visible;
    -webkit-tap-highlight-color: transparent;
}
div[story__rating-down]{
    display: block !important;
    width: 100% !important;
    cursor: pointer;
    position: relative;
    overflow: visible;
    -webkit-tap-highlight-color: transparent;
}
div[collapse-button]{
    display: block !important;
    width: 100% !important;
    cursor: pointer;
    position: relative;
    overflow: visible;
    -webkit-tap-highlight-color: transparent;
}
div[title]{
    display: none;
    width: 0px !important;
    height: 64px !important;
}
#branding_ad_footer {
    display: none !important;
    width: 0px !important;
    height: 0px !important;
}
