css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.google.com/search?") {

/*HEAD*/

#logo{display:none !important;}
table.ts{margin: 7px auto 3px !important;}
div.std{display:none !important;}

/*RESULTS*/
div#res{width:100% !important;}
div#ssb{background-color:#33AECF !important;color:#FFFFFF;height:3px !important;margin-top:0;overflow:hidden;padding:0.1em;}
ol {width:100% !important;}
ol li{margin:0 30px!important;width:543px; float:left !important;}

/*SEARCHBOX*/
.ts td input[type="text"]{padding: 3px !important; height: 30px !important; font-size: 24px !important; width: 495px !important;}
.ts td input[type="text"]:focus{border:solid 1px #33AECF !important;}
.ts td input[type="submit"]{font-size: 19px !important; width: 123px !important; position: relative !important; top: 1px !important; height: 39px !important; cursor: pointer;}

/*HEADERS*/
#res h3{font-size:21px !important;}
#res h3 a{color: #1783BF !important;}
#res h3 a:visited{color: #AF2076 !important;}

/*LINKS*/
#res .s a, #res .ts a{color: #369BAF !important;}

/*GOOGAZON*/
#blbottom{display:none !important;}
#blcontainer{padding:4px 0 4px 0 !important;}
#bltop {border-bottom:medium none !important;width: inherit !important;}
#blshowbtn{cursor: pointer; position: absolute; top: -19px; left: 11px;}
#bltop, #blbottom {border-top:1px solid #33AECF !important;padding:2px !important; margin-left:0px !important;}
#bltop {height:0 !important;margin-bottom:0 !important;position:relative;}
#bltop span em, #blresultsbox, #sharebutton{display:none;}
.dopeyresult a{color:#18454F !important;}
#innercontainer a b{color:#369BAF !important;}




}
