css:
@-moz-document domain(pikabu.ru) {
	body {
		font-size: 13px !important;
		line-height: 20px !important;
		font-family: Tahoma, Helvetica, Arial, sans-serif !important;
	}
	.header {
		min-height: 49.5px !important;
	}
	.header_fixed {
		top: 0 !important;
	}
	.sidebar__inner_fixed {
		top: 54px !important;
	}
	.menu_vertical {
		line-height: 30px;
	}
	.story__scroll {
		top: 75.5px !important;
	}
	.header_dark .header__main {
		background: #8ac858 !important;
	}
	.header__main {
		height: 49.5px !important;
	}
	.header__inner {
		height: 49.5px !important;
	}
	.header-menu {
		height: 49.5px !important;
	}
	.header-menu__item a {
		line-height: 49.5px !important;
	}
	.header-menu__extra {
		top: 17px !important;
	}
	.story__header {
		padding: 9px 12px 0 !important;
	}
	.story__user-at-top {
		margin: -5px 13px 15px !important;
		display: inline-block !important;
	}
	.story__tags {
		margin-left: 12px !important;
		margin-right: 12px !important;
	}
	.story_tags-at-top .story__user-at-top + .story__tags {
		display: inline-block !important;
	}
	.story-block_type_text {
		padding: 0 14px !important;
	}
	.story-block_type_image {
		padding-left: 14px !important;
		padding-right: 14px !important;
	}
	.story-block_type_video {
		padding: 0 14px !important;
	}
	.story__content {
		line-height: 20px !important;
	}
	.story_collapse .story__user-at-top {
		display: none !important;
	}
	.story_collapse .story__header {
		padding: 7px 25px 0 !important;
		transition: padding .3s ease-out !important;
	}
	.story_collapse.story_tags-at-top .story__user-at-top + .story__tags {
		margin-bottom: 7px !important;
	}
	.player:hover .player__preview,
	.player__preview {
		transform: scale3d(1, 1, 1) !important;
	}
	section,
	.section,
	.static-page__container > h1,
	.static-page__container > a,
	.static-page__container > div,
	.static-page__more {
		padding: 10px 14px !important;
	}
	a,
	.link {
		color: #5190b8;
	}
	.avatar,
	.avatar_note .avatar__inner,
	.avatar img,
	.avatar__default {
		border-radius: 0 !important;
	}
	.story__tags .tags__tag {
		border: 1px solid #d7d4d4 !important;
		padding: 0 4px 2px !important;
		border-radius: 1px !important;
		margin-right: 4px !important;
		line-height: 19px !important;
	}
	.story__tags {
		line-height: 30px !important;
	}
	.story__footer {
		padding: 6px 25px !important;
		line-height: 16px !important;
	}
	.story__user {
		direction: rtl !important;
	}
	.user__info {
		direction: ltr !important;
	}
	.user__info_left .user__info-item {
		display: inline-block !important;
	}
	.story__user .avatar {
		width: 16px !important;
		height: 16px !important;
		margin-right: 7px !important;
	}
	.story__user .avatar_note .avatar__inner {
		line-height: 16px !important;
	}
	.story__user .avatar__default {
		background-position: -179px -290px !important;
		width: 16px !important;
		height: 16px !important;
		border-radius: 0 !important;
	}
	.collapse-button {
		color: #5190b8 !important;
		border: 1px solid #5190b8 !important;
	}
	.collapse-button:hover {
		background-color: #5190b8 !important;
		color: #fff !important;
	}
	.comments-navigator__refresh {
		border: 1px solid #5190b8 !important;
		text-align: center !important;
		line-height: 24px !important;
		padding: 0px 12px !important;
		margin-top: 1px !important;
		transition: background .3s ease-out !important;
	}
	.comments-navigator__refresh::after {
		content: "\f021";
		font-family: 'FontAwesome';
		color: #5190b8;
		font-size: 14px;
		transition: color .3s ease-out !important;
	}
	.comments-navigator__refresh svg {
		display: none !important;
	}
	.comments-navigator__refresh:hover {
		background: #5190b8 !important;
	}
	.comments-navigator__refresh:hover::after {
		color: #fff;
	}
}
