css:
/* i really want this to be global */
@-moz-document domain("virtualpopstar.com")
{

  body
  {
    background-color: #222;
    /*BG COLOUR: background: url(https://i.imgur.com/wSgyCWW.jpg) no-repeat center center fixed;*/
    /*BG SIMPLE:*/
    background: url(https://i.imgur.com/wSgyCWW.jpg) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    font-family: Calibri, 'PT Sans Narrow', Calibri;
    letter-spacing: 0.8pt;
  }

/*Inlogpagina*/
  .regbox
  {
    background-color: rgba(250,250,250,0.8);
    border-style: solid;
    border-width: 1px;
    border-color: white;
  }

  .bar_top,
.header_bg_overlay
  {
    display: none;
  }

  .top_menu_container
  {
    background-color: #0FD197;
    font-size: 11pt;
  }

  .block_content_title_small
  {
    font-family: Abel;
    font-size: 13pt;
    font-weight: 500;
  }

  .block_menu_single
  {
    background-image: url(https://i.imgur.com/gXDSGzV.png);
  }

  .block_menu_multi
  {
    background-image: url(https://i.imgur.com/Nm3TRBQ.png);
  }

  .block_menu_multi_active
  {
    background-image: url(https://i.imgur.com/3wp8ZVP.png);
  }

  .block_content_top_left,
.block_content_top_right
  {
    background-image: url(https://i.imgur.com/LAN3syP.png);
  }

  .block_content_top_center_small,
.block_content_top_center_med,
.block_content_title_mini,
.block_title,
.block_content_top,
input[type="submit"]
  {
    background-image: url(https://i.imgur.com/VepKcXn.png);
  }

  .block_content_bottom_mini,
.block_content_bottom_small,
.block_bottom.avatar,
.block_content_bottom_med,
.block_content_bottom
  {
    background-image: url(https://i.imgur.com/4uv3AYF.png);
  }

  .okButton
  {
    background-image: url(https://i.imgur.com/fSLWGHX.png);
  }

  .header_bg
  {
    background-image: url(https://i.imgur.com/85PxwBD.png);
  }

  input[type="submit"].dark
  {
    background-image: url(http://i.imgur.com/PlhDbWl.png);
  }

  .block_menu_single:hover
  {
    background-image: url(https://i.imgur.com/LJLqLej.png);
  }

  .block_menu_single.active
  {
    background-image: url(https://i.imgur.com/PHyOWfG.png);
  }

  .icons_main
  {
    background-image: url(http://i.imgur.com/KrrffJF.png);
  }

  .icons_games
  {
    background-image: url(http://i.imgur.com/DpIESuX.png);
  }

  .icons_clothing
  {
    background-image: url(http://i.imgur.com/Kvs8LXd.png);
  }

  .icons_career
  {
    background-image: url(http://i.imgur.com/NCQUDLU.png);
  }

  .icons_financial
  {
    background-image: url(http://i.imgur.com/EHQD8t0.png);
  }

  .icons_social
  {
    background-image: url(http://i.imgur.com/QMATeTu.png);
  }

/*Pagina waar ik ben*/
  .icons_main_active
  {
    background-image: url(http://i.imgur.com/ccryhjD.png);
  }

  .icons_games_active
  {
    background-image: url(http://i.imgur.com/Qooj4Sg.png);
  }

  .icons_clothing_active
  {
    background-image: url(http://i.imgur.com/lkbABgz.png);
  }

  .icons_career_active
  {
    background-image: url(http://i.imgur.com/AXgR2ms.png);
  }

  .icons_financial_active
  {
    background-image: url(http://i.imgur.com/8ER54NQ.png);
  }

  .icons_social_active
  {
    background-image: url(http://i.imgur.com/AXgR2ms.png);
  }
}
