css:
@-moz-document url-prefix("https://www.smogon.com/forums/") {
.p-nav {
    color: #cc0202;
    background: #464646;
}
.p-header {
    color: #41045b;
    background: #2d2d2d;
}
.block-header {
    padding: 6px 10px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 15px;
    color: #fefefe;
    font-weight: 700;
    background: #6363B0;
    border-bottom: 1px solid #dfdfdf;
    background: linear-gradient(0deg, #41045b, #15b5b5);
}
.subNodeLink.subNodeLink--unread {
    font-weight: none;
}
.subNodeLink.subNodeLink--unread {
    font-weight: none;
}
.block-container {
    color: #ffffff;
    background: #15b5b5;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #15b5b5;
    border-bottom-color: #2e79f7;
    border-left-color: #15b5b5;
    border-radius: 4px;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: border margin;
    transition-property: border margin;
}
a {
    color: #a3c3fb;
    text-decoration: none;
}
.block-minorHeader {
    padding: 6px 10px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 17px;
    color: #ff00a1;
}
.pairs>dt {
    padding: 0;
    margin: 0;
    color: #b9b9b9;
}
html {
    background: #41045b;
    font: 15px / 1.4 sans-serif;
    font-family: 'Segoe UI', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    color: #d4d4d4;
    margin: 0;
    padding: 0;
    word-wrap: break-word;
}
.contentRow-minor {
    font-size: 13px;
    color: #dcdcdc;
}
.node-extra-row {
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    text-overflow: ellipsis;
    color: #dcdcdc;
}
.p-footer {
    font-size: 13px;
    color: #fefefe;
    background: #2d2d2d;
}
.p-nav-list .p-navEl.is-selected {
    color: #bf0101;
    font-weight: 600;
    background: #252525;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}
.p-nav-list .p-navEl {
    color: #bf0101;
    text-decoration: none;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    border-right: 1px solid #cecece;
}
a:hover {
    color: #c6d9fb;
}
.p-sectionLinks {
    font-size: 13px;
    color: #ffffff;
    background: #252525;
    border-bottom: 1px solid #cecece;
}
.block-textHeader {
    margin: 3px 0;
    padding: 0;
    font-weight: 400;
    font-size: 17px;
    color: #adade2;
}
.block-footer {
    padding: 6px 10px;
    font-size: 12px;
    color: #d0d0d0;
    background: #0000007a;
    border-top: 1px solid #dfdfdf;
}
.block--messages .message,
.block--messages .block-row {
    color: #caeaf9;
    background: #7b7b7b5c;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    border-radius: 4px;
}
.message-cell.message-cell--user,
.message-cell.message-cell--action {
    position: relative;
    background: #131313;
    border-right: 1px solid #d8d8d8;
    min-width: 0;
}
.message-attribution {
    color: #d6d6d6;
    font-size: 12px;
    padding-bottom: 3px;
    border-bottom: 1px solid #e7e7e7;
}
.message-responseRow {
    margin-top: -1px;
    background: #1d1d1d;
    border: 1px solid #6b6b6b;
    padding: 6px;
}
.memberHeader-content {
    padding: 10px;
    padding-left: 212px;
    background: #131313;
}
.memberHeader-name {
    margin: 0;
    margin-top: -.15em;
    padding: 0;
    font-weight: 400;
    font-size: 24px;
    color: #ff4949;
}
.block-tabHeader {
    padding: 0;
    margin: 0;
    font-weight: 400;
    font-size: 15px;
    color: #fefefe;
    background: #2c2c2c;
    border-bottom: 1px solid #d8d8d8;
    font-size: 0;
}
.block-tabHeader .tabs-tab.is-active {
    background: none;
    color: #6b9ffb;
    border-color: #6b9ffb;
}
.button.button--link,
a.button.button--link {
    background: #2c2c2c;
    color: #6b9ffb;
    border-color: #6b9ffb;
}
.bbCodeBlock {
    margin: .75em 0;
    background: #131313;
    border: 1px solid #dfdfdf;
    border-left: 3px solid #ffd190;
}
.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 13px;
    color: #ffd190;
    background: #131313;
}
.likesBar {
    display: none;
    opacity: 0;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: all, -xf-opacity;
    transition-property: all, -xf-opacity;
    overflow-y: hidden;
    height: 0;
    -webkit-transition-property: all, -xf-height;
    transition-property: all, -xf-height;
    background: #131313;
    border: 1px solid #dfdfdf;
    border-left: 2px solid #f77b8c;
    padding: 6px;
    font-size: 12px;
    margin-top: 6px;
}
.pageNav-page:hover {
    background: #000;
}
.pageNav-page {
    display: table-cell;
    background: #131313;
    color: #9cc0ff;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    font-size: 13px;
    white-space: nowrap;
}
.pageNav-page.pageNav-page--current {
    background: #3c3327;
    color: #f9ce93;
    border: 1px solid #f9d4a0;
    cursor: pointer;
}
.pageNav-page.pageNav-page--current:hover {
    background: #332c21;
    color: #f9d4a0;
}
.pageNav-jump {
    display: inline-block;
    background: #131313;
    color: #8ac0ff;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 5px 8px;
}
.pageNav-jump:hover {
    background: #000;
}
.block-filterBar {
    padding: 6px 10px;
    font-size: 13px;
    color: #fefefe;
    background: #BEBEE7;
    border-bottom: 1px solid #cbcbcb;
    background: linear-gradient(0deg, #313131, #616161);
}
.structItem-minor a {
    color: #d2e1ef;
    text-decoration: none;
}
.structItem-pageJump a {
    color: #e7e7e7;
    background: #131313;
    border: 1px solid #e7e7e7;
    text-decoration: none;
    border-radius: 2px;
    padding: 0 3px;
    opacity: .5;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}
.structItem-pageJump a:hover {
    text-decoration: none;
    background: #080808;
}
.menu-header {
    padding: 6px 15px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 15px;
    color: white;
    font-weight: 700;
    border-bottom: 1px solid #dfdfdf;
    background: linear-gradient(0deg, #404040, #a2a2a2);
}
.menu-row.menu-row--clickable:hover {
    background: #222222;
}
.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #dfdfdf;
    background: #404040;
    border-radius: 3px;
    border-top: 3px solid #6363b0;
}
.menu-footer {
    padding: 6px 15px;
    font-size: 12px;
    color: #505050;
    background: #f5f5f5;
    border-top: 1px solid #dfdfdf;
    background: linear-gradient(180deg, #606060, #696969);
}
label.iconic>input+i:before {
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-size: 120%;
    height: .9em;
    color: #b0b0ef;
    vertical-align: -.1em;
    visibility: visible;
    text-align: left;
}
label.iconic>input+i:before:hover {
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-size: 120%;
    height: .9em;
    color: #c3c3f7;
    vertical-align: -.1em;
    visibility: visible;
    text-align: left;
}
.menu-row.menu-row--alt {
    color: #e4e4e4;
    background: #2c2c2c;
}
.block-row.block-row--alt {
    color: #dfdfdf;
    background: #222222;
}
.blockLink.is-selected:hover {
    font-weight: 700;
    background: #0e0e0e;
    border-left: 3px solid #9f9fe8;
    padding-left: 7px;
}
.blockLink.is-selected {
    font-weight: 700;
    background: #151515;
    border-left: 3px solid #9f9fe8;
    padding-left: 7px;
}
.blockLink.is-selected {
    font-weight: 700;
    background: #151515;
    border-left: 3px solid #9f9fe8;
    padding-left: 7px;
}
.blockLink:hover {
    background: #151515;
}
.menu-linkRow {
    display: block;
    padding: 6px 15px 6px 12px;
    border-left: 3px solid transparent;
    color: #dfdfdf;
    text-decoration: none;
}
a.menu-linkRow.u-indentDepth0.js-offCanvasCopy:hover {
    background: #6d6d6d;
    color: #fff;
    border-left: 3px solid #000;
}
.notice.notice--primary {
    color: #cbcbcb;
    background: #252525;
}
.tooltip--member .tooltip-content {
    color: #dfdfdf;
    background: #3f3f3f;
    padding: 0;
    text-align: left;
    border: 1px solid #d8d8d8;
    box-shadow: 0 5px 10px 0 rgba(177, 177, 177, 0.35);
}
.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 6px;
    color: #dfdfdf;
    background: #fefefe;
    border-bottom: 1px solid #dfdfdf;
    background: linear-gradient(0deg, #131313, #464646);
}
.message-signature {
    margin-top: 10px;
    font-size: 12px;
    color: #dcdcdc;
    border-top: 1px solid #dfdfdf;
    padding-top: 3px;
    height: 125px;
    overflow: hidden;
}
.memberTooltip-name {
    margin: 0;
    margin-top: -.15em;
    padding: 0;
    font-weight: 400;
    line-height: 1.12;
    font-size: 24px;
    color: #cfcff9;
}
.fr-toolbar {
    color: #141414;
    background: #f5f5f5;
    position: relative;
    z-index: 4;
    font-family: 'Segoe UI', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    padding: 0 1px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    text-align: left;
    border: 1px solid #cbcbcb;
    border-top: 3px solid #cbcbcb;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 1.2;
}
.menu-row.menu-row--highlighted {
    color: #dfdfdf;
    background: #36414e;
}
.menu-row.menu-row--highlighted:hover {
    background: #475463;
}
.menu-linkRow:hover {
    background: #2d2d2d;
    color: #c6e0fb;
}
.formRow>dt {
    border-right: 1px solid transparent;
    background: #222222;
    border-color: #dfdfdf;
    text-align: right;
    width: 33%;
    padding: 15px 10px 15px 10px;
}
.formSubmitRow-bar {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: #000000;
    border-top: 1px solid #cbcbcb;
}
.memberOverviewBlock-seeMore {
    background: #191919;
    border: 1px solid #dfdfdf;
    padding: 3px;
    text-align: center;
    margin-top: auto;
}
.avatar img {
    background-color: #fefefe0d;
}
.block-container {
    color: #ffffff;
    background: #1d1d1d;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: border margin;
    transition-property: border margin;
}
.blockMessage {
    margin-bottom: 20px;
    padding: 6px 10px;
    color: #f98b8b;
    background: #291c1c;
    border-width: 1px;
    border-radius: 5px;
    border-style: solid;
    border-top-color: #ff4545;
    border-right-color: #ff4545;
    border-bottom-color: #ff4545;
    border-left-color: #ff4545;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: border margin;
    transition-property: border margin;
}
.memberHeader-content.memberHeader-content--info {
    background: #2c2c2c;
}
.bbCodeBlock-expandLink {
    display: none;
    position: absolute;
    top: 87px;
    left: 0;
    right: 0;
    height: 75px;
    cursor: pointer;
    background: transparent;
    background: linear-gradient(to bottom, rgba(60, 60, 60, 0.45) 0%, #1f1f1fb8 80%);
}
}
