css:
@-moz-document domain("lifehacker.com"), domain("avclub.com"), domain("earther.com"), domain("gizmodo.com"), domain("jalopnik.com"), domain("jezebel.com"), domain("kotaku.com"), domain("splinternews.com"), domain("thetakeout.com"), domain("theroot.com"), domain("theonion.com"), domain("kinja.com"), domain("clickhole.com") {
.pe_newlayout .sidebar {
    display: none;
    width: 0;
}

.pe_newlayout .main {
    width: 100%;
    border-left: none;
}

.pe_newlayout .main__content {
	float: none;
}

.global-nav--permalink .topnav__logostrip.topnav__logostrip--sectionnav, .global-nav--scrollback .topnav__logostrip.topnav__logostrip--sectionnav {
	justify-content: center;
}

.postlist__item .excerpt {
	width: 100%;
}

.site-footer {
    margin-bottom: 0 !important;
}

.reply__sidebar {
    right: 0px;
}

.reply__header .post-dropdown-ct {
	right: -32px;
    top: 0px;
}

.sharingfooter, .instream-native-video, .footer-subscribe {
	display: none;   
}

.editor .editor-inner .google-adx, .editor .editor-inner .inset--story, .editor .editor-inner .instream-permalink, .editor .editor-inner .pullquote, .editor .editor-inner .quotable, .editor .editor-inner .referenced-fullwidth, .editor .editor-inner > span, .editor .editor-inner blockquote, .editor .editor-inner figure, .editor .editor-inner h1, .editor .editor-inner h2, .editor .editor-inner h3, .editor .editor-inner h4, .editor .editor-inner h5, .editor .editor-inner h6, .editor .editor-inner hr, .editor .editor-inner ol, .editor .editor-inner p, .editor .editor-inner pre, .editor .editor-inner ul, .post-content .google-adx, .post-content .inset--story, .post-content .instream-permalink, .post-content .pullquote, .post-content .quotable, .post-content .referenced-fullwidth, .post-content > span, .post-content blockquote, .post-content figure, .post-content h1, .post-content h2, .post-content h3, .post-content h4, .post-content h5, .post-content h6, .post-content hr, .post-content ol, .post-content p, .post-content pre, .post-content ul, .author-bio__wrapper, .replies-wrapper, .discussion-header, .related-module, .reply__content {
    max-width: 800px;
}
}
