 .xenForm .ctrlUnit.fullWidth > dd > input,
 .xenForm .ctrlUnit.submitUnit > dd > input,
 .xenForm .ctrlUnit.fullWidth > dd > select,
 .xenForm .ctrlUnit.submitUnit > dd > select,
 .xenForm .ctrlUnit.fullWidth > dd > textarea,
 .xenForm .ctrlUnit.submitUnit > dd > textarea,
 .xenForm .ctrlUnit.fullWidth > dd > ul .xenForm .ctrlUnit.submitUnit > dd > ul {
     line-height: 1px;
 }
 /* Header section */
 
 div#logo a img {
     display: none;
 }
 div#logo a span {
     background-image: url("https://i.imgur.com/EV1K64y.png");
     width: 220px;
     height: 80px;
     background-repeat: no-repeat;
     display: block;
     background-size: 220px 76px;
     position: relative;
     bottom: 0px;
 }
 #content {
     background: url("https://i.imgur.com/KDBvBH9.png");
     background-repeat: no-repeat;
     background-color: rgb(30, 30, 30);
              
 }
 .message .messageContent {
     background: url("https://i.imgur.com/d8W6jRx.png");
     background-position: right bottom;
     background-repeat: no-repeat;
     background-size: 28px 28px;
 }
 .titleBar::after {
     height: 65px;
     width: 924px;
     background-repeat: no-repeat;
     background-size: contain;
     background-position: right bottom;
     display: block;
     position: absolute;
     visibility: hidden;
     float: right;
     margin-top: -57px;
 }
 #thread-53937 h3::after {
     content: " -Humble begginings!";
     font-size: 10px;
              z-index:-1;
 }
 

.section.membersOnline.userList h3 a {
display:none;
}
.sidebar div.section.membersOnline.userList h3:nth-child(1)::after {
 content: "Foxes Online Now";   
}
.sidebar .visitorPanel .secondaryContent span {
 display:none;
}
.sidebar .visitorPanel .secondaryContent h3::after {
 content: "Artic Fox Empire Status";
    width:50px;
    height:10px;
}
}

