css:
@-moz-document domain("soundcloud.com") {

    /* COLORS */

    /** DESCRIPTION : set the colors **/
    :root {
        --body-bg: #F0F1F5 !important;
        --card-bg: #FFF !important;
        --card-bg-2: #F9F9F9 !important;
        --card-bg-op: rgba(240, 241, 245, .98) !important;
        --border-color: #EBEBEB !important;
        --primary-color: #333 !important;
        --secondary-color: #7E7E7E !important;
        --filter: invert(0) !important;
    }

    /* GENERAL */

    /** DESCRIPTION : change the elements of all page **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド", "BM YEONSUNG" !important;
    }

    *::-moz-focus-inner {
        border: 0;
    }

    body,
    #app {
        background: var(--body-bg) !important;
    }

    body {
        overflow-x: hidden;
    }

    select {
        -webkit-appearance: none;
        -moz-appearance: none;
        text-indent: 0px;
        text-overflow: ellipsis;
        white-space: nowrap;
    }

    .l-fluid-fixed {
        width: 90% !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: -50px auto !important;
    }

    .l-fluid-fixed .sc-border-light-right {
        border-right: none !important;
    }

    .l-container.l-content {
        padding-top: 110px !important;
    }

    .accountSettings .sc-button-medium::before,
    .autocompleteItem__queryIcon,
    .chartTracks .sc-ministats-medium.sc-ministats-plays::before,
    .ownActivity__content .commentForm__avatar,
    .commentItem.editing.m-isReply .commentForm__avatar,
    .creatorsNavigation__onSoundCloud,
    .dashbox__wrapper,
    .dialog__arrow,
    .dropbar__content,
    .gritter-image,
    .g-dark-bg .userBadge.m-horizontal .userBadge__meta,
    .g-dark .sc-icon-medium.left.commentTitle__icon.sc-icon-comment-light,
    .g-tabs-link[href*="/stats"],
    .g-tabs-link[href*="/pro"],
    .g-tabs-link[href*="/pulse"],
    .header__link.header__goUpsell,
    .hintButton.sc-button.sc-button-small,
    .historicalPlays__item .compactTrackListItem__plays,
    .historicalPlays__item .sc-ministats-group,
    .historicalPlays__item .soundActions__purchaseLink,
    .image.m-station::before,
    .l-collection .playableTile__descriptionContainer .sc-icon.sc-icon-large,
    .l-collection .sound__footerRight,
    .l-fluid-fixed .l-main .ownActivity__title,
    .l-fluid-fixed .l-main .ownActivity.large .userBadge__meta,
    .l-fluid-fixed .l-main .sc-icon-medium.sc-icon-comment,
    .l-footer,
    .listenArtistInfo__copyright,
    .l-listen-wrapper .l-about-row .g-link-collapse.collapsed::after,
    .l-listen-wrapper .l-about-row .g-link-collapse::after,
    .l-listen-wrapper .l-about-row .sc-button-medium::before,
    .l-listen-wrapper .l-about-row .soundActions__purchaseLink,
    .l-listen-wrapper .truncatedAudioInfo__wrapper::after,
    .l-listen-wrapper .userBadge__stats,
    .truncatedUserDescription.m-overflow .truncatedUserDescription__collapse:after,
    .l-main .userNetwork .sc-button-share,
    .l-messages-left .inbox__item::before,
    .l-messages-main .sc-button-small.sc-button-icon:not(.sc-button-stretch-icon)::before,
    .l-messages-main .textfield__label.g-required-label,
    .l-sidebar-right .sidebarModule .sc-ministats-group,
    .l-sidebar-right .sidebarModule .soundBadge.compact .soundBadge__additional,
    .l-station-hero.l-inner-fullwidth .sc-button-small.sc-button-like::before,
    .l-vertical-bar .sc-button-medium.sc-button-medium::before,
    .l-three-column.listen-about .listenInfo .listenInfo__copyright,
    .l-three-column.listen-about .listenInfo .listenInfo__releaseTitle,
    .l-three-column.listen-about .sc-ministats-group,
    .modal__modal .soundTitle__tagContainer,
    .l-listen-wrapper .commentsList .sc-clearfix .sc-icon,
    .l-three-column .commentsList .sc-clearfix .sc-icon,
    .l-search .l-fixed-left .searchOptions__footer,
    .l-search .l-fixed-left .g-nav-item.g-nav-item-sounds.searchOptions__navigation-high_tier.searchOptions__navigationItem,
    .l-search .l-fixed-left .sc-icon,
    .moreMenu__link[href="/mobile"],
    .moreMenu__link[href="/you/premier"],
    .moreMenu__link.moreMenu__goUpsell,
    .moreMenu__link[href*="jobs"],
    .moreMenu__link[href*="developers"],
    .moreMenu__link[href="/you/subscriptions"],
    .moreMenu__link[href*="blog"],
    .paging-eof,
    .premiumIconLink,
    .profileMenu__link::after,
    .profileMenu__link.profileMenu__premium,
    .queue__hide.sc-button.sc-button-nostyle.sc-ir,
    .queue__itemWrapper.queue__fallback,
    .quotaBar__button,
    .sc-button-medium.sc-button-camera::before,
    .sc-button-small.sc-button-icon:not(.sc-button-stretch-icon)::before,
    .sc-button-small.sc-button-reply::before,
    .sc-promoted-icon.sc-promoted-icon-small.sc-text-light,
    .sc-snippet-badge.sc-snippet-badge-medium.sc-snippet-badge-grey,
    .sc-tag::before,
    .searchPremiumContentFooter,
    .searchList__item .compactTrackListItem__plays,
    .searchList__item .sc-button-small::before,
    .searchList__item .sc-ministats-group,
    .searchList__item .soundActions__purchaseLink,
    .sidebarInfoBox__body,
    .soundList__item .commentForm__wrapper.commentForm__transition,
    .soundList__item .compactTrackListItem__plays,
    .soundList__item .sc-button-small::before,
    .soundList__item .sc-ministats-group,
    .soundList__item .soundActions__purchaseLink,
    .soundList__item .waveform__scene.waveform__layer canvas:nth-child(2),
    .soundList__item .waveform__scene.waveform__layer canvas:nth-child(3),
    .soundList__item .commentPopover,
    .soundList__item .commentForm.commentForm__transition.m-small.visible,
    .historicalPlays__item .commentForm.commentForm__transition.m-small.visible,
    .searchList__item .commentForm.commentForm__transition.m-small.visible,
    .historicalPlays__item canvas:nth-child(2),
    .historicalPlays__item canvas:nth-child(3),
    .historicalPlays__item .commentPopover,
    .searchList__item canvas:nth-child(2),
    .searchList__item canvas:nth-child(3),
    .searchList__item .commentPopover,
    .historicalPlays__item .commentForm__wrapper.commentForm__transition,
    .searchList__item .commentForm__wrapper.commentForm__transition,
    .stationHeader__type::before,
    .systemPlaylistDetails__controls .sc-button-medium.sc-button-queue::before,
    .systemPlaylistDetails__controls .sc-button-medium.sc-button-addtoset::before,
    .historicalPlays__item .sc-button-small::before,
    .tagsMain .tabs__tabs,
    .textfield__clear,
    .trackItem:not(.m-disabled).hover .trackItem__additional,
    .trackItem:not(.m-disabled).active .trackItem__additional,.trackItem__additional,.truncatedAudioInfo__license,.truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after,.userMain .dropbar,.userMain__content .sound__footerRight {
        display: none !important;
    }

    .emptyNetworkPage__headline,
    .inSetsList__empty *,
    .searchList__emptyText,
    .trackManager__noTracksHeadline,
    .trackManager__noTracksAction,
    .waveformWrapper.m-empty .waveformWrapper__empty {
        color: var(--primary-color) !important;
        font-size: 16pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .emptyNetworkPage__subheadline,
    .emptyNetworkPage__link {
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: normal !important;
        margin-top: 8px;
    }

    .sc-type-h3.emptyNetworkPage__action {
        font-size: 16pt !important;
        font-stretch: condensed !important;
    }

    .gritter-item-wrapper {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    .gritter-item {
        background: var(--card-bg) !important;
        border: none !important;
        box-shadow: none !important;
        padding: 15px !important;
    }

    .gritter-item * {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    /* POPUP */

    /** DESCRIPTION : change the elements of the popup page **/
    .modal.modalWhiteout {

        /*** POPUP BACKGROUND ***/  
        background: var(--card-bg-op);
    }

    .modal.modalWhiteout > .modal__closeButton {
        filter: brightness(1) var(--invert) !important;
    }

    .g-modal-section,
    .modal__content .tabs__tabs {

        /*** POPUP SHARE ***/
        background: var(--card-bg);
        border-radius: 2px !important;
    }

    .modal__content .tabs__tabs .g-tabs.g-tabs-large,
    .modal__content .tabs__tabs .g-tabs-link:hover {
        border: none;
        color: #FCA65F !important;
    }

    .modal__content .tabs__tabs .g-tabs-link.active {
        border: none;
        color: #FCA65F !important;
    }

    .modal__content .tabs__tabs .g-tabs.g-tabs-large * {
        color: var(--primary-color);
        font-size: 18pt;
        font-stretch: condensed;
    }

    .modal__content .shareLink.m-fromDisabled .shareLink__field,
    .modal__content .widgetCustomization__textInput,
    .widgetCustomizationColor__colorInput,
    #widgetCustomization__optionSize {
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color);
        border-radius: 1px !important;
        box-shadow: none;
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: normal !important;
        padding: 10px !important;
    }

    .modal__content .g-modal-section *:not(.widgetCustomizationColor__colorBox) {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .modal__modal {

        /*** POPUP MESSAGE/BLOCK/REPORT/SETTINGS ***/
        background: var(--card-bg) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .3) !important;
        margin: 100px auto !important;
    }

    .newMessageForm__title.g-modal-title-h1,
    .blockUser .g-modal-title-h1.sc-truncate,
    .reportUser .g-modal-title-h1.sc-truncate,
    .profileSettings .g-modal-title-h1.sc-truncate,
    .profileSettings .g-form-section-head *,
    .keyboardShortcuts .g-modal-title-h2 {

        /*** POPUP MESSAGE/BLOCK/REPORT/SETTINGS TITLE ***/
        border: none;
        color: var(--primary-color);
        font-size: 18pt !important;
        font-stretch: condensed;
    }

    .modal__modal .composeMessage__toolbar.sc-clearfix {
        background: var(--card-bg);
    }

    .modal__modal .composeTextfield *,
    .modal__modal .newMessageForm__recipient *,
    .blockUser *,
    .reportUser *,
    .profileSettings *,
    .keyboardShortcuts * {

        /*** POPUP MESSAGE/BLOCK/REPORT/SETTINGS CONTENT ***/
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: normal !important;
        line-height: 18pt;
    }

    .modal__modal .recipientChooser .tokenInput__wrapper,
    .modal__modal .composeTextfield .textfield__input,
    .shareContent__link input,
    .profileSettings input,
    .profileSettings textarea {

        /*** POPUP MESSAGE/BLOCK/REPORT/SETTINGS INPUT/TEXTAREA ***/
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        color: var(--primary-color) !important;
        font-weight: normal !important;
        padding: 10px !important;
    }

    .keyboardShortcuts__shortcutsGroup > dl > dt kbd kbd {
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        font-weight: normal;
        padding: 2px;
    }

    #tokenInput__recipients {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color);
        border-radius: 1px !important;
    }

    .modal__modal .composeMessage__sendButton.sc-button-cta.sc-button.sc-button-medium {

        /*** POPUP MESSAGE/BLOCK/REPORT/SETTINGS BUTTONS ***/
        background: #FF9F55 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        height: 34px !important;
        width: auto !important;
        padding-right: 15px !important;
        padding-left: 15px !important;
        text-transform: uppercase !important;
    }

    .blockUser__actions button,
    .profileSettings__formButtons.sc-button-toolbar.sc-border-light-top button,
    .webProfilesControl__add.sc-button.sc-button-medium {
        background: #FF9F55 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        height: 34px !important;
        width: auto !important;
        padding-right: 15px !important;
        padding-left: 15px !important;
        text-transform: uppercase !important;
    }

    .imageChooser__chooseButton.sc-button.sc-button-translucent.sc-button-camera.sc-button-medium {
        background: #FF9F55 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        height: 30px !important;
        width: auto !important;
        padding-right: 15px !important;
        padding-left: 15px !important;
        text-indent: 0px;
        text-transform: uppercase !important;
    }

    .modal__modal .tokenInput__token {

        /*** POPUP MESSAGE/BLOCK/REPORT/SETTINGS OTHERS ***/
        background: var(--card-bg) !important;
        border-radius: 1px !important;
        border: 1px solid var(--border-color) !important;
        padding: 5px !important;
    }

    .modal__modal .tokenInput__tokenRemove.sc-ir {
        color: transparent !important;
    }

    .blockUser .sc-checkbox-check {
        margin-top: 6px !important;
    }

    .profileSettings .textfield.permalinkTextfield.medium {
        margin-bottom: 20px !important;
    }

    .permalinkTextfield__input {
        width: 380px !important;
    }

    .sc-button.sc-button-edit.sc-button-icon.permalinkTextfield__editButton.sc-button-medium {
        background: transparent !important;
        border: none !important;
        margin-top: 9px !important;
        margin-right: 6px !important;
    }

    .compactTrackListItem.sc-media.sc-border-light-bottom,
    .profileSettings .g-form-section-head,
    .profileSettings__formButtons.sc-button-toolbar.sc-border-light-top {
        border: none;
    }

    .dialog {

        /*** POPUP MORE ***/
        background: var(--card-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .3) !important;
    }

    .dialog a {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
    }

    .dialog p {
        color: var(--secondary-color);
        font-size: 14pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
    }

    .queue,
    .queue__itemsHeight,
    .queue__itemWrapper {

        /*** POPUP QUEUE ***/
        background: var(--card-bg-2);
    }

    .queue__panel {
        border-bottom: 1px solid var(--border-color);
        color: var(--primary-color) !important;
    }

    .queue__panel * {
        font-size: 17pt !important;
        font-stretch: condensed;
        font-weight: normal;
    }

    .queue__clear.sc-button.sc-button-medium {
        background: #FF9F55 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        height: 34px !important;
        width: auto !important;
        padding-right: 15px !important;
        padding-left: 15px !important;
        text-transform: uppercase !important;
        margin-top: -1px !important;
        margin-right: -10px !important;
    }

    .queue__itemWrapper {
        border-top: 1px dotted var(--border-color) !important;
    }

    .queueItemView:hover,
    .queueItemView.m-active {
        background: var(--card-bg) !important;
    }

    .queue__itemWrapper .queueItemView .queueItemView__details {
        overflow: visible !important;
        opacity: 1 !important;
        height: inherit !important;
    }

    .queue__itemWrapper .queueItemView * {
        color: var(--primary-color) !important;
        font-size: 12pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    .header {

        /*** HEADER CONTAINER ***/
        background: #FCA65F !important;
    }

    .header__logo {
        background: #EB6361 !important;
    }

    .header li a {

        /*** HEADER LINKS ***/
        color: #FFF !important;
    }

    .header__navMenu > li > a {
        border-right: none !important;
        line-height: 21px !important;
    }

    .header__navMenu > li > a.selected,
    .uploadButton.selected {
        background: transparent;
    }

    .header__mainMenu-loggedInHome,
    .header__mainMenu-collection,
    .uploadButton__title,
    .userNav__username.sc-truncate {
        color: #FFF;
        border-right: none !important;
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: bold;
        line-height: 21px !important;
        text-transform: uppercase;
    }

    .header__userNavUsername {
        color: #FFF !important;
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        text-transform: uppercase !important;
        margin-top: -1px !important;
    }

    .left.header__navWrapper * {
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        text-transform: uppercase !important;
    }

    .header__collection-wrapper {
        background: #0C545C !important;
        padding-left: 10px !important;
        padding-right: 10px !important;
        margin-right: -1px !important;
    }

    .headerSearch {

        /*** HEADER SEARCHBAR ***/
        background: #F0F1F5 !important;
        padding-bottom: 9px !important;
        margin-right: 10px !important;
        margin-left: 1px !important;
    }

    .headerSearch__input.sc-input {
        background: inherit !important;
        border-radius: 1px !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        padding-bottom: 8px !important;
    }

    .newItemBadge.newItems__some {
        border: 1px solid transparent !important;
    }

    .autocompleteItem {
        background: #212121 !important;
        padding: 5px !important;
        margin-bottom: -10px !important;
    }

    .autocompleteItem * {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        line-height: 16pt !important;
    }

    .autocompleteItem__title {
        font-weight: normal !important;
    }

    .searchMenu__searchForText {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .autocompleteItem:last-child {
        border-bottom-left-radius: 2px !important;
        border-bottom-right-radius: 2px !important;
    }

    .userNav__item.selected,
    .userNav__button.selected,
    .header__navMenu > li > a.header__moreButton.selected {

        /*** HEADER SECTION ***/
        background-color: #3C3741;
    }

    .unreadConversations.g-dark,
    .profileMenu__list,
    .g-dark-bg {
        background-color: #3C3741 !important;
        border-bottom-right-radius: 2px !important;
        border-bottom-left-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .3) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 20px !important;
        margin-left: 0px !important;
        margin-right: 1px !important;
    }

    .dropdownMenu,
    .profileMenu.g-dark {
        min-width: 200px !important;
    }

    .conversationBadge__imageDeleted {
        width: 40px !important;
        height: 40px !important;
    }

    .notifications.g-dark .notifications__list .conversationBadge * {
        color: #FFF !important;
    }

    .conversationBadge__missingUsername,
    .conversationBadge__time,
    .conversationBadge__body.sc-truncate {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .profileMenu__list.profileMenu__listener.sc-list-nostyle {
        margin-bottom: -40px !important;
    }

    .header__userNav {
        margin-left: 12px !important;
    }

    .g-dark-list {
        background-color: transparent !important;
    }

    .profileMenu__link {
        padding: 8px !important;
    }

    .profileMenu__link:hover,
    .profileMenu__link:focus {
        background: transparent !important;
    }

    .notifications__viewAll.sc-type-h3.latestActivities__viewAll,
    .notifications__viewAll.sc-type-h3.outgoing-messages.headerMessages__viewAll,
    .profileMenu__list:not(:last-child),.moreMenu__list,.profileMenu__link.profileMenu__trackManager {
        border: none;
        font-size: 14pt !important;
    }

    .g-dark-bg .sc-clearfix.sc-type-light.sc-truncate {
        margin-top: -10px !important;
        margin-left: 4px !important;
    }

    .g-dark-bg .userBadge .userBadge__title *,
    .g-dark-bg .userBadge .userBadge__title .sc-truncate {
        font-size: 14pt !important;
        font-weight: normal !important;
    }

    .g-dark-bg .ownActivity__content * {
        font-size: 14pt !important;
        font-weight: normal !important;
    }

    .g-dark .ownActivity .ownActivity__time {
        font-size: 14pt !important;
        font-weight: normal !important;
        top: 5px !important;
        right: 2px !important;
    }

    /* PLAYER */

    /** DESCRIPTION : change the elements of the player footer **/
    .playControls {
        bottom: 0px !important;
    }

    .playControls .playControls__inner {

        /*** PLAYER CONTAINER ***/
        background-color: var(--card-bg) !important;
        border-top: 1px solid var(--border-color) !important;
        padding: 10px 0px !important;
    }

    .playControls__bg {
        background-color: transparent;
        border-top: none;
    }

    .skipControl__previous {

        /*** PLAYER PREVIOUS BUTTON ***/
        background: no-repeat url("https://i.imgur.com/uNuXhE0.png") center !important;
        background-size: 17px auto !important;
        filter: var(--filter) !important;
    }

    .playControl[title="Play current"] {

        /*** PLAYER PLAY BUTTON ***/
        background: no-repeat url("https://i.imgur.com/QUuScCg.png") center !important;
        background-size: 24px auto !important;
