css:
@-moz-document domain('isis.tu-berlin.de'), domain('shibboleth.tubit.tu-berlin.de') {

/* GERERAL RULES */

* {
    color: #ccc !important;
    background-color: transparent !important;
    text-shadow: none !important;
    border-radius: 0 !important;
    border: none !important;
    box-shadow: none !important;
    background-image: none !important;
    background-repeat: no-repeat !important;
}

body {
    background: #222 !important;
}

/* HEADER RULES */

nav {
    height: auto !important;
}
#head {
    margin: 0 !important;
}
nav.navbar-inner {
    background: #333 !important;
    position: static !important;
}
nav.breadcrumb-nav, .header .title *, #navbar-button, .moodle-dialogue-hd {
    background-color: /*[[color]]*/ !important;
}
nav.breadcrumb-nav {
    padding: 0 !important;
}
#rightheader > a > img {
    display: none !important;
}
ul.menu, .popover {
    background: #444 !important;
}
ul.menu a:hover {
    background: #555 !important;
}
#navbar-button {
    height: 100% !important;
    text-transform: uppercase;
}
.nav-collapse {
    background: #333 !important;
}

/* INTERFACE SPECIFIC RULES */

.content {
    margin: 0 !important;
}
.moodle-dialogue-bd {
    background: #333 !important;
    height: auto !important;
}
.block:not(.block_course_overview), .section, .que {
    background: #333 !important;
    margin: 12px 0 !important;
}
input, button, select, div[class^="fp-btn"], .fp-viewbar {
    background: #444 !important;
    margin: 4px !important;
}
input:hover, button:hover, select:hover, div[class^="fp-btn"]:hover {
    background: #555 !important;
}
.section table, .section tr, .section  td {
    border: 2px solid #444 !important;
}
table.ygtvtable, tr.ygtvrow, td.ygtvcell {
    border: none !important;
}
.closebutton {
    height: 16px !important;
    width: 16px !important;
}
.closebutton:before {
    content: "\274C";
}
div[class^="fp-btn"] {
    display: block !important;
}

/* ICON RULES */

#page img:not(.icon):not(.activityicon):not(.userpicture):not([name^="check_icon_"]),
#page svg:not(.icon):not(.activityicon):not(.userpicture):not([name^="check_icon_"]) {
    filter: invert(100%) !important;
}
#page img.icon, #page img.activityicon,
#page svg.icon, #page svg.activityicon {
    filter: opacity(0) !important;
}
.activity, .activity_overview .collapsibleregion {
    filter: invert(100%);
    background-size: 24px !important;
    background-position: 0 6px;
}
.activity_overview .collapsibleregion {
    background-position: 0 0;
}
.activity *, .activity_overview * {
    color: #444 !important;
}
.activity input {
    background: #bbb !important;
}
.activity input:hover {
    background: #aaa !important;
}
.activity.url, .activity_overview .collapsibleregion[id$="url"],
.activity.page, .activity_overview .collapsibleregion[id$="page"] {
    background-image: url(https://cdn.technologyreview.com/_/img/social-icon--link.svg) !important;
    background-size: 22px !important;
    background-position: 1px 8px;
}
.activity.resource, .activity_overview .collapsibleregion[id$="resource"] {
    background-image: url(https://png.icons8.com/small/2x/new-document.png) !important;
}
.activity.assign, .activity_overview .collapsibleregion[id$="assign"] {
    background-image: url(https://png.icons8.com/small/2x/ball-point-pen.png) !important;
}
.activity.quiz, .activity_overview .collapsibleregion[id$="quiz"] {
    background-image: url(https://png.icons8.com/small/2x/question-mark.png) !important;
}
.activity.forum, .activity_overview .collapsibleregion[id$="forum"] {
    background-image: url(https://png.icons8.com/small/2x/filled-chat.png) !important;
}
.activity.folder, .activity_overview .collapsibleregion[id$="folder"] {
    background-image: url(https://png.icons8.com/small/2x/documents-folder.png) !important;
}
#page .filemanager-toolbar img.icon {
    filter: none !important;
}

/* COURSE OVERVIEW RULES*/

.block_course_overview .header {
    display: none !important;
}
.coursebox {
    background: #333 !important;
    padding: 0 !important;
}
.coursebox .course_title {
    padding: 8px 8px 0 !important;
    background-color: /*[[color]]*/ !important;
}
.coursebox .activity_info  {
    padding: 8px !important;
}

/* CALENDAR RULES */

td.day.hasevent {
    background: #444 !important;
}
td.day.weekend {
    color: #777 !important;
}

/* FORUM SPECIFIC RULES */

table.forumheaderlist, table.forumheaderlist tr, table.forumheaderlist td, table.forumheaderlist thead {
    border-top: 2px solid #222 !important;
    border-bottom: 2px solid #222 !important;
}
.forumheaderlist .discussion, .forumpost {
    background: #333 !important;
}
.forumpost {
    padding: 8px;
}
#page button img.icon, #page button img.activityicon,
#page button svg.icon, #page button svg.activityicon {
    filter: none !important;
}

/* TEST SPECIFIC RULES */

.que .info {
    margin-left: -120px !important;
}
.quizreviewsummary {
    margin: 24px 0 !important;
}
.answer .correct, .qnbutton.correct, .question_element input[style="background-color: rgb(144, 255, 144);"] {
    background-color: #050 !important;
}
.answer .incorrect,  .qnbutton.incorrect, .question_element input[style="background-color: rgb(255, 144, 144);"] {
    background-color: #800 !important;
}
.outcome {
    background-color: #860 !important;
}
.qnbutton * {
    display: none !important;
}
.qnbutton {
    background: #444 !important;
    padding: 8px 0px !important;
    height: auto !important;
}
#WzBoDy {
    background: #444 !important;
}

/* HIDE SIDEBAR OPTION */

/*[[hide-right-sidebar]]*/

/* SCROLL BAR */

::-webkit-scrollbar {
    max-width: 10px !important;
    max-height: 10px !important;
}
::-webkit-scrollbar-track,
::-webkit-scrollbar-corner {
    background: #222 !important;
}
::-webkit-scrollbar-thumb {
    background: #555 !important;
}
::-webkit-scrollbar-thumb:hover {
    background: #666 !important;
}

/* FIX FOR VIMIUM PLUGIN */

.vimiumHintMarker  {
    background: #ca0 !important;
    filter: opacity(.7) !important;
    transform: translate(-6px, -4px) rotate(-8deg) !important;
}
.vimiumHintMarker span {
    color: #333 !important;
}

}
